// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

// SafeMath library for secure arithmetic operations
library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        uint256 c = a - b;
        return c;
    }
}

contract Token {
    using SafeMath for uint256; // Using SafeMath library for uint256 type

    // Mapping to track balances of token holders
    mapping(address => uint256) public balances;

    // Mapping to track allowances for spending tokens on behalf of others
    mapping(address => mapping(address => uint256)) public allowance;

    // Total supply of tokens and token metadata
    uint256 public totalSupply = 1000000000 * 10 ** 18;
    string public name = "EduToken";
    string public symbol = "EDU";
    uint8 public decimals = 18;

    // Events for token transfer and approval
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    // Contract deployment initializes total supply to contract deployer
    constructor() {
        balances[msg.sender] = totalSupply;
    }

    // Get the token balance of a particular address
    function balanceOf(address owner) public view returns (uint256) {
        return balances[owner];
    }

    // Transfer tokens from sender's address to another address
    function transfer(address to, uint256 value) public returns (bool) {
        require(balanceOf(msg.sender) >= value, 'balance too low');
        balances[to] = balances[to].add(value);
        balances[msg.sender] = balances[msg.sender].sub(value);
        emit Transfer(msg.sender, to, value);
        return true;
    }

    // Transfer tokens from one address to another, with allowance
    function transferFrom(address from, address to, uint256 value) public returns (bool) {
        require(balanceOf(from) >= value, 'balance too low');
        require(allowance[from][msg.sender] >= value, 'allowance too low');
        balances[to] = balances[to].add(value);
        balances[from] = balances[from].sub(value);
        emit Transfer(from, to, value);
        return true;
    }

    // Approve an address to spend tokens on behalf of the sender
    function approve(address spender, uint256 value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }
}
