    using SafeMath for uint256;

    bool public enableFee;
    uint256 public maxSellAmount;
    uint256 public maxBuyAmount;
    uint256 public sellFeeRate;
    uint256 public buyFeeRate;

    mapping(address => bool) public buyFeeList;
    mapping(address => bool) public sellFeeList;
    mapping(address => bool) public blackList;

    uint256 public LaunchAt = 0;

    mapping(address => bool) private isExempt; 

    modifier onlyPayloadSize(uint size) 
    {
        require(!(msg.data.length < size + 4));
        _;
    }

    modifier onlyAdmin()
    {
        require(msg.sender == _adminAddress, "only admin user opertions");
        _;
    }

    bool public addLiquidityEnabled = true;
    bool public inAddLiquidity;

    modifier AddLiquiditying() {
        inAddLiquidity = true;
        _;
        inAddLiquidity = false;
    }

    address public lpWalletAddress;
    address public marketingAddress;
    address public WETH;
    ISwapRouter public SwapRouter;
    uint256 public MiniCoinByLiquidity;

    event AddLiquidity(uint256 tokenAmount, uint256 ethAmount, uint256 timestamp);

    constructor() 
        ERC20("Funny Coin", "FUC") 
    {
        _mint(0xab5A2D6CA4b799A61729AB6224Aa7A023fCeCceA, 6000_0000 * (10 ** _decimals));
        _mint(0xD0ced804223D5Da5983Df3D8D1f456748c099F05, 2000_0000 * (10 ** _decimals));
        _mint(0xcB1bBCb92b16E1358c4821700F203A956dd6C629, 12000_0000 * (10 ** _decimals));


        lpWalletAddress = 0x32c09912a5E2f9B6c9A4150e3001Ac5073405a2F;
        marketingAddress = 0x574a5cd7a1A67292FB2F8eBEe5Da2Ca6a3691E65;

        WETH = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
        SwapRouter = ISwapRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);

        sellFeeRate = 600;
        buyFeeRate = 600;
        enableFee = true;
        maxSellAmount = 100_0000 * (10 ** _decimals);
        maxBuyAmount = 100_0000 * (10 ** _decimals);

        _adminAddress = 0x692c2e89AB2eAA3b560CDEfcfE9BE83CaE423318;


        isExempt[address(this)] = true;
        isExempt[_adminAddress] = true;
        isExempt[0xab5A2D6CA4b799A61729AB6224Aa7A023fCeCceA] = true;
        isExempt[0xD0ced804223D5Da5983Df3D8D1f456748c099F05] = true;
        isExempt[0xcB1bBCb92b16E1358c4821700F203A956dd6C629] = true;
        isExempt[lpWalletAddress] = true;
        isExempt[marketingAddress] = true;

        MiniCoinByLiquidity = 10 * (10 ** _decimals);
    }

    function pause() public onlyAdmin 
    {
        _pause();
    }

    function unpause() public onlyAdmin 
    {
        _unpause();
    }

    function setBuyFeeRate(uint256 rate) public onlyAdmin
    {
        require(rate >= 0 && rate <= 2500, "buyFeeRate transfer > 25 !");
        buyFeeRate = rate;
    }

    function setSellFeeRate(uint256 rate) public onlyAdmin
    {
        require(rate >= 0 && rate <= 2500, "sellFeeRate transfer > 25 !");
        sellFeeRate = rate;
    }

    function setEnableFee(bool enable) public onlyAdmin
    {
        require(enableFee != enable, "enableSFee value same");
        enableFee = enable;
    }

    function setSwapLimit(uint256 _maxSellAmount, uint256 _maxBuyAmount) public onlyAdmin
    {
        require(maxSellAmount != _maxSellAmount || maxBuyAmount != _maxBuyAmount, "maxAmount value same");
        maxSellAmount = _maxSellAmount;
        maxBuyAmount = _maxBuyAmount;
    }

    function setMarketingAddress(address _marketingAddress) public onlyAdmin
    {
        marketingAddress = _marketingAddress;
    }

     function setLpWalletAddress(address _lpWalletAddress) public onlyAdmin
    {
        lpWalletAddress = _lpWalletAddress;
    }

     function setAdmin(address adminAddress) public onlyAdmin
    {
        _adminAddress = adminAddress;
    }

    function setLaunchAt(uint256 launchAt) public onlyAdmin
    {
        LaunchAt = launchAt;
    }

    function setExempt(address addr, bool enable) public onlyAdmin
    {
        require(isExempt[addr] != enable,  "is already set to that value"  );
        isExempt[addr] = enable;
    }
    function setBuyFeeList(address addr, bool enable) public onlyAdmin 
    {
        require(buyFeeList[addr] != enable,  "is already set to that value"  );
        buyFeeList[addr] = enable;
    }

    function setSellFeeList(address addr, bool enable) public onlyAdmin 
    {
        require(sellFeeList[addr] != enable,  "is already set to that value"  );
        sellFeeList[addr] = enable;
    }

    function setBlackList(address addr, bool enable) public onlyAdmin 
    {
        require(blackList[addr] != enable,  "is already set to that value"  );
        blackList[addr] = enable;
    }

    function setAddLiquidityEnabled(bool _enabled) external onlyAdmin {
        addLiquidityEnabled = _enabled;
    }

    function setMiniCoinByLiquidity(uint256 mincoins) external onlyAdmin 
    {
        MiniCoinByLiquidity = mincoins;
    }

    function decimals() public view virtual override returns (uint8) 
    {
        return _decimals;
    }

    function transfer(address to, uint256 amount) public whenNotPaused onlyPayloadSize(2 * 32) virtual override returns (bool) {
        require(balanceOf(msg.sender) >= amount, "ERC20: transfer amount exceeds balance");
        return _cointransfer(_msgSender(), to, amount);
    }

    function transferFrom(address sender, address recipient, uint256 amount) public whenNotPaused onlyPayloadSize(3 * 32) virtual override returns (bool) {  
        require(balanceOf(sender) >= amount, "ERC20: transfer amount exceeds balance");
        _spendAllowance(sender, _msgSender(), amount);
        return _cointransfer(sender, recipient, amount);
    }

    function _cointransfer(address from, address to, uint256 amount) internal returns (bool)
    {
        require(blackList[from] == false && blackList[to] == false, "in blacklist!");     

        if(inAddLiquidity == true)
        {
            _transfer(from, to, amount);
            return true;
        }

        uint256 sendAmount = transferFee(from, to,  amount);
        _transfer(from, to, sendAmount);
        return true;
    }

    function transferFee(address from, address to, uint256 amount) private returns(uint256)
    {
        if(isExempt[from] == true || isExempt[to] == true)
        {
            return amount;
        }

        require(LaunchAt > 0 && block.timestamp >= LaunchAt, "Trading not open yet");

        uint256 mFeeRate = 0;
        // buy
        if(buyFeeList[from] == true)
        {
            if(maxBuyAmount > 0)
            {
                require(amount <= maxBuyAmount, "ERC20: > max tx amount");
            }
            mFeeRate = buyFeeRate;
        }
        // sell
        else if(sellFeeList[to] == true)
        {
            if(maxSellAmount > 0 )
            {
                require(amount <= maxSellAmount, "ERC20: > max tx amount");
            }
            mFeeRate = sellFeeRate;

            if(canAddLiquidity())
            {
                _doAddLp();
            }
        }

        if (enableFee == true && mFeeRate > 0) 
        {
            uint256 fee = (amount.mul(mFeeRate)).div(1e4);

            amount = amount - fee;

            if (fee > 0)  
            { 
                uint256 mLPfee = fee * 30 / 100;
                uint256 mMarketingFee = fee * 40 / 100;
                uint256 mLiquidityFee = fee - mLPfee - mMarketingFee;
                _transfer(from, lpWalletAddress, mLPfee); 
                _transfer(from, marketingAddress, mMarketingFee);  
                _transfer(from, address(this), mLiquidityFee);
            }
        }
        return amount;
    }

    function canAddLiquidity() internal view returns (bool) {
        return addLiquidityEnabled && inAddLiquidity == false && balanceOf(address(this)) >= MiniCoinByLiquidity;
    }

    function _doAddLp() internal AddLiquiditying {
        address[] memory pathEth = new address[](2);
        pathEth[0] = address(this);
        pathEth[1] = address(WETH);

        uint256 tokenAmount = balanceOf(address(this));
        uint256 half = tokenAmount / 2;
        if(half < 100000) return;

        uint256 ethAmountBefore = address(this).balance;
        _approve(address(this), address(SwapRouter), tokenAmount);
        bool success = false;

        try SwapRouter.swapExactTokensForETH(half,0, pathEth,address(this),block.timestamp)
        {
            success = true;
        }
        catch 
        {

        }
         if (success == false) 
        {
            return;
        }
        uint256 ethAmount = address(this).balance - ethAmountBefore;
        _addLiquidity(half, ethAmount);
    }

    function _addLiquidity(uint256 tokenAmount, uint256 ethAmount) internal 
    {
        try SwapRouter.addLiquidityETH{value: ethAmount}(address(this), tokenAmount, 0, 0, address(0),block.timestamp)
        {
            emit AddLiquidity(tokenAmount, ethAmount, block.timestamp);
        }
        catch {}
    }

    receive() external payable {}
}
