contract OrchestratedUpgradeable is Initializable, OwnableUpgradeable {
    event GrantedAccess(address access, bytes4 sig);
    event CancelAccess(address access, bytes4 sig);

    //admin address
    address public adminAddress;

    mapping(address => mapping(bytes4 => bool)) public orchestration;

    // function initialize() initializer public {
    //     __Orchestrated_init();
    // }

    function __Orchestrated_init() internal onlyInitializing {
        __Context_init_unchained();
        __Ownable_init_unchained();
        __Orchestrated_init_unchained();
    }

    function __Orchestrated_init_unchained() internal onlyInitializing {

    }

    modifier onlyAdmin() {
        require(msg.sender == adminAddress || msg.sender == owner());
        _;
    }

    function setAdmin(address _newAdmin) external onlyAdmin() {
        require(_newAdmin != address(0));
        adminAddress = _newAdmin;
    }

    /// @dev Restrict usage to authorized users;
    modifier onlyOrchestrated(string memory err) {
        require(orchestration[msg.sender][msg.sig], err);
        _;
    }

    /// @dev add orchestration
    function orchestrate(address user, bytes4 sig) public onlyOwner {
        orchestration[user][sig] = true;
        emit GrantedAccess(user, sig);
    }

    /// @dev remove orchestration
    function removeOrchestrate(address user, bytes4 sig) public onlyOwner {
        orchestration[user][sig] = false;
        emit CancelAccess(user, sig);
    }


}


contract Fragments1155 is Initializable, OrchestratedUpgradeable, ERC1155Upgradeable, PausableUpgradeable, ERC1155BurnableUpgradeable, ERC1155SupplyUpgradeable {

	mapping(uint => bool) public tokenIdCanTransfer;

	function initialize() initializer public {
		__ERC1155_init("");
		__Ownable_init();
		__Pausable_init();
		__ERC1155Burnable_init();
		__ERC1155Supply_init();
		__Orchestrated_init();

		tokenIdCanTransfer[1] = true;
        orchestration[_msgSender()][0x656dab63] = true;
        orchestration[_msgSender()][0xb0f6f1be] = true;
	}

	function setTokenIdCanTransfer(uint tokenId, bool canTransfer) public onlyAdmin {
		tokenIdCanTransfer[tokenId] = canTransfer;
	}

	function setURI(string memory newUri) public onlyOwner {
		_setURI(newUri);
	}

	function mint(address account, uint256 id, uint256 amount, bytes memory data)
	public
	onlyOwner
	{
		_mint(account, id, amount, data);
	}

	function mintBatch(address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
	public
	onlyOwner
	{
		_mintBatch(to, ids, amounts, data);
	}

	//0x656dab63
	function contractMint(address account, uint256 id, uint256 amount, bytes memory data)
	public
	onlyOrchestrated("onlyOrchestrated: contractMint")
	{
		_mint(account, id, amount, data);
	}

	//0xb0f6f1be
	function contractMintBatch(address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
	public
	onlyOrchestrated("onlyOrchestrated: contractMintBatch")
	{
		_mintBatch(to, ids, amounts, data);
	}

	function _beforeTokenTransfer(address operator, address from, address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
	internal
	whenNotPaused
	override(ERC1155Upgradeable, ERC1155SupplyUpgradeable)
	{
		super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
		uint idsLength = ids.length;
		for(uint i; i<idsLength; i++) {
			require(from==address(0) || to==address(0) || to==address(0x000000000000000000000000000000000000dEaD) || tokenIdCanTransfer[ids[i]], "token can not transfer");
		}
	}
}
