      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function balanceOf(address owner) external view returns(uint256);
}

interface IERC721 {
    function ownerOf(uint256 tokenId) external view returns (address owner);
    function getApproved(uint256 tokenId) external view returns (address operator);
    function transferFrom(address from, address to, uint256 tokenId) external;
}

contract Dividend{
    address public owner;
    address public ybfToken; // YBF Token address
    address public nft1Token;
    address public nft2Token;

    mapping(address => uint256) public userDividend;

    constructor(address _owner, address _ybfToken, address _nft1Token, address _nft2Token){
        ybfToken = _ybfToken;
        nft1Token = _nft1Token;
        nft2Token = _nft2Token;

        owner = _owner; 
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }

    function recharge(uint256 amount) public { 
        IERC20(ybfToken).transferFrom(msg.sender, address(this), amount);

        uint256 nft1Dividend = (amount * 80) / (100 * 30); 

        uint256 nft2Dividend = (amount * 20) / (100 * 150);

        for(uint256 i=0; i< 30; i++){
            try IERC721(nft1Token).ownerOf(i) {
                userDividend[IERC721(nft1Token).ownerOf(i)] += nft1Dividend;
            }catch{}
            
        }

        for(uint256 i=0; i< 150; i++){
            try IERC721(nft2Token).ownerOf(i){
                userDividend[IERC721(nft2Token).ownerOf(i)] += nft2Dividend;
            }catch {}
        }

    }


    function withdraw() public {
        require(userDividend[msg.sender] >0, "You don't have award");

        IERC20(ybfToken).transfer(msg.sender, userDividend[msg.sender]);

        userDividend[msg.sender] = 0;
    }

    function emergencyWithdraw() external onlyOwner { 
        // In case of emergency, the owner can withdraw the remaining ybf tokens
        IERC20(ybfToken).transfer(owner, IERC20(ybfToken).balanceOf(address(this)));
    }

}"
