      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.7.6;

import "@openzeppelin/contracts/access/Ownable.sol";

contract SubmitAddresses is Ownable {
    string[] chains = [
        "Solana",
        "Cardano",
        "Cosmos",
        "Tezos",
        "Terra",
        "Elrond"
    ];

    mapping(address => mapping(uint256 => string)) public mySubmittedAddress;

    function addChain(string memory _chain) external onlyOwner {
        chains.push(_chain);
    }

    function getNumberOfChain() external view returns (uint256) {
        return chains.length;
    }

    function getChainName(uint256 _index)
        external
        view
        returns (string memory)
    {
        return chains[_index];
    }

    function submitMyAddress(uint256 _index, string memory _address) external {
        mySubmittedAddress[msg.sender][_index] = _address;
    }
}
