contract DividendPayingToken is
    ERC20,
    DividendPayingTokenInterface,
    DividendPayingTokenOptionalInterface,
    Ownable
{
    using SafeMath for uint256;
    using SafeMathUint for uint256;
    using SafeMathInt for int256;

    // With `magnitude`, we can properly distribute dividends even if the amount of received ether is small.
    // For more discussion about choosing the value of `magnitude`,
    //  see https://github.com/ethereum/EIPs/issues/1726#issuecomment-472352728
    uint256 internal constant magnitude = 2**128;

    uint256 internal magnifiedDividendPerShare;

    IUniswapV2Router02 public uniswapV2Router =
        IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

    address public immutable token =
        address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c); // WBNB

    // About dividendCorrection:
    // If the token balance of a `_user` is never changed, the dividend of `_user` can be computed with:
    //   `dividendOf(_user) = dividendPerShare * balanceOf(_user)`.
    // When `balanceOf(_user)` is changed (via minting/burning/transferring tokens),
    //   `dividendOf(_user)` should not be changed,
    //   but the computed value of `dividendPerShare * balanceOf(_user)` is changed.
    // To keep the `dividendOf(_user)` unchanged, we add a correction term:
    //   `dividendOf(_user) = dividendPerShare * balanceOf(_user) + dividendCorrectionOf(_user)`,
    //   where `dividendCorrectionOf(_user)` is updated whenever `balanceOf(_user)` is changed:
    //   `dividendCorrectionOf(_user) = dividendPerShare * (old balanceOf(_user)) - (new balanceOf(_user))`.
    // So now `dividendOf(_user)` returns the same value before and after `balanceOf(_user)` is changed.
    mapping(address => int256) internal magnifiedDividendCorrections;
    mapping(address => uint256) internal withdrawnDividends;

    uint256 public totalDividendsDistributed;

    constructor(string memory _name, string memory _symbol)
        ERC20(_name, _symbol)
    {}

    /// @dev Distributes dividends whenever ether is paid to this contract.
    receive() external payable {
        distributeDividends();
    }

    /// @notice Distributes ether to token holders as dividends.
    /// @dev It reverts if the total supply of tokens is 0.
    /// It emits the `DividendsDistributed` event if the amount of received ether is greater than 0.
    /// About undistributed ether:
    ///   In each distribution, there is a small amount of ether not distributed,
    ///     the magnified amount of which is
    ///     `(msg.value * magnitude) % totalSupply()`.
    ///   With a well-chosen `magnitude`, the amount of undistributed ether
    ///     (de-magnified) in a distribution can be less than 1 wei.
    ///   We can actually keep track of the undistributed ether in a distribution
    ///     and try to distribute it in the next distribution,
    ///     but keeping track of such data on-chain costs much more than
    ///     the saved ether, so we don't do that.

    function distributeDividends() public payable override {
        require(false, "Cannot send BNB to tracker as it is unrecoverable"); //
    }

    function distributeTokenDividends(uint256 amount) public onlyOwner {
        require(totalSupply() > 0, "");

        if (amount > 0) {
            magnifiedDividendPerShare = magnifiedDividendPerShare.add(
                (amount).mul(magnitude) / totalSupply()
            );
            emit DividendsDistributed(msg.sender, amount);

            totalDividendsDistributed = totalDividendsDistributed.add(amount);
        }
    }

    /// @notice Withdraws the ether distributed to the sender.
    /// @dev It emits a `DividendWithdrawn` event if the amount of withdrawn ether is greater than 0.
    function withdrawDividend() public virtual override {
        _withdrawDividendOfUser(payable(msg.sender));
    }

    /// @notice Withdraws the ether distributed to the sender.
    /// @dev It emits a `DividendWithdrawn` event if the amount of withdrawn ether is greater than 0.
    function _withdrawDividendOfUser(address payable user)
        internal
        returns (uint256)
    {
        uint256 _withdrawableDividend = withdrawableDividendOf(user);
        if (_withdrawableDividend > 0) {
            withdrawnDividends[user] = withdrawnDividends[user].add(
                _withdrawableDividend
            );
            emit DividendWithdrawn(user, _withdrawableDividend);
            bool success = IERC20(token).transfer(user, _withdrawableDividend);

            if (!success) {
                withdrawnDividends[user] = withdrawnDividends[user].sub(
                    _withdrawableDividend
                );
                return 0;
            }

            return _withdrawableDividend;
        }

        return 0;
    }

    /// @notice View the amount of dividend in wei that an address can withdraw.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` can withdraw.
    function dividendOf(address _owner) public view override returns (uint256) {
        return withdrawableDividendOf(_owner);
    }

    /// @notice View the amount of dividend in wei that an address can withdraw.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` can withdraw.
    function withdrawableDividendOf(address _owner)
        public
        view
        override
        returns (uint256)
    {
        return accumulativeDividendOf(_owner).sub(withdrawnDividends[_owner]);
    }

    /// @notice View the amount of dividend in wei that an address has withdrawn.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` has withdrawn.
    function withdrawnDividendOf(address _owner)
        public
        view
        override
        returns (uint256)
    {
        return withdrawnDividends[_owner];
    }

    /// @notice View the amount of dividend in wei that an address has earned in total.
    /// @dev accumulativeDividendOf(_owner) = withdrawableDividendOf(_owner) + withdrawnDividendOf(_owner)
    /// = (magnifiedDividendPerShare * balanceOf(_owner) + magnifiedDividendCorrections[_owner]) / magnitude
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` has earned in total.
    function accumulativeDividendOf(address _owner)
        public
        view
        override
        returns (uint256)
    {
        return
            magnifiedDividendPerShare
                .mul(balanceOf(_owner))
                .toInt256Safe()
                .add(magnifiedDividendCorrections[_owner])
                .toUint256Safe() / magnitude;
    }

    /// @dev Internal function that transfer tokens from one address to another.
    /// Update magnifiedDividendCorrections to keep dividends unchanged.
    /// @param from The address to transfer from.
    /// @param to The address to transfer to.
    /// @param value The amount to be transferred.
    function _transfer(
        address from,
        address to,
        uint256 value
    ) internal virtual override {
        require(false);

        int256 _magCorrection = magnifiedDividendPerShare
            .mul(value)
            .toInt256Safe();
        magnifiedDividendCorrections[from] = magnifiedDividendCorrections[from]
            .add(_magCorrection);
        magnifiedDividendCorrections[to] = magnifiedDividendCorrections[to].sub(
            _magCorrection
        );
    }

    /// @dev Internal function that mints tokens to an account.
    /// Update magnifiedDividendCorrections to keep dividends unchanged.
    /// @param account The account that will receive the created tokens.
    /// @param value The amount that will be created.
    function _mint(address account, uint256 value) internal override {
        super._mint(account, value);

        magnifiedDividendCorrections[account] = magnifiedDividendCorrections[
            account
        ].sub((magnifiedDividendPerShare.mul(value)).toInt256Safe());
    }

    /// @dev Internal function that burns an amount of the token of a given account.
    /// Update magnifiedDividendCorrections to keep dividends unchanged.
    /// @param account The account whose tokens will be burnt.
    /// @param value The amount that will be burnt.
    function _burn(address account, uint256 value) internal override {
        super._burn(account, value);

        magnifiedDividendCorrections[account] = magnifiedDividendCorrections[
            account
        ].add((magnifiedDividendPerShare.mul(value)).toInt256Safe());
    }

    function _setBalance(address account, uint256 newBalance) internal {
        uint256 currentBalance = balanceOf(account);

        if (newBalance > currentBalance) {
            uint256 mintAmount = newBalance.sub(currentBalance);
            _mint(account, mintAmount);
        } else if (newBalance < currentBalance) {
            uint256 burnAmount = currentBalance.sub(newBalance);
            _burn(account, burnAmount);
        }
    }
}

contract Spin is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    address public immutable uniswapV2Pair;

    bool private swapping;

    DividendTracker public dividendTracker;

    GameTracker public gameTracker;

    Game public megaSpin;
    Game public superSpin;

    address public marketingWallet;
    address public devWallet;
    address public xSpinWallet;

    address public immutable token =
        address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c); // WBNB

    uint256 public maxSell;

    uint256 public swapTokensAtBNBAmount;
    uint256 public sendBetsAtBNBAmount;

    uint256 public maxWallet;
    uint256 public liquidityActiveBlock = 0; // 0 means liquidity is not active yet
    uint256 public tradingActiveBlock = 0; // 0 means trading is not active
    uint256 public earlyBuyPenaltyEnd; // determines when snipers/bots can sell without extra penalty

    bool public limitsInEffect = true;
    bool public tradingActive = false;

    address public presaleAddress;
    address public presaleRouterAddress;

    uint256 public feesDenominator = 1000;

    uint256 public totalFees;
    uint256 public rewardsFee;
    uint256 public liquidityFee;

    uint256 public operationsFee;
    uint256 public megaSpinFee;
    uint256 public superSpinFee;
    uint256 public marketingFee;
    uint256 public devFee;
    uint256 public xSpinFee;

    // use by default 300,000 gas to process auto-claiming dividends
    uint256 public gasForProcessing = 300000;

    mapping(address => bool) public boughtEarly; // mapping to track addresses that buy within the first 2 blocks pay a 3x tax for 69420 hours to sell

    // exclude from fees and max transaction amount
    mapping(address => bool) private _isExcludedFromFees;

    mapping(address => bool) public _isExcludedMaxSellTransactionAmount;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    event UpdateDividendTracker(
        address indexed newAddress,
        address indexed oldAddress
    );

    event UpdateUniswapV2Router(
        address indexed newAddress,
        address indexed oldAddress
    );

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event ExcludedMaxSellTransactionAmount(
        address indexed account,
        bool isExcluded
    );

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event MarketingWalletUpdated(
        address indexed newWallet,
        address indexed oldWallet
    );

    event GasForProcessingUpdated(
        uint256 indexed newValue,
        uint256 indexed oldValue
    );

    event BuybackMultiplierActive(uint256 duration);

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    event SendDividends(uint256 tokensSwapped, uint256 amount);

    event ProcessedDividendTracker(
        uint256 iterations,
        uint256 claims,
        uint256 lastProcessedIndex,
        bool indexed automatic,
        uint256 gas,
        address indexed processor
    );

    constructor() ERC20("Spin", "SPIN") {
        uint256 _rewardsFee = 30;
        uint256 _liquidityFee = 0;

        megaSpinFee = 20;
        superSpinFee = 20;
        marketingFee = 50;
        devFee = 5;
        xSpinFee = 0;

        uint256 totalSupply = 10 * 1e9 * 1e18; // 10 Billion total supply


        // Swap tokens(for reward, liquidity, other fees) when contract balance is 'swapTokensAtBNBAmount' worth
        swapTokensAtBNBAmount = 2 ether; // 1 BNB

        // Set bets when contract balance is 'sendBetsAtBNBAmount' worth
        sendBetsAtBNBAmount = 0.3 ether;

        maxWallet = (totalSupply * 50) / 1000; // 1% maxWallet
        maxSell = (totalSupply * 10) / 1000; // 1% maxSellTxn

        rewardsFee = _rewardsFee;
        liquidityFee = _liquidityFee;
        operationsFee =
            megaSpinFee +
            superSpinFee +
            marketingFee +
            devFee +
            xSpinFee;
        totalFees = operationsFee + rewardsFee + liquidityFee;

        dividendTracker = new DividendTracker();
        gameTracker = new GameTracker();

        marketingWallet = address(0x26DDC2CBF790158fe8ED4EA685215E16899cA980);
        devWallet = address(0x7245AA3377401C1a0482219053edC8545D65920f);
        xSpinWallet = marketingWallet;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);

        // exclude from receiving dividends
        dividendTracker.excludeFromDividends(address(dividendTracker));
        dividendTracker.excludeFromDividends(address(this));
        dividendTracker.excludeFromDividends(owner());
        dividendTracker.excludeFromDividends(address(_uniswapV2Router));
        dividendTracker.excludeFromDividends(address(0xdead));
        dividendTracker.excludeFromDividends(address(gameTracker));

        // exclude from paying fees or having max transaction amount
        excludeFromFees(owner(), true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(gameTracker), true);
        excludeFromFees(address(0xdead), true);
        excludeFromMaxTransaction(owner(), true);
        excludeFromMaxTransaction(address(this), true);
        excludeFromMaxTransaction(address(gameTracker), true);
        excludeFromMaxTransaction(address(dividendTracker), true);
        excludeFromMaxTransaction(address(_uniswapV2Router), true);
        excludeFromMaxTransaction(address(0xdead), true);

        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(), totalSupply);
    }

    receive() external payable {}

    // only use if conducting a presale.
    function addPresaleAddressForExclusions(
        address _presaleAddress,
        address _presaleRouterAddress
    ) external onlyOwner {
        presaleAddress = _presaleAddress;
        excludeFromFees(_presaleAddress, true);
        dividendTracker.excludeFromDividends(_presaleAddress);
        excludeFromMaxTransaction(_presaleAddress, true);
        presaleRouterAddress = _presaleRouterAddress;
        excludeFromFees(_presaleRouterAddress, true);
        dividendTracker.excludeFromDividends(_presaleRouterAddress);
        excludeFromMaxTransaction(_presaleRouterAddress, true);
    }

    function emergencyPresaleAddressUpdate(address _presaleAddress)
        external
        onlyOwner
    {
        presaleAddress = _presaleAddress;
    }

    // never ever do this without professional assistance.
    function updateDividendTracker(address newAddress) external onlyOwner {
        require(
            newAddress != address(dividendTracker),
            "The dividend tracker already has that address"
        );

        DividendTracker newDividendTracker = DividendTracker(
            payable(newAddress)
        );

        require(
            newDividendTracker.owner() == address(this),
            "The new dividend tracker must be owned by the Spin token contract"
        );

        newDividendTracker.excludeFromDividends(address(newDividendTracker));
        newDividendTracker.excludeFromDividends(address(this));
        newDividendTracker.excludeFromDividends(owner());
        newDividendTracker.excludeFromDividends(address(gameTracker));
        newDividendTracker.excludeFromDividends(address(uniswapV2Router));

        emit UpdateDividendTracker(newAddress, address(dividendTracker));

        dividendTracker = newDividendTracker;
    }

    function updateGameTracker(address newAddress) external onlyOwner {
        require(
            newAddress != address(gameTracker),
            "The game tracker already has that address"
        );

        excludeFromMaxTransaction(newAddress, true);
        dividendTracker.excludeFromDividends(newAddress);
        excludeFromFees(newAddress, true);

        gameTracker = GameTracker(payable(newAddress));
    }

    // excludes wallets and contracts from dividends (such as CEX hotwallets, etc.)
    function excludeFromDividends(address account) external onlyOwner {
        dividendTracker.excludeFromDividends(account);
    }

    // removes exclusion on wallets and contracts from dividends (such as CEX hotwallets, etc.)
    function includeInDividends(address account) external onlyOwner {
        dividendTracker.includeInDividends(account);
    }

    function setTrading(bool _status) external onlyOwner {
        require(_status != tradingActive, "Trading status is already set");
        tradingActive = _status;
        tradingActiveBlock = block.number;
        earlyBuyPenaltyEnd = block.timestamp + 69420 hours;
    }

    function updateMaxAmount(uint256 newNum) external onlyOwner {
        require(maxSell != newNum);
        maxSell = newNum * (10**18);
    }

    function updateRedistributionFees(
        uint256 _rewardsFee,
        uint256 _liquidityFee
    ) external onlyOwner {
        rewardsFee = _rewardsFee;
        liquidityFee = _liquidityFee;

        totalFees = operationsFee + rewardsFee + liquidityFee;
    }

    function updateOperationsFees(
        uint256 _megaSpinFee,
        uint256 _superSpinFee,
        uint256 _marketingFee,
        uint256 _devFee,
        uint256 _xSpinFee
    ) external onlyOwner {
        megaSpinFee = _megaSpinFee;
        superSpinFee = _superSpinFee;
        marketingFee = _marketingFee;
        devFee = _devFee;
        xSpinFee = _xSpinFee;
        operationsFee =
            _megaSpinFee +
            _superSpinFee +
            _marketingFee +
            _devFee +
            _xSpinFee;

        totalFees = operationsFee + rewardsFee + liquidityFee;
    }

    function updateUniswapV2Router(address newAddress) external onlyOwner {
        require(
            newAddress != address(uniswapV2Router),
            "The router already has that address"
        );
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
    }

    // Update the BNB limit to swap the tokens for rewards
    function updateSwapTokensBNBLimit(uint256 _limit) external onlyOwner {
        require(_limit > 0, "Invalid swap tokens limit");
        swapTokensAtBNBAmount = _limit;
    }

    // Update the BNB limit to swap the tokens to send the BNB to both the games
    function updateSetBetsBNBLimit(uint256 _limit) external onlyOwner {
        require(_limit > 0, "Invalid set bets limit");
        sendBetsAtBNBAmount = _limit;
    }

    function setGames(address _megaSpin, address _superSpin)
        external
        onlyOwner
    {
        require(!tradingActive, "Please turn OFF the trading to set games");
        megaSpin = Game(_megaSpin);
        superSpin = Game(_superSpin);
    }

    // Set the credit price of SuperSpin game
    // Same can also be updated from SuperSpin's contract
    function setSuperSpinCreditPrice(uint256 _amountInBNB) external onlyOwner {
        superSpin.setCreditPrice(_amountInBNB);
    }

    // Set the credit price of MegaSpin game
    // Same can also be updated from MegaSpin's contract
    function setMegaSpinCreditPrice(uint256 _amountInBNB) external onlyOwner {
        megaSpin.setCreditPrice(_amountInBNB);
    }

    function excludeFromMaxTransaction(address updAds, bool isEx)
        public
        onlyOwner
    {
        _isExcludedMaxSellTransactionAmount[updAds] = isEx;
        emit ExcludedMaxSellTransactionAmount(updAds, isEx);
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(
            _isExcludedFromFees[account] != excluded,
            "Account is already the value of 'excluded'"
        );
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(
        address[] calldata accounts,
        bool excluded
    ) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setAutomatedMarketMakerPair(address pair, bool value)
        public
        onlyOwner
    {
        require(
            pair != uniswapV2Pair,
            "The PCS pair cannot be removed from automatedMarketMakerPairs"
        );

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(
            automatedMarketMakerPairs[pair] != value,
            "Automated market maker pair is already set to that value"
        );
        automatedMarketMakerPairs[pair] = value;

        if (value) {
            dividendTracker.excludeFromDividends(pair);
        }

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function updateMarketingWallet(address newOperationsWallet)
        external
        onlyOwner
    {
        excludeFromFees(newOperationsWallet, true);
        emit MarketingWalletUpdated(newOperationsWallet, marketingWallet);
        marketingWallet = newOperationsWallet;
    }

    function updateGasForProcessing(uint256 newValue) external onlyOwner {
        require(
            newValue >= 200000 && newValue <= 500000,
            " gasForProcessing must be between 200,000 and 500,000"
        );
        require(
            newValue != gasForProcessing,
            "Cannot update gasForProcessing to same value"
        );
        emit GasForProcessingUpdated(newValue, gasForProcessing);
        gasForProcessing = newValue;
    }

    function updateClaimWait(uint256 claimWait) external onlyOwner {
        dividendTracker.updateClaimWait(claimWait);
    }

    function getClaimWait() external view returns (uint256) {
        return dividendTracker.claimWait();
    }

    function getTotalDividendsDistributed() external view returns (uint256) {
        return dividendTracker.totalDividendsDistributed();
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function withdrawableDividendOf(address account)
        public
        view
        returns (uint256)
    {
        return dividendTracker.withdrawableDividendOf(account);
    }

    function dividendTokenBalanceOf(address account)
        public
        view
        returns (uint256)
    {
        return dividendTracker.balanceOf(account);
    }

    function getAccountDividendsInfo(address account)
        external
        view
        returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return dividendTracker.getAccount(account);
    }

    function getAccountDividendsInfoAtIndex(uint256 index)
        external
        view
        returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return dividendTracker.getAccountAtIndex(index);
    }

    function processDividendTracker(uint256 gas) external {
        (
            uint256 iterations,
            uint256 claims,
            uint256 lastProcessedIndex
        ) = dividendTracker.process(gas);
        emit ProcessedDividendTracker(
            iterations,
            claims,
            lastProcessedIndex,
            false,
            gas,
            tx.origin
        );
    }

    function claim() external {
        dividendTracker.processAccount(payable(msg.sender), false);
    }

    function getLastProcessedIndex() external view returns (uint256) {
        return dividendTracker.getLastProcessedIndex();
    }

    function getNumberOfDividendTokenHolders() external view returns (uint256) {
        return dividendTracker.getNumberOfTokenHolders();
    }

    function getNumberOfDividends() external view returns (uint256) {
        return dividendTracker.totalSupply();
    }

    function megaSpinCreditPrice() external view returns (uint256) {
        return megaSpin.creditPrice();
    }

    function superSpinCreditPrice() external view returns (uint256) {
        return superSpin.creditPrice();
    }

    // Will send BNB to both the games when the contract reach this much tokens
    function sendBetsAtTokensAmount() external view returns (uint256) {
        return getAmountTokensOut(sendBetsAtBNBAmount);
    }

    // Will swap BNB for rewards when the contract reach this much tokens
    function swapAtTokensAmount() external view returns (uint256) {
        return getAmountTokensOut(swapTokensAtBNBAmount);
    }

    // Check if both the games are active
    // If true, then token can be bought
    function areGamesActive() external view returns (bool) {
        return (superSpin.isGameActive() && megaSpin.isGameActive());
    }

    // Remove limits after token is stable
    function removeLimits() external onlyOwner returns (bool) {
        limitsInEffect = false;
        return true;
    }

    function rescueToken(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(msg.sender, _amount);
    }

    function rescueBnb(uint256 _amount) external onlyOwner {
        payable(msg.sender).transfer(_amount);
    }

    // ### PRIVATES ###

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        // Bot / Sniper wrecker.  WARN USERS TO NOT SNIPE OR BOT!!!
        if (
            !tradingActive &&
            block.number < liquidityActiveBlock + 1 &&
            from != owner() &&
            from != presaleAddress &&
            from != presaleRouterAddress
        ) {
            super._transfer(from, to, 1); // give bots 1 wei of tokens if they snipe in the same block or the next block that liquidity is added.

            // Send it to MegaSpin
            super._transfer(
                from,
                address(megaSpin),
                amount - 1
            ); // Burn the remaining tokens
            return;
        }

        if (!tradingActive) {
            require(
                from == owner() ||
                    from == presaleAddress ||
                    from == presaleRouterAddress,
                "Trading is not active."
            );
            if (liquidityActiveBlock == 0 && to == uniswapV2Pair) {
                liquidityActiveBlock = block.number;
            }
        }

        if (
            from != owner() &&
            from != presaleAddress &&
            from != presaleRouterAddress &&
            block.number <= tradingActiveBlock + 1 &&
            !_isExcludedMaxSellTransactionAmount[to]
        ) {
            boughtEarly[to] = true;
        }

        if (limitsInEffect) {
            if (
                from != owner() &&
                to != owner() &&
                to != address(0) &&
                to != address(0xdead) &&
                from != presaleAddress &&
                to != presaleAddress &&
                from != presaleRouterAddress &&
                to != presaleRouterAddress &&
                !swapping
            ) {
                //when buy
                if (
                    automatedMarketMakerPairs[from] &&
                    !_isExcludedMaxSellTransactionAmount[to]
                ) {
                    require(
                        amount + balanceOf(to) <= maxWallet,
                        "Can't exceed maxWallet"
                    );
                }
                //when sell
                else if (
                    automatedMarketMakerPairs[to] &&
                    !_isExcludedMaxSellTransactionAmount[from]
                ) {
                    require(
                        amount <= maxSell,
                        "Sell transfer amount exceeds the MaxSell."
                    );
                }
                //normal transfer
                else if (!_isExcludedMaxSellTransactionAmount[to]) {
                    require(
                        amount + balanceOf(to) <= maxWallet,
                        "Can't exceed maxWallet"
                    );
                }
            }
        }

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        // "to" is buying tokens
        if (
            from == uniswapV2Pair &&
            to != owner() &&
            to != address(0) &&
            to != address(0xdead)
        ) {
            // Both the games should be active to let the person buy tokens
            require(
                megaSpin.isGameActive() && superSpin.isGameActive(),
                "One of the games is not active"
            );

            // Send "amount" worth of BNB credits
            sendBetToGames(to, amount);
        }

        uint256 gamesTrackerBalance = balanceOf(address(gameTracker));
        bool canSendToGames = gamesTrackerBalance >=
            getAmountTokensOut(sendBetsAtBNBAmount);

        uint256 contractTokenBalance = balanceOf(address(this));
        uint256 toSellForSwap = getAmountTokensOut(swapTokensAtBNBAmount);
        bool canSwap = contractTokenBalance >= toSellForSwap;

        if (
            (canSwap || canSendToGames) &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to]
        ) {
            swapping = true;

            if (canSwap) {
                // swap, liquify and send dividends
                if (liquidityFee > 0) {
                    uint256 swapTokens = contractTokenBalance
                        .mul(liquidityFee)
                        .div(totalFees.sub(operationsFee));
                    swapAndLiquify(swapTokens);
                }

                uint256 sellTokens = balanceOf(address(this));
                swapAndSendDividends(sellTokens);
            }

            if (canSendToGames) {
                // canSendToGames
                // Send BNBs to both the games
                GameTracker(gameTracker).swapForEth();
                distributeAmongGames(address(this).balance);
            }

            swapping = false;
        }

        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 opsFees = amount.mul(operationsFee).div(feesDenominator);
            uint256 rewFees = amount.mul(totalFees.sub(operationsFee)).div(
                feesDenominator
            );
            uint256 fees = rewFees + opsFees;

            // snipers / bots pay 4x fees to exit for 69420 hours
            if (boughtEarly[from] && earlyBuyPenaltyEnd > block.timestamp) {
                super._transfer(from, address(megaSpin), fees * 3); // burn 3/4 of the tokens to prevent price dump.
                super._transfer(from, address(this), rewFees);
                super._transfer(from, address(gameTracker), opsFees);
                fees = fees * 4;
            } else {
                super._transfer(from, address(this), rewFees);
                super._transfer(from, address(gameTracker), opsFees);
            }
            amount = amount.sub(fees);
        }

        super._transfer(from, to, amount);

        try
            dividendTracker.setBalance(payable(from), balanceOf(from))
        {} catch {}
        try dividendTracker.setBalance(payable(to), balanceOf(to)) {} catch {}

        if (!swapping) {
            uint256 gas = gasForProcessing;

            try dividendTracker.process(gas) returns (
                uint256 iterations,
                uint256 claims,
                uint256 lastProcessedIndex
            ) {
                emit ProcessedDividendTracker(
                    iterations,
                    claims,
                    lastProcessedIndex,
                    true,
                    gas,
                    tx.origin
                );
            } catch {}
        }

        // Remove bets of token seller
        if (
            from != owner() &&
            from != uniswapV2Pair &&
            from != address(uniswapV2Router)
        ) {
            // Remove bets only if game is active
            if (megaSpin.isGameActive()) {
                megaSpin.removeBet(from);
            }
            if (superSpin.isGameActive()) {
                superSpin.removeBet(from);
            }
        }
    }

    // Send 'amount' worth of bets to the games
    function sendBetToGames(address _bettor, uint256 _amountTokens) private {
        // Amount of BNB to put in, to get _amountTokens
        // Previously getAmountsOut was used, but that comes way less than credit price
        // after applying fees and adjusting according to liquidity
        uint256 amountsIn = getAmountBNBIn(_amountTokens);

        if (amountsIn >= megaSpin.creditPrice()) {
            megaSpin.placeBet(_bettor, amountsIn);
        }
        if (amountsIn >= superSpin.creditPrice()) {
            superSpin.placeBet(_bettor, amountsIn);
        }
    }

    function getAmountBNBOut(uint256 _amountTokens)
        private
        view
        returns (uint256)
    {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        try uniswapV2Router.getAmountsOut(_amountTokens, path) returns (
            uint256[] memory amountsOut
        ) {
            return amountsOut[1];
        } catch {
            return 0;
        }
    }

    function getAmountBNBIn(uint256 _amountTokens)
        private
        view
        returns (uint256)
    {
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(this);

        try uniswapV2Router.getAmountsIn(_amountTokens, path) returns (
            uint256[] memory amountsIn
        ) {
            return amountsIn[0];
        } catch {
            return 0;
        }
    }

    function getAmountTokensOut(uint256 _amountBNB)
        private
        view
        returns (uint256)
    {
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(this);

        try uniswapV2Router.getAmountsOut(_amountBNB, path) returns (
            uint256[] memory amountsOut
        ) {
            return amountsOut[1];
        } catch {
            return 0;
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function swapAndLiquify(uint256 tokens) private {
        // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0x42979Ab546B5fF75E5DCEbb17363878D2F72b0F2),
            block.timestamp
        );
    }

    function distributeAmongGames(uint256 amount) private {
        uint256 megaSpinAmt = amount.mul(megaSpinFee).div(operationsFee);
        uint256 superSpinAmt = amount.mul(superSpinFee).div(operationsFee);
        uint256 xSpinFeeAmt = amount.mul(xSpinFee).div(operationsFee);

        uint256 marketingAmt = amount.mul(marketingFee).div(operationsFee);
        uint256 devAmt = amount.mul(devFee).div(operationsFee);

        if (megaSpinAmt > 0) {
            (bool success, ) = address(megaSpin).call{value: megaSpinAmt}("");
        }

        if (superSpinAmt > 0) {
            (bool success, ) = address(superSpin).call{value: superSpinAmt}("");
        }

        if (xSpinFeeAmt > 0) {
            (bool success, ) = address(xSpinWallet).call{value: xSpinFeeAmt}(
                ""
            );
        }

        if (marketingAmt > 0) {
            (bool success, ) = address(marketingWallet).call{
                value: marketingAmt
            }("");
        }

        if (devAmt > 0) {
            (bool success, ) = address(devWallet).call{value: devAmt}("");
        }
    }

    function swapAndSendDividends(uint256 tokens) private {
        swapTokensForEth(tokens);

        uint256 dividends = address(this).balance;

        IWETH9(token).deposit{value: dividends}();
        uint256 tokenBalance = IERC20(token).balanceOf(address(this));

        bool success = IERC20(token).transfer(
            address(dividendTracker),
            tokenBalance
        );

        if (success) {
            dividendTracker.distributeTokenDividends(dividends);
            emit SendDividends(tokens, dividends);
        }
    }
}

contract DividendTracker is DividendPayingToken {
    using SafeMath for uint256;
    using SafeMathInt for int256;
    using IterableMapping for IterableMapping.Map;

    IterableMapping.Map private tokenHoldersMap;
    uint256 public lastProcessedIndex;

    mapping(address => bool) public excludedFromDividends;

    mapping(address => uint256) public lastClaimTimes;

    uint256 public claimWait;
    uint256 public immutable minimumTokenBalanceForDividends;

    event ExcludeFromDividends(address indexed account);
    event IncludeInDividends(address indexed account);
    event ClaimWaitUpdated(uint256 indexed newValue, uint256 indexed oldValue);

    event Claim(
        address indexed account,
        uint256 amount,
        bool indexed automatic
    );

    constructor() DividendPayingToken("Dividend_Tracker", "Dividend_Tracker") {
        claimWait = 1200;
        minimumTokenBalanceForDividends = 20000 * (10**18); //must hold 20,000+ tokens
    }

    function _transfer(
        address,
        address,
        uint256
    ) internal pure override {
        require(false, "Dividend_Tracker: No transfers allowed");
    }

    function withdrawDividend() public pure override {
        require(
            false,
            "Dividend_Tracker: withdrawDividend disabled. Use the 'claim' function on the main contract."
        );
    }

    function excludeFromDividends(address account) external onlyOwner {
        require(!excludedFromDividends[account]);
        excludedFromDividends[account] = true;

        _setBalance(account, 0);
        tokenHoldersMap.remove(account);

        emit ExcludeFromDividends(account);
    }

    function includeInDividends(address account) external onlyOwner {
        require(excludedFromDividends[account]);
        excludedFromDividends[account] = false;

        emit IncludeInDividends(account);
    }

    function updateClaimWait(uint256 newClaimWait) external onlyOwner {
        require(
            newClaimWait >= 1200 && newClaimWait <= 86400,
            "Dividend_Tracker: claimWait must be updated to between 1 and 24 hours"
        );
        require(
            newClaimWait != claimWait,
            "Dividend_Tracker: Cannot update claimWait to same value"
        );
        emit ClaimWaitUpdated(newClaimWait, claimWait);
        claimWait = newClaimWait;
    }

    function getLastProcessedIndex() external view returns (uint256) {
        return lastProcessedIndex;
    }

    function getNumberOfTokenHolders() external view returns (uint256) {
        return tokenHoldersMap.keys.length;
    }

    function getAccount(address _account)
        public
        view
        returns (
            address account,
            int256 index,
            int256 iterationsUntilProcessed,
            uint256 withdrawableDividends,
            uint256 totalDividends,
            uint256 lastClaimTime,
            uint256 nextClaimTime,
            uint256 secondsUntilAutoClaimAvailable
        )
    {
        account = _account;

        index = tokenHoldersMap.getIndexOfKey(account);

        iterationsUntilProcessed = -1;

        if (index >= 0) {
            if (uint256(index) > lastProcessedIndex) {
                iterationsUntilProcessed = index.sub(
                    int256(lastProcessedIndex)
                );
            } else {
                uint256 processesUntilEndOfArray = tokenHoldersMap.keys.length >
                    lastProcessedIndex
                    ? tokenHoldersMap.keys.length.sub(lastProcessedIndex)
                    : 0;

                iterationsUntilProcessed = index.add(
                    int256(processesUntilEndOfArray)
                );
            }
        }

        withdrawableDividends = withdrawableDividendOf(account);
        totalDividends = accumulativeDividendOf(account);

        lastClaimTime = lastClaimTimes[account];

        nextClaimTime = lastClaimTime > 0 ? lastClaimTime.add(claimWait) : 0;

        secondsUntilAutoClaimAvailable = nextClaimTime > block.timestamp
            ? nextClaimTime.sub(block.timestamp)
            : 0;
    }

    function getAccountAtIndex(uint256 index)
        public
        view
        returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        if (index >= tokenHoldersMap.size()) {
            return (
                0x0000000000000000000000000000000000000000,
                -1,
                -1,
                0,
                0,
                0,
                0,
                0
            );
        }

        address account = tokenHoldersMap.getKeyAtIndex(index);

        return getAccount(account);
    }

    function canAutoClaim(uint256 lastClaimTime) private view returns (bool) {
        if (lastClaimTime > block.timestamp) {
            return false;
        }

        return block.timestamp.sub(lastClaimTime) >= claimWait;
    }

    function setBalance(address payable account, uint256 newBalance)
        external
        onlyOwner
    {
        if (excludedFromDividends[account]) {
            return;
        }

        if (newBalance >= minimumTokenBalanceForDividends) {
            _setBalance(account, newBalance);
            tokenHoldersMap.set(account, newBalance);
        } else {
            _setBalance(account, 0);
            tokenHoldersMap.remove(account);
        }

        processAccount(account, true);
    }

    function process(uint256 gas)
        public
        returns (
            uint256,
            uint256,
            uint256
        )
    {
        uint256 numberOfTokenHolders = tokenHoldersMap.keys.length;

        if (numberOfTokenHolders == 0) {
            return (0, 0, lastProcessedIndex);
        }

        uint256 _lastProcessedIndex = lastProcessedIndex;

        uint256 gasUsed = 0;

        uint256 gasLeft = gasleft();

        uint256 iterations = 0;
        uint256 claims = 0;

        while (gasUsed < gas && iterations < numberOfTokenHolders) {
            _lastProcessedIndex++;

            if (_lastProcessedIndex >= tokenHoldersMap.keys.length) {
                _lastProcessedIndex = 0;
            }

            address account = tokenHoldersMap.keys[_lastProcessedIndex];

            if (canAutoClaim(lastClaimTimes[account])) {
                if (processAccount(payable(account), true)) {
                    claims++;
                }
            }

            iterations++;

            uint256 newGasLeft = gasleft();

            if (gasLeft > newGasLeft) {
                gasUsed = gasUsed.add(gasLeft.sub(newGasLeft));
            }
            gasLeft = newGasLeft;
        }

        lastProcessedIndex = _lastProcessedIndex;

        return (iterations, claims, lastProcessedIndex);
    }

    function processAccount(address payable account, bool automatic)
        public
        onlyOwner
        returns (bool)
    {
        uint256 amount = _withdrawDividendOfUser(account);

        if (amount > 0) {
            lastClaimTimes[account] = block.timestamp;
            emit Claim(account, amount, automatic);
            return true;
        }

        return false;
    }
}

contract GameTracker is Ownable {
    IUniswapV2Router02 uniswapV2Router =
        IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

    function swapForEth() external onlyOwner returns (bool) {
        swapTokensForEth(IERC20(owner()).balanceOf(address(this)));
        uint256 balance = address(this).balance;

        (bool success, ) = address(owner()).call{value: balance}("");
        return success;
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = owner();
        path[1] = uniswapV2Router.WETH();

        IERC20(owner()).approve(address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    receive() external payable {}
}
