// OpenZeppelin Contracts v4.3.2 (finance/VestingWallet.sol)
contract WSGTreasuryDepositNFT is Ownable {
    event RankNFTActivated(uint256 rankId, address account);
    event GameNFTActivated(uint256 gameId, address account);
    event KeysReceived(uint256 keyAmount, uint256 id, address account);

    struct RankKeySwapPair {
        uint256 cards;
        uint256 keys;
    }

    address private _treasury;
    IERC1155Mintable public immutable nftContract;
    uint256 public pricePerKey = 4250000 ether;
    mapping(uint256 => uint256) public gamePrices;
    mapping(address => uint256) public userRanks;
    mapping(address => mapping(uint256 => bool)) public userGames;

    mapping(uint256 => RankKeySwapPair) private swapRates;
    mapping(address => bool) private _blacklisted;
    address private constant wsg = address(0xA58950F05FeA2277d2608748412bf9F802eA4901);

    constructor (
        address treasury_
    ) {
        _treasury = treasury_;
        nftContract = IERC1155Mintable(0xe86E4b3bB1846a017153CedCD0458dc9Ad835D9b);
        setGamePrice(101, 197500000);
        swapRates[1] = RankKeySwapPair(10,1);
        swapRates[2] = RankKeySwapPair(5,1);
        swapRates[3] = RankKeySwapPair(1,1);
        swapRates[4] = RankKeySwapPair(1,5);
        swapRates[5] = RankKeySwapPair(1,10);
        swapRates[6] = RankKeySwapPair(1,30);
        swapRates[7] = RankKeySwapPair(1,100);
    }

    function hasGame(address account, uint256 gameId) public view virtual returns (bool) {
        return userGames[account][gameId];
    }

    function isBlacklisted(address account) public view virtual returns (bool) {
        return _blacklisted[account];
    }

    function treasury() public view virtual returns (address) {
        return _treasury;
    }

    function activateRank(uint256 rankId) public virtual {
        require(nftContract.balanceOf(msg.sender, rankId) >= 1, '!rankNft');
        require(userRanks[msg.sender] == rankId - 1, '!rank');
        require(!_blacklisted[msg.sender], '!blacklisted');
        userRanks[msg.sender]++;
        nftContract.burn(msg.sender, rankId, 1);
        emit RankNFTActivated(rankId, msg.sender);
    }

    function activateGame(uint256 gameId) public virtual {
        require(nftContract.balanceOf(msg.sender, gameId) >= 1, '!gameNft');
        require(!userGames[msg.sender][gameId], '!gameId');
        require(!_blacklisted[msg.sender], '!blacklisted');
        userGames[msg.sender][gameId] = true;
        nftContract.burn(msg.sender, gameId, 1);
        emit GameNFTActivated(gameId, msg.sender);
    }

    function buyGame(uint256 gameId) public virtual {
        require(!userGames[msg.sender][gameId], '!gameId');
        require(!_blacklisted[msg.sender], '!blacklisted');
        uint256 amount = gamePrices[gameId];
        if (amount > 0) {
            userGames[msg.sender][gameId] = true;
            SafeBEP20.safeTransferFrom(IBEP20(wsg), msg.sender, address(this), amount);
            emit GameNFTActivated(gameId, msg.sender);
        }
    }

    function swapRankForKeys(uint256 rankId, uint256 keyAmount, uint256 id) public virtual {
        require(IBEP20(wsg).balanceOf(msg.sender) >= keyAmount * pricePerKey, '!balance');
        require(!_blacklisted[msg.sender], '!blacklisted');
        RankKeySwapPair storage pair = swapRates[rankId];
        require(keyAmount >= pair.keys, '!min');
        require(keyAmount % pair.keys == 0, '!multiplier');

        uint256 total = keyAmount * pricePerKey; 
        uint256 cards;
        if (pair.keys >= pair.cards) {
            cards = keyAmount / pair.keys;
        } else {
            cards = keyAmount * pair.cards;
        }

        require(nftContract.balanceOf(msg.sender, rankId) >= cards, '!nfts');
        nftContract.burn(msg.sender, rankId, cards);

        SafeBEP20.safeTransferFrom(IBEP20(wsg), msg.sender, address(this), total);
        emit KeysReceived(keyAmount, id, msg.sender);
    }

    /** RESTRICTED FUNCTION */

    function release() external virtual onlyOwner {
        Address.sendValue(payable(treasury()), address(this).balance);
    }

    function release(address token) external virtual onlyOwner {
        SafeBEP20.safeTransfer(IBEP20(token), treasury(), IBEP20(token).balanceOf(address(this)));
    }

    function setGamePrice(uint256 gameId, uint256 price) public virtual onlyOwner {
        gamePrices[gameId] = price * 10**18;
    }

    function setUserRank(address account, uint256 rankId) external virtual onlyOwner {
        userRanks[account] = rankId;
    }

    function setSwapRate(uint256 rank, uint256 cards, uint256 keys) external virtual onlyOwner {
        swapRates[rank] = RankKeySwapPair(cards, keys);
    }

    function setSwapPrice(uint256 price) external virtual onlyOwner {
        pricePerKey = price * 10**18;
    }

    function toggleBlacklisted(address account) external virtual onlyOwner {
        _blacklisted[account] = !_blacklisted[account];
    }

    function setTreasury(address treasury_) external virtual onlyOwner {
        require(treasury_ != address(0), '!treasury');
        _treasury = treasury_;
    }
}
