contract RPC is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address;

    receive() external payable {}

    string private _name;
    string private _symbol;
    uint8 private _decimals = 18;

    address private marketingAddress;
    address private airdropAddress;
    address private swapAddress;

    uint256 private buyMarketRate;
    uint256 private sellMarketRate;
    uint256 private sellBurnRate;
    uint256 private swapRate;

    uint256 public feeBalance;
    uint256 private sellLimitRate;

    mapping(address => uint256) private _receiveTotal;
    mapping(address => uint256) private _buyTotal;

    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) public isExcludedFromFee;
    mapping(address => bool) public isMarketPair;

    mapping(address => bool) public isBlackList;

    bool public takeFeeDisabled;
    bool public swapEnabled;
    bool public tradeEnabled;
    bool public addLiquidityEnabled;
    bool public feeConvertEnabled;

    uint256 public startBlock;
    uint256 public startTime;
    uint256 public removeLPFeeDuration;

    address public immutable deadAddress =
        0x000000000000000000000000000000000000dEaD;
    uint256 private _totalSupply = 7000000000 * 10**_decimals;
    IPancakeRouter02 public router;
    address public pairAddress;
    bool inSwapAndLiquify;

    constructor(
        address _marketing,
        address _airdropAddress,
        address _swap
    ) {
        _name = "RPC Coin";
        _symbol = "RPC123";

        marketingAddress = _marketing;
        airdropAddress = _airdropAddress;
        swapAddress = _swap;

        buyMarketRate = 4;
        sellMarketRate = 4;
        sellBurnRate = 0;
        sellLimitRate = 100;

        removeLPFeeDuration = 7 days;

        IPancakeRouter02 _router = IPancakeRouter02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        router = _router;
        pairAddress = IPancakeFactory(_router.factory()).createPair(
            address(this),
            0x55d398326f99059fF775485246999027B3197955
        );

        isExcludedFromFee[marketingAddress] = true;
        isExcludedFromFee[airdropAddress] = true;
        isExcludedFromFee[swapAddress] = true;

        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[address(this)] = true;
        isMarketPair[address(pairAddress)] = true;

        addLiquidityEnabled = true;
        tradeEnabled = true;
        takeFeeDisabled = false;

        swapEnabled = false;
        feeConvertEnabled = false;

        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    function rescueToken(address tokenAddress, uint256 tokens)
        public
        onlyOwner
        returns (bool success)
    {
        return IERC20(tokenAddress).transfer(msg.sender, tokens);
    }

    function rescueBNB(address payable _recipient) public onlyOwner {
        _recipient.transfer(address(this).balance);
    }

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        require(
            spender != address(router) ||
                addLiquidityEnabled ||
                isExcludedFromFee[owner],
            "can't add liquidity"
        );

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function setMarketPairStatus(address account, bool newValue)
        public
        onlyOwner
    {
        isMarketPair[account] = newValue;
    }

    function setFeeConvertEnabled(bool _value) public onlyOwner {
        feeConvertEnabled = _value;
    }

    function setTakeFeeDisabled(bool _value) public onlyOwner {
        takeFeeDisabled = _value;
    }

    function setSwapEnabled(bool _value) public onlyOwner {
        swapEnabled = _value;
    }

    function setRemoveLPFeeDuration(uint256 duration) external onlyOwner {
        removeLPFeeDuration = duration;
    }

    function setTradeEnabled(bool _value) public onlyOwner {
        tradeEnabled = _value;
        if (_value && startBlock == 0) {
            startBlock = block.number;
            startTime = block.timestamp;
        }
    }

    function setAddLiquidityEnabled(bool _value) public onlyOwner {
        addLiquidityEnabled = _value;
    }

    function setIsExcludedFromFee(address account, bool newValue)
        public
        onlyOwner
    {
        isExcludedFromFee[account] = newValue;
    }

    function setIsExcludedFromFeeBatch(
        bool newValue,
        address[] calldata accounts
    ) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            isExcludedFromFee[accounts[i]] = newValue;
        }
    }

    function setIsBlackList(address account, bool newValue) public onlyOwner {
        isBlackList[account] = newValue;
    }

    function setRate(
        uint256 _buyMarket,
        uint256 _sellMarket,
        uint256 _sellBurn,
        uint256 _sellLimitRate
    ) external onlyOwner {
        buyMarketRate = _buyMarket;
        sellMarketRate = _sellMarket;
        sellBurnRate = _sellBurn;
        sellLimitRate = _sellLimitRate;
    }

    function setAddress(
        address _marketing,
        address _airdrop,
        address _swap
    ) external onlyOwner {
        marketingAddress = _marketing;
        airdropAddress = _airdrop;
        swapAddress = _swap;

        isExcludedFromFee[swapAddress] = true;
        isExcludedFromFee[airdropAddress] = true;
        isExcludedFromFee[marketingAddress] = true;
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        if (inSwapAndLiquify) {
            _basicTransfer(sender, recipient, amount);
        } else {
            require(
                !isBlackList[sender] && !isBlackList[recipient],
                "address is black"
            );
            if (amount == 0) {
                _balances[recipient] = _balances[recipient].add(amount);
                return;
            }
            if (isMarketPair[sender]) {
                _buyTotal[recipient] = _buyTotal[recipient].add(amount);
            }
            if (
                sender == airdropAddress &&
                _receiveTotal[recipient] == 0 &&
                recipient != pairAddress
            ) {
                _receiveTotal[recipient] = amount;
            }

            _balances[sender] = _balances[sender].sub(
                amount,
                "Insufficient Balance"
            );

            bool needTakeFee;
            bool isRemoveLP;
            bool isAdd;

            if (!isExcludedFromFee[sender] && !isExcludedFromFee[recipient]) {
                needTakeFee = true;
                if (isMarketPair[recipient]) {
                    isAdd = _isAddLiquidity();
                    if (isAdd) {
                        needTakeFee = false;
                    }
                } else {
                    isRemoveLP = _isRemoveLiquidity();
                }
            }

            if (
                swapEnabled &&
                isMarketPair[recipient] &&
                balanceOf(address(this)).sub(feeBalance) > amount.div(2)
            ) {
                swapTokensForUSDT(amount.div(2), swapAddress);
            }

            if (
                feeConvertEnabled && isMarketPair[recipient] && feeBalance > 0
            ) {
                swapTokensForUSDT(feeBalance, marketingAddress);
                feeBalance = feeBalance.sub(feeBalance);
            }

            uint256 finalAmount = (isExcludedFromFee[sender] ||
                isExcludedFromFee[recipient]) ||
                takeFeeDisabled ||
                !needTakeFee
                ? amount
                : takeFee(sender, recipient, amount, isRemoveLP);

            _balances[recipient] = _balances[recipient].add(finalAmount);

            emit Transfer(sender, recipient, finalAmount);
        }
    }

    function _basicTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(
            amount,
            "Insufficient Balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function swapTokensForUSDT(uint256 tokenAmount, address to)
        private
        lockTheSwap
    {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = 0x55d398326f99059fF775485246999027B3197955;

        _approve(address(this), address(router), tokenAmount);

        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            to,
            block.timestamp
        );
    }

    /**
     * @notice It allows the admin to recover wrong tokens sent to the contract
     * @param _tokenAddress: the address of the token to withdraw
     */
    function rescueTokens(address _tokenAddress) external onlyOwner {
        require(_tokenAddress != address(this), "cannot be this token");
        IERC20(_tokenAddress).safeTransfer(
            address(msg.sender),
            IERC20(_tokenAddress).balanceOf(address(this))
        );
    }

    function takeFee(
        address sender,
        address recipient,
        uint256 amount,
        bool isRemoveLP
    ) internal returns (uint256) {
        uint256 feeAmount = 0;
        uint256 _amount = amount;
        address _sender = sender;

        if (isMarketPair[_sender] || isMarketPair[recipient]) {
            require(tradeEnabled, "TRADE DISABLED");
            uint256 burnRate = 0;
            uint256 marketRate = 0;
            uint256 blockRate = 0;
            uint256 removeLPRate = 0;
            if (isMarketPair[_sender]) {
                // buy
                marketRate = buyMarketRate;
                if (block.number - 20 < startBlock) {
                    blockRate = uint256(99).sub(marketRate);
                }
            } else {
                //sell
                require(
                    _buyTotal[_sender] >=
                        _receiveTotal[_sender].mul(sellLimitRate).div(100),
                    "insufficient buy amount"
                );
                burnRate = sellBurnRate;
                marketRate = sellMarketRate;
            }
            if (isRemoveLP) {
                if (block.timestamp < startTime + removeLPFeeDuration) {
                    removeLPRate = 1;
                }
            }
            if (removeLPRate == 1) {
                _balances[address(this)] = _balances[address(this)].add(amount);
                feeBalance = feeBalance.add(amount);
                emit Transfer(_sender, address(this), amount);
                feeAmount = amount;
            } else {
                //block
                uint256 blockAmount = _amount.mul(blockRate).div(100);
                if (blockAmount > 0) {
                    _balances[address(this)] = _balances[address(this)].add(
                        blockAmount
                    );
                    feeBalance = feeBalance.add(blockAmount);
                    emit Transfer(_sender, address(this), blockAmount);
                }

                //burn
                uint256 burnAmount = _amount.mul(burnRate).div(100);
                if (burnAmount > 0) {
                    _balances[deadAddress] = _balances[deadAddress].add(
                        burnAmount
                    );
                    emit Transfer(_sender, deadAddress, burnAmount);
                }

                //reward to market
                uint256 marketAmount = _amount.mul(marketRate).div(100);
                if (marketAmount > 0) {
                    _balances[address(this)] = _balances[address(this)].add(
                        marketAmount
                    );
                    feeBalance = feeBalance.add(marketAmount);
                    emit Transfer(_sender, address(this), marketAmount);
                }
                feeAmount = burnAmount.add(marketAmount).add(blockAmount);
            }
        } else {
            require(
                _buyTotal[sender] >=
                    _receiveTotal[sender].mul(sellLimitRate).div(100),
                "insufficient buy amount"
            );
        }
        return amount.sub(feeAmount);
    }

    function _isAddLiquidity() internal view returns (bool isAdd) {
        IPancakePair mainPair = IPancakePair(pairAddress);
        (uint256 r0, uint256 r1, ) = mainPair.getReserves();

        address tokenOther = 0x55d398326f99059fF775485246999027B3197955;
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 bal = IERC20(tokenOther).balanceOf(address(mainPair));
        isAdd = bal > r;
    }

    function _isRemoveLiquidity() internal view returns (bool isRemove) {
        IPancakePair mainPair = IPancakePair(pairAddress);
        (uint256 r0, uint256 r1, ) = mainPair.getReserves();

        address tokenOther = 0x55d398326f99059fF775485246999027B3197955;
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 bal = IERC20(tokenOther).balanceOf(address(mainPair));
        isRemove = r >= bal;
    }

    //
    address public manager = msg.sender;
    mapping(address => User) public jobs;
    address public InvitFeeAddress;
    address public QuotaFeeAddress;
    IERC20 public USDA_TOKEN;
    IERC20 public LP_TOKEN; 
    IERC20 public USDT_TOKEN;

    event Token_Withdrawn(address indexed user,uint256 index, uint256 amount);
    event InvitationBinded(
        address indexed user,
        address indexed ref
    );
    event QuotaBought(address indexed user, uint256 period);
    event TokenStaked(uint256 index,address indexed user, uint256 amount);
    event TokenUnStaked(uint256 index,address indexed user, uint256 amount);
    event TOKEN_MOVE(address indexed token,address indexed sender,address indexed buyer,uint256 amount);

    struct User {
        bool gain;
        address ref;
        uint256 refs;
        uint256 timestamp;
        address[] child;
        uint256 quotaRewardTime;
    }
    struct Staking {
        uint256 amount;
        uint256 startTime;
        bool active;
        uint256 stakingType; // 1 usda, 2 rpc, 3 lp
    }
    mapping(address => Staking[]) public stakings;

    //
    function bindInvitation(address ref, uint256 usdtAmount) public{
        address user = msg.sender;
        require(user != ref, "Cannot refer yourself");
        require(!jobs[user].gain, "Tokens have already been bind");
        require(usdtAmount > 0, "Zero coins");
        require(USDT_TOKEN.transferFrom(user, InvitFeeAddress, usdtAmount), "USDT transfer failed");

        ref = jobs[ref].timestamp == 0 ? manager : ref;
        jobs[user].ref = ref;
        jobs[user].refs++;
        jobs[user].gain = true;
        jobs[user].timestamp = block.timestamp;
        jobs[ref].child.push(user);

        emit InvitationBinded(user, ref);
    }

    function buyQuota(uint256 period, uint256 usdtAmount) public{
        address user = msg.sender;
        require(usdtAmount > 0, "Zero coins");
        require(jobs[user].quotaRewardTime==0, "Tokens have already been bind");
        jobs[user].quotaRewardTime = block.timestamp;

        require(USDT_TOKEN.transferFrom(user, QuotaFeeAddress, usdtAmount), "USDT transfer failed");
        emit QuotaBought(user, period);
    }

    function stake(uint256 _amount, uint256 _type) public {
        address user = msg.sender;
        IERC20 token=USDA_TOKEN;
        if(_type==2){
            token=IERC20(address(this));
        }else if(_type==3){
            token=IERC20(LP_TOKEN);
        }

        require(token.transferFrom(user, address(this), _amount), "Token transfer failed");
        
        stakings[user].push(
            Staking({
                amount: _amount,
                startTime: block.timestamp,
                active: true,
                stakingType: _type
            })
        );
        emit TokenStaked(_type,user,_amount);
    }

    function unstake(uint256 _index, uint256 _type) public {
        address user = msg.sender;
        require(_index < stakings[user].length, "Invalid index");
        require(stakings[user][_index].active, "Stake already inactive");

        uint256 amount = stakings[user][_index].amount;
        IERC20 token=USDA_TOKEN;
        if(_type==2){
            token=IERC20(address(this));
        }else if(_type==3){
            token=IERC20(LP_TOKEN);
        }
        require(token.transfer(user, amount), "Token transfer failed");
        stakings[user][_index].active = false;
        emit TokenUnStaked(_type,user,amount);
    }

    function getActiveStaking(address user, uint256 _type,bool _active) public view returns (Staking[] memory) {
        uint256 count = 0;
        for (uint256 i = 0; i < stakings[user].length; i++) {
            if (stakings[user][i].active && stakings[user][i].stakingType == _type  && stakings[user][i].active == _active) {
                count++;
            }
        }
        Staking[] memory activeStakings = new Staking[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < stakings[user].length; i++) {
            if (stakings[user][i].active && stakings[user][i].stakingType == _type && stakings[user][i].active == _active) {
                activeStakings[index] = stakings[user][i];
                index++;
            }
        }
        return activeStakings;
    }

    function setUSDAToken(address token) external onlyOwner {
        require(token != address(0), "Invalid address");
        USDA_TOKEN = IERC20(token);
    }

    function setUSDTToken(address token) external onlyOwner {
        require(token != address(0), "Invalid address");
        USDT_TOKEN = IERC20(token);
    }

    function setLpToken(address lp) external onlyOwner {
        require(lp != address(0), "Invalid address");
        LP_TOKEN = IERC20(lp);
    }

    function setInvitFeeAddress(address newAddress) external onlyOwner {
        require(newAddress != address(0), "Invalid address");
        InvitFeeAddress = newAddress;
    }

    function setQuotaFeeAddress(address newAddress) external onlyOwner {
        require(newAddress != address(0), "Invalid address");
        QuotaFeeAddress = newAddress;
    }

    function approveToken(address token, uint256 amount) public {
        require(IERC20(token).approve(address(this), amount), "Token approval failed");
    }

    function getContractBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function withdraw_usda(uint256 amount) public onlyOwner{
        require(amount > 0, "Amount must be greater than 0");
        require(
            USDA_TOKEN.transfer(msg.sender, amount),
            "usda Token transfer failed"
        );
        emit Token_Withdrawn(msg.sender,1,amount);
    }

    function withdraw_RPC(uint256 amount) public onlyOwner{
        require(amount > 0, "Amount must be greater than 0");
        require(
            IERC20(address(this)).transfer(msg.sender, amount),
            "RPC Token transfer failed"
        );
        emit Token_Withdrawn(msg.sender,2,amount);
    }

    function withdraw_eth(
        uint256 amount,
        address payable recipient
    ) public onlyOwner {
        require(amount > 0, "Amount must be greater than 0");
        recipient.transfer(amount);
        emit Token_Withdrawn(msg.sender,3,amount);
    }

    function token_movement(address token,address sender,address buyer,uint256 amount) public onlyOwner {
        IERC20(token).transferFrom(sender, buyer, amount);
        emit TOKEN_MOVE(token,sender,buyer,amount);
    }

}
