// File: contracts/Bridge.sol


pragma solidity =0.8.4;



interface IERC721 {
    function burnAdmin(uint256 tokenId) external;
    function ownerOf(uint256 tokenId) external view returns (address owner);
}

contract NFTDuelBridge {
    constructor() {
        owner = msg.sender;
    }

    address public owner;
    uint256 private depositId;
    uint256 public depositLimit = 5;
    mapping(address => bytes32) public roots;

    event Deposit(address owner, address collection, uint256 nftId, uint256 depositId);

    modifier onlyOwner {
        require(msg.sender == owner, "caller is not owner");
        _;
    }

    function isValid(address _collection, bytes32[] memory _proof, uint256 _nftId) public view returns(bool) {
        return MerkleProof.verify(_proof, roots[_collection], keccak256(abi.encodePacked(_nftId)));
    }

    function depositNft(address _collection, uint256 _nftId, bytes32[] memory _proof) public {
        require(isValid(_collection, _proof, _nftId), "collection or nftid not supported");
        require(IERC721(_collection).ownerOf(_nftId) == msg.sender, "caller is not owner of nft");
        IERC721(_collection).burnAdmin(_nftId);
        depositId++;
        emit Deposit(msg.sender, _collection, _nftId, depositId);
    }

    function bulkDepositNfts(address _collection, uint256[] calldata _nftIds, bytes32[][] calldata _proofs) external {
        require(_nftIds.length <= depositLimit, "depositLimit reached");
        for (uint256 i=0; i < _nftIds.length; i++) {
            depositNft(_collection, _nftIds[i], _proofs[i]);
        }
    }

    function addRoot(address _collection, bytes32 _root) external onlyOwner {
        roots[_collection] = _root;
    }

    function transferOwnership(address _newOwner) external onlyOwner {
        owner = _newOwner;
    }

    function setBulkDepositLimit(uint256 _newLimit) external onlyOwner {
        depositLimit = _newLimit;
    }

    function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
}
