      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.10;

import "./lib/ZoaVesting.sol";

// solhint-disable not-rely-on-time

contract ZoaVesting2Private is ZoaVesting {
    constructor(address _token, bytes32 _mercleRoot, uint256 _tgeTimestamp) {
        require(_token != address(0), "ZoaVesting2Private: zero token address");
        require(_mercleRoot != bytes32(0), "ZoaVesting2Private: zero mercle root");
        require(_tgeTimestamp >= block.timestamp, "ZoaVesting2Private: wrong TGE timestamp");

        token = _token;
        mercleRoot = _mercleRoot;
        tgeTimestamp = _tgeTimestamp;

        // rounds settings
        // (120000 + 97778 * 9) / 1e6 = 1.000002 => 1.0
        categories[CategoryNames.SALE_CATEGORY] = CategoryType({
            totalSteps: 9,
            stepTime: 30 days,
            tgePercent: 120000,
            stepStageDelay: 120 days,
            stepPercent: 97778
        });

        inited = true;
    }
}
"
