      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "./IStaking.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Staking is IStaking, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    uint16 public constant FEE_DENOMINATOR = 10000;
    uint72 public constant PRECISION_FACTOR = 1000000000000000000000;

    uint24 private constant DAY = 86400;

    uint256 public immutable REWARD_PER_SECOND;
    uint256 public immutable REWARD_PER_SECOND_DENOMINATOR;
    IERC20 public immutable QUACK;

    uint256[3] public fees = [250, 250, 2000];

    uint256 public poolStartTime;
    uint256 public poolEndTime;
    uint256 public totalStaked;
    uint256 public accTokenPerShare;
    uint256 public feesCollected;

    uint256 private lastActionTime;
    uint256 private totalRewardTaken;

    mapping (address => UserInfo) public stakeInfo;

    struct UserInfo {
        uint256 amount;
        uint256 rewardTaken;
        uint256 rewardTakenDay;
        uint256 enteredAt;
    }

    modifier startedAndNotEnded() {
        require(poolStartTime > 0 && poolEndTime == 0, "Pool not yet started or already ended");
        _;
    }

    constructor(address _quack, uint256 _rewardPerSecond, uint256 _rewardPerSecondDenominator) {
        REWARD_PER_SECOND = _rewardPerSecond;
        REWARD_PER_SECOND_DENOMINATOR = _rewardPerSecondDenominator;
        QUACK = IERC20(_quack);
    }

    function pendingReward(address account) external view returns(uint256) {
        if (totalStaked > 0) {
            UserInfo storage stake = stakeInfo[account];
            uint256 rightBound;
            if (poolEndTime > 0) {
                rightBound = poolEndTime;
            }
            else {
                rightBound = block.timestamp;
            }
            uint256 adjustedTokenPerShare =
                accTokenPerShare + ((((rightBound - lastActionTime) * REWARD_PER_SECOND) * PRECISION_FACTOR) / REWARD_PER_SECOND_DENOMINATOR) / totalStaked;
            return ((stake.amount * adjustedTokenPerShare) / PRECISION_FACTOR) - stake.rewardTaken;
        }
        else {
            return 0;
        }
    }

    function deposit(uint256 amount) external startedAndNotEnded nonReentrant {
        require(amount > 0, "Cannot stake zero");
        UserInfo storage stake = stakeInfo[_msgSender()];
        _updatePool();
        QUACK.safeTransferFrom(_msgSender(), address(this), amount);
        amount = _takeFee(amount, fees[0]);
        uint256 reward = ((stake.amount * accTokenPerShare) / PRECISION_FACTOR) - stake.rewardTaken;
        if (reward > 0) {
            require(QUACK.balanceOf(address(this)) - (totalStaked + feesCollected) >= reward, "Not enough token for reward");
            totalRewardTaken += reward;
            QUACK.safeTransfer(_msgSender(), reward);
        }
        totalStaked += amount;
        stake.amount += amount;
        stake.rewardTaken = ((stake.amount * accTokenPerShare) / PRECISION_FACTOR);
        stake.enteredAt = block.timestamp;
    }

    function withdraw(uint256 amount) external nonReentrant {
        UserInfo storage stake = stakeInfo[_msgSender()];
        require(stake.amount >= amount, "Cannot withdraw this much");
        _updatePool();
        uint256 toTransfer = ((stake.amount * accTokenPerShare) / PRECISION_FACTOR) - stake.rewardTaken;
        require(QUACK.balanceOf(address(this)) - (totalStaked + feesCollected) >= toTransfer, "Not enough token for reward");
        totalRewardTaken += toTransfer;
        if (amount > 0) {
            if (stake.enteredAt + (DAY * 14) > block.timestamp) {
                toTransfer += _takeFee(amount, fees[2]);
            }
            else {
                toTransfer += _takeFee(amount, fees[1]);
            }
        }
        else {
            uint256 currentDay = determineDay(block.timestamp);
            require(stake.rewardTakenDay < currentDay, "Cannot take reward twice a day");
            stake.rewardTakenDay = currentDay;
        }
        if (amount > 0) {
            stake.amount -= amount;
            totalStaked -= amount;
        }
        if (stake.amount == 0) {
            delete stakeInfo[_msgSender()];
        }
        else {
            stake.rewardTaken = (stake.amount * accTokenPerShare) / PRECISION_FACTOR;
        }
        if (toTransfer > 0) {
            QUACK.safeTransfer(_msgSender(), toTransfer);
        }
    }

    function emergencyWithdraw() external nonReentrant {
        UserInfo storage stake = stakeInfo[_msgSender()];
        uint256 toTransfer;
        if (stake.enteredAt + (DAY * 14) > block.timestamp) {
            toTransfer = _takeFee(stake.amount, fees[2]);
        }
        else {
            toTransfer = _takeFee(stake.amount, fees[1]);
        }
        totalStaked -= stake.amount;
        delete stakeInfo[_msgSender()];
        QUACK.safeTransfer(_msgSender(), toTransfer);
    }

    function editFees(uint256[3] calldata _fees) external onlyOwner {
        for (uint8 i; i < 3; i++) {
            require(_fees[i] <= 10000, "Cannot set fee higher than 100%");
            fees[i] = _fees[i];
        }
    }

    function getFees() external onlyOwner nonReentrant {
        QUACK.safeTransfer(_msgSender(), feesCollected);
        delete feesCollected;
    }

    function startPool() external onlyOwner {
        require(poolStartTime == 0, "Already started");
        poolStartTime = block.timestamp;
        lastActionTime = block.timestamp;
    }

    function endPool() external startedAndNotEnded onlyOwner {
        poolEndTime = block.timestamp;
        uint256 toTransfer = QUACK.balanceOf(address(this)) - ((((block.timestamp - poolStartTime) * REWARD_PER_SECOND) / REWARD_PER_SECOND_DENOMINATOR) - totalRewardTaken) - totalStaked - feesCollected;
        QUACK.safeTransfer(_msgSender(), toTransfer);
    }

    function determineDay(uint256 timestamp) public view returns(uint256) {
        return (timestamp - poolStartTime) / DAY + 1;
    }

    function _updatePool() private {
        if (totalStaked > 0) {
            uint256 rightBound;
            if (poolEndTime > 0) {
                rightBound = poolEndTime;
            }
            else {
                rightBound = block.timestamp;
            }
            accTokenPerShare += ((((rightBound - lastActionTime) * REWARD_PER_SECOND) * PRECISION_FACTOR) / REWARD_PER_SECOND_DENOMINATOR) / totalStaked;
        }
        if (lastActionTime != poolEndTime) {
            if (poolEndTime > 0) {
                lastActionTime = poolEndTime;
            }
            else {
                lastActionTime = block.timestamp;
            }
        }
    }

    function _takeFee(uint256 amount, uint256 fee) private returns(uint256) {
        uint256 toReturn = (amount * (FEE_DENOMINATOR - fee)) / FEE_DENOMINATOR;
        feesCollected += amount - toReturn;
        return toReturn;
    }
}"
