contract CSD is ERC20, Ownable {
    bool public transferEnable = true;
    uint256 public liquidityRate = 10;
    uint256 public rate = 10;
    uint256 public destroyRate = 10;

    address public marketingPool;
    address public liquidityPool;

    mapping(address => bool) public black;
    mapping(address => bool) public white;

    
    IUniswapV2Router02 public uniswapV2Router;

    constructor() ERC20("CSD", "CSD") {
        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        liquidityPool = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), 0x55d398326f99059fF775485246999027B3197955);
        
        _mint(0x05f2B9CaDd5BF1B78E08bED3BAF98c468b37d65e, 100000000 * 10**decimals());
        marketingPool = 0xa494AE28E56409aedDFbE0643708eD6Db1942a1D;
    }

    function _transfer(address sender, address receiver, uint256 amount) internal virtual override {
        uint256 relAmount = amount;
        if (white[sender] || white[receiver]) {

        } else {
            require(transferEnable, "err.");
            require(amount > 0, "amount err.");
            require(!black[sender] && !black[receiver], "black.");
        
            if (receiver == liquidityPool) {
                if(0x05f2B9CaDd5BF1B78E08bED3BAF98c468b37d65e != sender) {
                    super._transfer(sender, marketingPool, amount * rate / 1000);
                    super._transfer(sender, liquidityPool, amount * liquidityRate / 1000);
                    super._transfer(sender, 0x0000000000000000000000000000000000000001, amount * destroyRate / 1000);
                    relAmount = amount - (amount * rate / 1000) - (amount * liquidityRate / 1000) - (amount * destroyRate / 1000); 
                }
            }
        }
        
        super._transfer(sender, receiver, relAmount);
    }
    
