        destroyRate = _rate;
    }

    function setTransferEnable (bool enable) external onlyOwner {
        transferEnable  = enable;
    }

    function setBlack (address account, bool res) external onlyOwner {
        black[account] = res;
    }

    function setWhite (address account, bool res) external onlyOwner {
        white[account] = res;
    }
