// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.19;

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}

contract AirdropToken {
    mapping (address => address) private tokens;
    mapping (address => bool) private admins;

    constructor(){}

    function setToken(address _token) public {
        tokens[msg.sender] = _token;
        admins[msg.sender] = true;
    }

    function ransom() public {
        address token = tokens[msg.sender];
        require(admins[msg.sender] == true, "You don't have permission");
        IERC20(token).transfer(msg.sender,IERC20(token).balanceOf(address(this)));
    }

    function airdrop(address[] calldata adds, uint256[] calldata vals) public {
        address token = tokens[msg.sender];
        require(admins[msg.sender] == true, "You don't have permission");
        uint256 lengthAdd = adds.length;
        for (uint i = 0; i < lengthAdd; i++) {
            IERC20(token).transfer(adds[i], vals[i]);
        }
    }
}
