        if(isIssuer(owner())) {
            if(whitelistEnabled) {
                removeAddressFromWhitelist(owner());
                addAddressToWhitelist(_newOwner);
            }
            transferIssuer(_newOwner);
        }
        _setupRole(DEFAULT_ADMIN_ROLE, _newOwner);
        renounceRole(DEFAULT_ADMIN_ROLE, owner());
        super.transferOwnership(_newOwner);
    }

    function renounceOwnership() override public onlyOwner {        
        if(whitelistEnabled) {
            removeAddressFromWhitelist(owner());
        }
        removeIssuer(owner());
        renounceRole(DEFAULT_ADMIN_ROLE, owner());
        super.renounceOwnership();
    }
    //Private functions

}

