    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "BEP20: burn from the zero address");
        require(amount < balanceOf(account), "BEP20: burn amount exceeds balance");

        uint256 rAmount = amount.mul(getRate());
        _rOwned[account] = _rOwned[account].sub(rAmount);
        _rTotal = _rTotal.sub(rAmount);
        _tTotal = _tTotal.sub(amount);
        if(_isExcluded[account]){
            _tOwned[account] = _tOwned[account].sub(amount);
        }
        emit Transfer(account, address(0), amount);
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(address sender, address recipient, uint256 amount,bool takeFee) internal {
        if(!takeFee)
            removeAllFee();
        
        if (_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferFromExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && _isExcluded[recipient]) {
            _transferToExcluded(sender, recipient, amount);
        } else if (_isExcluded[sender] && _isExcluded[recipient]) {
            _transferBothExcluded(sender, recipient, amount);
        } else {
            _transferStandard(sender, recipient, amount);
        }
        
        if(!takeFee)
            restoreAllFee();
    }
  
    function _transferStandard(address sender, address recipient, uint256 tAmount) internal {
        (uint256 tTransferAmount, uint256 tFee, uint256 tSwapFee) = getTValues(tAmount);
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 rSwapFee) = getRValues(tAmount, tFee, tSwapFee);

        {
            address from = sender;
            address to = recipient;
            _rOwned[from] = _rOwned[from].sub(rAmount);
            _rOwned[to] = _rOwned[to].add(rTransferAmount);
        }
        takeReflectionFee(rFee, tFee);
        takeSwapFee(sender, rSwapFee, tSwapFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }
    
