            _tOwned[to] = _tOwned[to].add(tTransferAmount);
            _rOwned[to] = _rOwned[to].add(rTransferAmount); 
        }      
        takeReflectionFee(rFee, tFee);
        takeSwapFee(sender, rSwapFee, tSwapFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }
    
