    }

    function getTValues(uint256 amount) internal view returns (uint256, uint256, uint256) {
        uint256 tAmount = amount;
        uint256 tFee = calculateTaxFee(tAmount);
        uint256 tSwapFee = calculateSwapFee(tAmount);
        uint256 tTransferAmount = tAmount.sub(tFee).sub(tSwapFee);
        return (tTransferAmount, tFee, tSwapFee);
    }

    function getRValues(uint256 amount, uint256 tFee, uint256 tSwapFee) internal view returns (uint256, uint256, uint256, uint256) {
        uint256 currentRate = getRate();
        uint256 tAmount = amount;
        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rFee = tFee.mul(currentRate);
        uint256 rSwapFee = tSwapFee.mul(currentRate);
        uint256 rTransferAmount = rAmount.sub(rFee).sub(rSwapFee);
        return (rAmount, rTransferAmount, rFee, rSwapFee);
    }

    function calculateTaxFee(uint256 _amount) internal view returns (uint256) {
        return _amount.mul(taxFee).div(
            10**2
        );
    }

    function calculateSwapFee(uint256 _amount) internal view returns (uint256) {
        return _amount.mul(totalSwapFee).div(
            10**2
        );
    }

    function takeReflectionFee(uint256 rFee, uint256 tFee) internal {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    function takeSwapFee(address sender, uint256 rSwapFee, uint256 tSwapFee) internal {
        _rOwned[address(this)] = _rOwned[address(this)].add(rSwapFee); 
        if(_isExcluded[address(this)]){
             _tOwned[address(this)] = _tOwned[address(this)].add(tSwapFee);
        }
        if(tSwapFee > 0) emit Transfer(sender, address(this), tSwapFee);
    }

    function removeAllFee() internal {
        if(taxFee == 0 && totalSwapFee == 0) return;
        
        previousTaxFee = taxFee;
        taxFee = 0;

        previousTotalSwapFee = totalSwapFee;
        totalSwapFee = 0;
    }
 
    function restoreAllFee() internal {
        taxFee = previousTaxFee;
        totalSwapFee = previousTotalSwapFee;
    }

    function distributeFee() internal {
        uint256 initialBalance = address(this).balance;
        uint256 contractTokenBalance = balanceOf(address(this));

        if(contractTokenBalance >= minimumTokensBeforeSwap) {
            bool initialFeeState = enableFee;
            bool initialSellLimitState = enableMaxSellLimit;
            // remove fee if initialFeeState was true
            if(initialFeeState) enableFee = false;
            // remove sell limit if initialSellLimitState was true
            if(initialSellLimitState) enableMaxSellLimit = false;

            swapFeeToEth = true;

            // swap tokens in contract address to eth
            swapTokensForEth(contractTokenBalance, address(this));
            uint256 transferredBalance = address(this).balance.sub(initialBalance);

            // Send bnb to Marketing address
            if(marketingswapEnabled) transferBNBToAddress(payable(marketingWallet), transferredBalance.mul(marketingFee).div(totalSwapFee));

            // Send bnb to Charity address
            if(charityswapEnabled) transferBNBToAddress(payable(charityWallet), transferredBalance.mul(charityFee).div(totalSwapFee));

            //send bnb to staffwallet address
            if(staffWageswapEnabled) transferBNBToAddress(payable(staffWageWallet), transferredBalance.mul(staffWagefee).div(totalSwapFee));

            //send bnb to women address
            if(womenswapEnabled) transferBNBToAddress(payable(womenWallet), transferredBalance.mul(womenfee).div(totalSwapFee)); 

            //send bnb to exchangelist address
            if(exchangeswapEnabled) transferBNBToAddress(payable(exchangeListWallet), transferredBalance.mul(exchangefee).div(totalSwapFee)); 

            // Buy back tokens using eth
            if(buyBackswapEnabled) transferBNBToAddress(payable(buyBackWallet), transferredBalance.mul(buyBackFee).div(totalSwapFee));

            // enable fee if initialFeeState was true
            if(initialFeeState) enableFee = true;
            // enable sell limit if initialSellLimitState was true
            if(initialSellLimitState) enableMaxSellLimit = true;

            swapFeeToEth = false;
        }
    }

    function transferBNBToAddress(address payable recipient, uint256 amount) internal {
        recipient.transfer(amount);
    }

    /**
     * @dev swap tokens to eth and added that to respective charity or marketing wallet.
     * @param tokenAmount Number of tokens to be sent
     * @param account charity or marketing wallet
     */

    function swapTokensForEth(uint256 tokenAmount, address account) internal {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of BNB
            path,
            account,
            block.timestamp
        );
    }

   
    function tokenFromReflection(uint256 rAmount) internal view returns(uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total reflections");
        uint256 currentRate =  getRate();
        return rAmount.div(currentRate);
    }

    function getRate() internal view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function getCurrentSupply() internal view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;      
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }
}
