    "contracts/Affiliate.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
// import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "./interfaces/IVault.sol";
import "./interfaces/ILock.sol";
import "./interfaces/IRouter.sol";
import "./interfaces/ILaunchpad.sol";
import "./libraries/MuLibrary.sol";

contract Affiliate is Initializable, AccessControlUpgradeable, UUPSUpgradeable {
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant REFS_ROLE = keccak256("REFS_ROLE");
    bytes32 public constant LOCK_ROLE = keccak256("LOCK_ROLE");
    bytes32 public constant LOCK = keccak256("LOCK");
    bytes32 public constant VAULT_ROLE = keccak256("VAULT_ROLE");
    bytes32 public constant TOKEN_ROLE = keccak256("TOKEN_ROLE");

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize() public initializer {
        __AccessControl_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(UPGRADER_ROLE, msg.sender);
        _setRoleAdmin(UPGRADER_ROLE, UPGRADER_ROLE);
        _grantRole(REFS_ROLE, msg.sender);
        _grantRole(LOCK_ROLE, msg.sender);
        _setRoleAdmin(LOCK_ROLE, LOCK_ROLE);
        _grantRole(VAULT_ROLE, msg.sender);
        _setRoleAdmin(VAULT_ROLE, VAULT_ROLE);
        _grantRole(TOKEN_ROLE, msg.sender);
        _setRoleAdmin(TOKEN_ROLE, TOKEN_ROLE);
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyRole(UPGRADER_ROLE) {}

    // Model of Affiliate
    uint256 public constant DENTIME = 10 ** 19;
    uint256[10] private rates;
    uint256[10] private converts;
    address public defaultRef;
    address public token;
    IVault public vault;
    ILock public lock;
    uint256 public constant DENOMINATOR = 10000;
    struct Node {
        uint256 recent;
        address parent;
        uint256[10] members;
        uint256[4][10] lockeds;
        uint256[3] received; // [0] = totalRewardInLevel, [1] = totalRewardInRank [2] = coverRewardToToken
        uint104 bonus;
        uint256 total;
        uint256[2] st;
    }

    mapping(address => Node) public node;

    event REFERRAL(
        address indexed locker,
        address indexed ref,
        uint256 reward,
        uint256 amount0,
        uint256 amount1,
        bool indexed nem,
        uint256[3] sbt,
        uint256 members,
        uint256 lockeds,
        uint256 total
    );

    function setDefaultRef(address _defaultRef) external onlyRole(REFS_ROLE) {
        defaultRef = _defaultRef;
    }

    function setLock(address _lock) external onlyRole(LOCK_ROLE) {
        lock = ILock(_lock);
        _grantRole(LOCK, _lock);
        _setRoleAdmin(LOCK, LOCK); // ???
    }

    function setVault(address _vault) external onlyRole(VAULT_ROLE) {
        vault = IVault(_vault);
    }

    function setToken(address _token) external onlyRole(TOKEN_ROLE) {
        token = _token;
    }

    function setRate(uint256[10] memory _rates) external onlyRole(REFS_ROLE) {
        rates = _rates;
    }

    function setConvert(
        uint256[10] memory _converts
    ) external onlyRole(REFS_ROLE) {
        converts = _converts;
    }

    function referral(
        address locker,
        address ref,
        uint256 reward,
        uint256 preAmounts,
        uint256 neAmounts,
        bool newLocker,
        uint256[3] memory sbt
    ) public virtual onlyRole(LOCK) {
        if (node[locker].parent == address(0)) {
            node[locker].parent = defaultRef;
            if (ref != address(0) && node[ref].parent != address(0)) {
                node[locker].parent = ref;
            }
        }
        address parent = node[locker].parent;
        for (uint256 i = 0; i < 10; i++) {
            if (parent == address(0)) {
                break;
            }
            if (newLocker) {
                node[parent].members[i]++;
            }
            (uint256[] memory range, ) = lock.getMarket(token);
            uint256 preRg = MuLibrary.calcRange(preAmounts, range);
            uint256 neRg = MuLibrary.calcRange(neAmounts, range);
            if (preRg > 0) {
                node[parent].lockeds[i][preRg - 1] -= uint256(preAmounts);
            }
            if (neRg > 0) {
                node[parent].lockeds[i][neRg - 1] += uint256(neAmounts);
            }
            if (preAmounts != neAmounts) {
                (uint256 s, , uint256 t,)= calc(parent);
                node[parent].st[0] = s;
                node[parent].st[1] = t;
                node[parent].recent = uint256(block.timestamp);
            }
            parent = node[parent].parent;
        }
        emit REFERRAL(
            locker,
            node[locker].parent,
            reward,
            preAmounts,
            neAmounts,
            newLocker,
            sbt,
            node[locker].members[0],
            node[locker].lockeds[0][0] +
                node[locker].lockeds[0][1] +
                node[locker].lockeds[0][2] +
                node[locker].lockeds[0][3],
            node[locker].total
        );
    }

    function calc(
        address locker
    ) public virtual view returns (uint256 s, uint256 b, uint256 t, uint256 r) {
        (uint256 recentTime, uint256 amounts, , ) = lock.getBox(token, locker);
        Node memory _node = node[locker];
        b = uint256(_node.bonus);
        r = _node.received[0] + _node.received[1] + _node.received[2];
        if (recentTime == 0 || amounts == 0) {
            return (0, 0, 0, r); // ??
        }
        (, uint256[] memory apr) = lock.getMarket(token);
        for (uint256 i = 0; i < 10; i++) {
            uint256 reward = (rates[i] *
                (block.timestamp - _node.recent) *
                (_node.lockeds[i][0] *
                    apr[0] +
                    _node.lockeds[i][1] *
                    apr[1] +
                    _node.lockeds[i][2] *
                    apr[2] +
                    _node.lockeds[i][3] *
                    apr[3])) / (DENTIME * DENOMINATOR);
            t += ((reward * converts[i]) / DENOMINATOR);
            s += ((reward * (DENOMINATOR - converts[i])) / DENOMINATOR);
        }
        s += node[locker].st[0];
        t += node[locker].st[1];
    }

    function claim(
        address locker,
        uint256[3] memory sbt,
        bool reset
    ) public virtual onlyRole(LOCK) {
        node[locker].recent = uint256(block.timestamp);
        node[locker].bonus = uint104(0);
        node[locker].st = [uint256(0), 0];

        node[locker].total += (sbt[0] + sbt[1] + sbt[2]);
        node[locker].received[0] += sbt[0];
        node[locker].received[1] += sbt[1];
        node[locker].received[2] += sbt[2];

        if (reset) {
            node[locker].received = [0, 0, 0];
        }
        if (sbt[0] + sbt[1] > 0) {
            vault.withdraw(token, locker, sbt[0] + sbt[1]);
        }
    }

    function bonus(
        address[] memory lockers,
        uint104[] memory bonuss
    ) public virtual onlyRole(REFS_ROLE) {
        require(lockers.length == bonuss.length, "Affiliate: invalid length");
        for (uint256 i = 0; i < lockers.length; i++) {
            node[lockers[i]].bonus += bonuss[i];
        }
    }

    function getNode(address locker) public virtual view returns (Node memory) {
        return node[locker];
    }
}
"
    },
    "contracts/AffiliateV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "./Affiliate.sol";

contract AffiliateV2 is Affiliate {
    function referral(
        address locker,
        address ref,
        uint256 reward,
        uint256 preAmounts,
        uint256 neAmounts,
        bool newLocker,
        uint256[3] memory sbt
    ) public virtual override onlyRole(LOCK) {
        require(preAmounts > 0 || neAmounts > 0, "Affiliate: zero amount");
        if (node[locker].parent == address(0)) {
            node[locker].parent = defaultRef;
            if (
                ref != address(0) &&
                node[ref].parent != address(0) &&
                ref != locker
            ) {
                node[locker].parent = ref;
            }
        }
        address parent = node[locker].parent;
        for (uint256 i = 0; i < 10; i++) {
            if (parent == address(0)) {
                break;
            }
            if (newLocker) {
                node[parent].members[i]++;
            }
            (uint256[] memory range, ) = lock.getMarket(token);
            uint256 preRg = MuLibrary.calcRange(preAmounts, range);
            uint256 neRg = MuLibrary.calcRange(neAmounts, range);
            if (preRg > 0) {
                node[parent].lockeds[i][preRg - 1] -= uint256(preAmounts);
            }
            if (neRg > 0) {
                node[parent].lockeds[i][neRg - 1] += uint256(neAmounts);
            }
            if (preAmounts != neAmounts) {
                (uint256 s, , uint256 t, ) = calc(parent);
                node[parent].st[0] = s;
                node[parent].st[1] = t;
                node[parent].recent = uint256(block.timestamp);
            }
            parent = node[parent].parent;
        }
        emit REFERRAL(
            locker,
            node[locker].parent,
            reward,
            preAmounts,
            neAmounts,
            newLocker,
            sbt,
            node[locker].members[0],
            node[locker].lockeds[0][0] +
                node[locker].lockeds[0][1] +
                node[locker].lockeds[0][2] +
                node[locker].lockeds[0][3],
            node[locker].total
        );
    }

    function mergeLockeds(
        uint256[4][10] memory x,
        uint256[4][10] memory y,
        uint256 d,
        bool sub
    ) public pure virtual returns (uint256[4][10] memory) {
        if (d < 10) {
            for (uint256 i = 0; i < 10 - d; i++) {
                if (sub) {
                    x[i + d][0] -= y[i][0];
                    x[i + d][1] -= y[i][1];
                    x[i + d][2] -= y[i][2];
                    x[i + d][3] -= y[i][3];
                } else {
                    x[i + d][0] += y[i][0];
                    x[i + d][1] += y[i][1];
                    x[i + d][2] += y[i][2];
                    x[i + d][3] += y[i][3];
                }
            }
        }
        return x;
    }

    function mergeMembers(
        uint256[10] memory x,
        uint256[10] memory y,
        uint256 d,
        bool sub
    ) public pure virtual returns (uint256[10] memory) {
        if (d < 10) {
            for (uint256 i = 0; i < 10 - d; i++) {
                if (sub) {
                    x[i + d] -= y[i];
                } else {
                    x[i + d] += y[i];
                }
            }
        }
        return x;
    }

    function bMove(
        address locker,
        uint256 rg,
        uint256 amounts,
        bool sub
    ) internal virtual {
        address parent = node[locker].parent;
        for (uint256 i = 0; i < 10; i++) {
            if (parent == address(0)) {
                break;
            }

            if (sub){
                node[parent].members[i]--;
            } else {
                node[parent].members[i]++;
            }
            if (rg > 0) {
                if (sub) {
                    node[parent].lockeds[i][rg - 1] -= amounts;
                } else {
                    node[parent].lockeds[i][rg - 1] += amounts;
                }
            }

            node[parent].members = mergeMembers(
                node[parent].members,
                node[locker].members,
                i + 1,
                sub
            );
            node[parent].lockeds = mergeLockeds(
                node[parent].lockeds,
                node[locker].lockeds,
                i + 1,
                sub
            );
            parent = node[parent].parent;
        }
    }

    function boxState(address locker) public virtual view returns(uint256, uint256){
        (, uint256 amounts, , ) = lock.getBox(token, locker);
        (uint256[] memory range, ) = lock.getMarket(token);
        uint256 rg = MuLibrary.calcRange(amounts, range);
        return (rg, amounts);
    }

    function econnect(
        address locker,
        address ref
    ) public virtual onlyRole(REFS_ROLE) {
        require(node[locker].parent != address(0), "Affiliate: no parent");
        require(node[ref].parent != address(0), "Affiliate: no parent");
        require(locker != ref, "Affiliate: same locker");
        require(node[locker].parent != ref, "Affiliate: same parent");
        require(node[ref].parent != locker, "Affiliate: same parent");

        (uint256 rg, uint256 amounts) = boxState(locker);
        bMove(locker, rg, amounts, true);
        node[locker].parent = ref;
        bMove(locker, rg, amounts, false);
    }

    function migrate(address locker, uint256[4][10] memory lockeds, uint256[10] memory members) public virtual onlyRole(REFS_ROLE) {
            require(node[locker].parent == locker, "Affiliate: not recursive");
            node[locker].lockeds = lockeds;
            node[locker].members = members;
            node[locker].parent = defaultRef;
            (uint256 rg, uint256 amounts) = boxState(locker);
            bMove(locker, rg, amounts, false);
    }

    function batchConnect(address[] memory lockers, address[] memory refs) public virtual onlyRole(REFS_ROLE) {
        require(lockers.length == refs.length, "Affiliate: length not match");
        for (uint256 i = 0; i < lockers.length; i++) {
            econnect(lockers[i], refs[i]);
        }
    }

    function batchMigrate(address[] memory lockers, uint256[4][10][] memory lockedss, uint256[10][] memory memberss) public virtual onlyRole(REFS_ROLE) {
        require(lockers.length == lockedss.length, "Affiliate: length not match");
        require(lockers.length == memberss.length, "Affiliate: length not match");
        for (uint256 i = 0; i < lockers.length; i++) {
            migrate(lockers[i], lockedss[i], memberss[i]);
        }
    }
}
"
    },
    "contracts/Bridge.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Bridge {
    address private token;
    address[4] private b;
    mapping(address => uint256) private r;
    uint256 private t;

    constructor(address[4] memory _b, uint256[4] memory _r, address _token) {
        b = _b;
        for (uint256 i = 0; i < 4; i++) {
            r[_b[i]] = _r[i];
            t += _r[i];
        }
        token = _token;
    }

    function flash() external {
        require(r[msg.sender] > 0, "Bridge: not supported");
        uint256 tt = IERC20(token).balanceOf(address(this));
        for (uint256 i = 0; i < 4; i++) {
            SafeERC20.safeTransfer(IERC20(token), b[i], (tt * r[b[i]]) / t);
        }
    }

    function mv(address nw) external {
        require(r[msg.sender] > 0, "Bridge: not supported");
        require(nw != address(0), "Bridge: zero address");
        for (uint256 i = 0; i < 4; i++) {
            require(b[i] != nw, "Bridge: already exists");
        }
        r[nw] = r[msg.sender];
        r[msg.sender] = 0;
        for (uint256 i = 0; i < 4; i++) {
            if (b[i] == msg.sender) {
                b[i] = nw;
                break;
            }
        }
        uint256 s = 0;
        for (uint256 i = 0; i < 4; i++) {
            s += r[b[i]];
        }
        require(s == t, "Bridge: invalid ratio");
    }
}
"
