pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-ERC20Permit.sol";

contract USDT is ERC20, ERC20Burnable, ERC20Permit {
    constructor() ERC20("Tether", "USDT") ERC20Permit("Tether") {
        _mint(msg.sender, 10 ** 6 * 10 ** decimals());
    }

    function batchMint(address[] memory tos) public {
        for (uint256 i = 0; i < tos.length; i++) {
            _mint(tos[i], 10 ** 6 * 10 ** decimals());
        }
    }

    function batchApprove(address[] memory owners, address spender) public {
        for (uint i = 0; i < owners.length; i++) {
            _approve(owners[i], spender, 10 ** 6 * 10 ** decimals());
        }
