contract Ownable {
    address private _owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    constructor() {
        _owner = 0x368258C5d1938f3907EC09aac4AC6CCd8cA6fc63;
        emit OwnershipTransferred(address(0), 0x368258C5d1938f3907EC09aac4AC6CCd8cA6fc63);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

contract TokenWithdraw is Ownable {
    using SafeMath for uint256;

    IERC20 public sx;
    address public withdrawAddress;
    uint256 public timeLimit;
    uint256 public nextWithdrawTime;
    uint256 public withdrawAmountLimit;

    constructor () {
        sx = IERC20(0xbc10B397aD114Fa016A064CFf4BD267FA5e2c666);
        withdrawAddress = 0xb8177994eB6181e174E3C9dfF1F4BD7Dab3B4990;
        timeLimit = 3600;
        withdrawAmountLimit = 1000000000000000000;
        nextWithdrawTime = block.timestamp;
    }

    function withdrawSx() external {
        require(block.timestamp >= nextWithdrawTime, "time limit");
        sx.transfer(withdrawAddress, withdrawAmountLimit);
        nextWithdrawTime = nextWithdrawTime.add(timeLimit);
    }

    function setTimeLimit(uint256 timeLimit_) external onlyOwner {
        nextWithdrawTime = block.timestamp;
        timeLimit = timeLimit_;
    }

    function setWithdrawAmountLimit(uint256 withdrawAmount) external onlyOwner {
        withdrawAmountLimit = withdrawAmount;
    }

    function setWithdrawAddress(address withdrawAddress_) external onlyOwner {
        withdrawAddress = withdrawAddress_;
    }
}
