interface ISwapFactory {
    function getPair(address token0,address token1) external view returns(address);
}

interface ILpProxy {
    function lastTime() external view returns(uint256);
    function notifyRemoveLiquidityAndReward() external;
}

interface IAppleData {
    function feeTo() external view returns(address);
    function receiver() external view returns(address);
    function usdt() external view returns(address);
    function wbnb() external view returns(address);
    function pancakeFactory() external view returns(address);
    function appleFactory() external view returns(address);
    function lpProxy() external view returns(address);
    function isExcludedFromFee(address from, address to) external view returns (bool);
    function isOtherSwapPair(address pair) external view returns (bool);
    function isNotifyLp() external view returns (bool);
    function isOnAllowContract() external view returns (bool);
    function isAllowContract(address from, address to) external view returns (bool);
}


contract AppleERC20 is Context, Ownable, IERC20 {
    using SafeMath for uint256;

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint8 private _decimals;
    bool  private swapping;
    address private burnAddress = address(0x000000000000000000000000000000000000dEaD);

    IAppleData public appleData;

    constructor (address appleData_) public {
        _name = "APPLE";
        _symbol = "APPLE";
        _decimals = 18;
        
        appleData = IAppleData(appleData_);

        _mint(appleData.receiver(), 10000000 * 10**18);
    }
    
    
    function name() public view virtual returns (string memory) {
        return _name;
    }

    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }
    
    function _transfer(address from, address to, uint256 amount) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(amount > 0, "ERC20: transfer amount must be greater than zero");
        
        if ((_isContract(from) || _isContract(to)) && appleData.isOnAllowContract()) {
            require(appleData.isAllowContract(from, to), "ERC20: invalid contract address");
        } 

        _balances[from] = _balances[from].sub(amount, "ERC20: transfer amount exceeds balance");

        _notifyRemoveLiquidityAndReward(from);
        
        bool isBuy = isSwapPair(from);
        bool isSell = isSwapPair(to);
        if(to != address(0) && to != burnAddress && !appleData.isExcludedFromFee(from, to) && (isBuy || isSell)){
            uint256 feeAmount = amount / 100;
            amount -= feeAmount;
            _takeFee(from, appleData.feeTo(), feeAmount);
        }

        _balances[to] = _balances[to].add(amount);
        emit Transfer(from, to, amount);
    }
    
    function _notifyRemoveLiquidityAndReward(address from) private {
        uint256 nowTime = block.timestamp;
        ILpProxy lpProxy = ILpProxy(appleData.lpProxy());
        if (appleData.isNotifyLp() && nowTime >= lpProxy.lastTime() && !swapping && !isSwapPair(from)) {
            swapping = true;
            lpProxy.notifyRemoveLiquidityAndReward();
            swapping = false;
        } 
    }

    function _takeFee(address from, address to, uint256 feeAmount) internal {
        _balances[to] = _balances[to].add(feeAmount);
        emit Transfer(from, to, feeAmount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    function isSwapPair(address pair) public view returns(bool){
        if(pair == address(0)){
            return false;
        }

        address usdt = appleData.usdt();
        address wbnb = appleData.wbnb();
        ISwapFactory pancakeSwapFactory = ISwapFactory(appleData.pancakeFactory());
        ISwapFactory appleSwapFactory = ISwapFactory(appleData.appleFactory());

        return appleSwapFactory.getPair(address(this), usdt) == pair || appleSwapFactory.getPair(address(this), wbnb) == pair || 
        pancakeSwapFactory.getPair(address(this), usdt) == pair || pancakeSwapFactory.getPair(address(this), wbnb) == pair || 
        appleData.isOtherSwapPair(pair);
    }

    function setAppleData(address appleData_) public onlyOwner {
        appleData = IAppleData(appleData_);
    }

}
