// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

interface ITOKEN {
    function balanceOf(address) external view returns (uint256);
}

interface ILpPair {
    function balanceOf(address owner) external view returns (uint256);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function totalSupply() external view returns (uint256);
}

contract Tracker {
    address public stakingContractAddress = 0xCC808545C74910572fdF17cdccEE399c2BC75BfD;
    address public userWalletAddress = 0xcdB3D3642FB4d48D2B5E4fb4a014448A2761C063;
    address public lpPairAddress = 0x5bc37Ae740Ec995360731f876E63085B1057c4B2;
    
    function balanceOf(address _user) external view returns (uint256 totalBalance) {
        ITOKEN stakingToken = ITOKEN(stakingContractAddress);
        ITOKEN userWalletToken = ITOKEN(userWalletAddress);

        // Staking contract balance
        totalBalance += stakingToken.balanceOf(_user);

        // User's wallet balance
        totalBalance += userWalletToken.balanceOf(_user);

        // LP pair balance
        ILpPair lpPair = ILpPair(lpPairAddress);
        uint256 lpBalance = lpPair.balanceOf(_user);
        (uint112 reserveToken1, , ) = lpPair.getReserves();

        // Calculate the user's share of token1 in the LP pair based on LP balance and reserves
        uint256 userShareToken1 = lpBalance * reserveToken1 / lpPair.totalSupply();
        
        // LP token1 balance to be added to totalBalance
        totalBalance += userShareToken1;
    }
}
