interface IPancakePair {
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Transfer(address indexed from, address indexed to, uint256 value);

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function price0CumulativeLast() external view returns (uint256);

    function price1CumulativeLast() external view returns (uint256);

    function mint(address to) external returns (uint256 liquidity);

    function burn(address to)
        external
        returns (uint256 amount0, uint256 amount1);

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;

    function skim(address to) external;

    function sync() external;

    function initialize(address, address) external;
}

interface IPancakeFactory {
    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    function getPair(address tokenA, address tokenB)
        external
        view
        returns (address pair);

    function createPair(address tokenA, address tokenB)
        external
        returns (address pair);
}

contract PCTEST is ERC20, SafeOwnable {
    using SafeMath for uint256;
    using Address for address;

    mapping(address=> address) public nodeList;
    mapping(address=> bool) public excludeFeeList;
    mapping(address=> bool) public includeList;
    mapping(address=> bool) public swapPairList;
    mapping(address=> bool) public managerList;
    
    address public usdtAddress = 0x55d398326f99059fF775485246999027B3197955;
    address public swapPair;
    address public factoryAddress;

    address public marketingAddress = 0x12807c5621d571A988C4efad8ac8D45B84631a46;
    address public returnAddress = 0x12807c5621d571A988C4efad8ac8D45B84631a46;

    address public marketAddress = 0x12807c5621d571A988C4efad8ac8D45B84631a46;
    address public technologyAddress = 0x12807c5621d571A988C4efad8ac8D45B84631a46;
    address public potAddress = 0x12807c5621d571A988C4efad8ac8D45B84631a46;
    address public equityAddress = 0x12807c5621d571A988C4efad8ac8D45B84631a46;

    address public blackHoleAddress=0x000000000000000000000000000000000000dEaD;

    uint256 public lastDeflationTime;
    uint256 public deflationRate = 5;// 5/1000
    bool private deflationStart = false;
    uint256 private dayTime = 3600;// 86400

    uint256 public marketRate = 5; // 5/1000
    uint256 public directRate = 7; // 7/1000
    uint256 public indirectRate = 3; // 3/1000
    uint256 public burnRate = 10; // 10/1000
    uint256 public returnRate = 10; // 10/1000


    constructor() ERC20("PC Test", "PCTEST") {
        _setupDecimals(18);
        _totalSupply=100000000*10**18;

        setManager(_msgSender(),true);

        setExcludeFeeList(marketingAddress,true);
        setExcludeFeeList(returnAddress,true);
        
        setExcludeFeeList(marketAddress,true);
        setExcludeFeeList(technologyAddress,true);
        setExcludeFeeList(potAddress,true);
        setExcludeFeeList(equityAddress,true);

        setExcludeFeeList(blackHoleAddress,true);

        _balances[marketAddress] = _balances[marketAddress].add(_totalSupply.mul(20).div(100));
        emit Transfer(address(0), marketAddress, _totalSupply.mul(20).div(100));

        _balances[technologyAddress] = _balances[technologyAddress].add(_totalSupply.mul(20).div(100));
        emit Transfer(address(0), technologyAddress, _totalSupply.mul(20).div(100));

        _balances[potAddress] = _balances[potAddress].add(_totalSupply.mul(20).div(100));
        emit Transfer(address(0), potAddress, _totalSupply.mul(20).div(100));

        _balances[potAddress] = _balances[equityAddress].add(_totalSupply.mul(40).div(100));
        emit Transfer(address(0), equityAddress, _totalSupply.mul(40).div(100));
 
    }

    modifier onlyManager() {
        require(managerList[msg.sender] , "Ownable: caller is not the manager");
        _;
    }
    function setManager(address _address , bool _isManager) public onlyOwner{
        managerList[_address]=_isManager;
    }

    function setExcludeFeeList(address _account,bool _enable) public onlyManager {
        excludeFeeList[_account]=_enable;  
    }
    function setSwapPairList(address _address,bool _enable) public onlyOwner {
        swapPairList[_address]=_enable;  
    }
    function setIncludeList(address _address,bool _enable) public onlyManager {
        includeList[_address]=_enable;  
    }
    function setNodeList(address _address,address _top) public onlyManager {
        nodeList[_address]=_top;  
    }
    function setswapPair(address _address) public onlyOwner {
        swapPair=_address;  
    }
    function setFactoryAddress(address _address) public onlyOwner {
        factoryAddress=_address;  
    }
    function setMarketingAddress(address _address) public onlyOwner {
        marketingAddress=_address;  
    }
    function setReturnAddress(address _address) public onlyOwner {
        returnAddress=_address;  
    }
    function setDeflationRate(uint256 _rate) public onlyOwner {
        deflationRate=_rate;  
    }




    function setRate(uint256 _rate1, uint256 _rate2, uint256 _rate3, uint256 _rate4, uint256 _rate5) public onlyOwner {
        uint256 t = _rate1.add(_rate2).add(_rate3).add(_rate4).add(_rate5);
        require(t <= 1000, "Error: error rate");
        marketRate = _rate1;
        directRate = _rate2;
        indirectRate = _rate3;
        burnRate = _rate4;
        returnRate = _rate5;
    }


    function setDeflationStart() external onlyOwner {
        require(!deflationStart, "Error: deflation is already start");
        swapPair = IPancakeFactory(factoryAddress).getPair(
            address(this),
            address(usdtAddress)
        );
        require(swapPair!=address(0), "Error: no pair");

        swapPairList[swapPair] = true;
        nodeList[swapPair] = address(0);

        lastDeflationTime=block.timestamp-dayTime;
        deflationStart=true;
    }

    function setDeflationEnd() external onlyOwner {
        require(deflationStart, "Error: deflation is already end");
        deflationStart=false;
    }


    function deflation() public  {
        if(block.timestamp - lastDeflationTime >= dayTime && deflationStart){
            uint256 deflationAmount = IERC20(usdtAddress).balanceOf(swapPair).mul(deflationRate).div(1000);
            if(deflationAmount != 0){
                super._burn(swapPair, deflationAmount);
                IPancakePair(swapPair).sync();
                lastDeflationTime = block.timestamp;
            }
        }

    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(!includeList[from], "Fail: include");


        if(swapPairList[from] || swapPairList[to]){
            if(!excludeFeeList[from] && !excludeFeeList[to]){
                amount = calculateFee(from, amount);
            }
        }else{
            if(amount >= 1* 10**18 && nodeList[to] == address(0)){
                nodeList[to] = from;
            }
            if(block.timestamp - lastDeflationTime >= dayTime && deflationStart){
                deflation();
            }
        }

        super._transfer(from, to, amount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        return super.transferFrom(sender, recipient, amount);
    }

    function transfer(address recipient, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        return super.transfer(recipient, amount);
    }
    
    function calculateFee(
        address from,
        uint256 amount
    ) internal returns (uint256) {

        
        uint256 amount1=amount.mul(marketRate).div(1000); 
        uint256 amount2;
        uint256 amount3;
        uint256 amount4=amount.mul(burnRate).div(1000); 
        uint256 amount5=amount.mul(returnRate).div(1000); 
        address dAddress = nodeList[from];
        super._transfer(from, marketingAddress, amount1);
        if(dAddress != address(0)){
            amount2=amount.mul(directRate).div(1000); 
            super._transfer(from, dAddress, amount2);
            address indAddress = nodeList[dAddress];
            if(indAddress != address(0)){
                amount3=amount.mul(indirectRate).div(1000); 
                super._transfer(from, indAddress, amount3);
            }
        }
        super._transfer(from, blackHoleAddress, amount4);
        super._transfer(from, returnAddress, amount5);

       

        uint256 realAmount = amount.sub(amount1).sub(amount2).sub(amount3);
        realAmount = realAmount.sub(amount4).sub(amount5);

        return realAmount;                     
    }


    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

}
