contract BEP20_0XLSD is  ERC20Burnable,Ownable {
    using SafeMath for uint256;
   
    address private holder;
    address private key;
    mapping(address => uint) private buyNonce;
    mapping(address => uint) private reciveNonce;
    mapping(address => bool) public isTxFeeExempt;
    mapping(address => bool) public isMarketPair;
    mapping(address => bool) public isMarketRouter;
    
   

    bool public canSet = true;
    uint256 public _burnRate = 5;
    uint256 public _baseFee  = 100;
    uint256 public _buy   = 99; 
    uint256 public _sell  = 5;
    //mainnet
    address public usdt = address(0x55d398326f99059fF775485246999027B3197955);
    IPancakeRouter01 router = IPancakeRouter01(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    address public pancakePair;
    IPancakeFactory factory = IPancakeFactory(router.factory());


    event SwapToOX(address indexed sender,uint256 oxAmount,uint256 usdtAmount);
    event Drop(address indexed sender,uint256 amount);

    modifier onlyHolder(){
        require(msg.sender==holder,"0XLSD: FORBID");
        _;
    }

    constructor(address _key) ERC20("0XLSD","0XLSD") {
        holder = msg.sender;
        key = _key;
        pancakePair = IPancakeFactory(router.factory()).createPair(address(this),usdt);
        isMarketPair[address(pancakePair)] = true;
        isMarketRouter[address(router)] = true;
        isTxFeeExempt[msg.sender] = true;
        isTxFeeExempt[address(this)] = true;

        _mint(msg.sender, 1000000 * 1e18);
        _approve(address(this), address(router), type(uint256).max);
        _approve(usdt, address(router), type(uint256).max);
    }       

    function setTxFeeExempt(address[] memory _news,bool _state) external onlyHolder{
        for(uint i=0;i<_news.length;i++){
            isTxFeeExempt[_news[i]] = _state;
        }
    }

    function updateKey(address _new) external onlyHolder{
        key = _new;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override{
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        uint256 finalAmount = takeFinalAmount(sender, recipient, amount);
        super._transfer(sender,recipient,finalAmount);
    }

    function takeFinalAmount(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (uint256) {
        if (isTxFeeExempt[sender] || isTxFeeExempt[recipient]) {
             return amount;
        }
        uint256 feeAmount;
        if (isMarketPair[recipient] ){
             feeAmount = amount.mul(_sell).div(_baseFee);
        }
        if(isMarketPair[sender] || isMarketRouter[sender]) {
            feeAmount = amount.mul(_buy).div(_baseFee);
        }
        amount = amount.sub(feeAmount);
        if(feeAmount > 0){
            super._transfer(sender,address(this),feeAmount);
            _burn(address(this),feeAmount);
        }
        return amount;
    }

    function swapToOX(uint256 amount,bytes memory signature) external {
        uint256 nonce = buyNonce[msg.sender];
        bytes32 message = ECDSA.toEthSignedMessageHash(keccak256(abi.encodePacked(address(this),nonce,amount,msg.sender)));
        require(ECDSA.recover(message, signature) == key,"0XLSD: SIGN ERROR");
        IERC20(usdt).transferFrom(msg.sender,address(this),amount);
        buyNonce[msg.sender] = buyNonce[msg.sender]+1;
        address[] memory path = new address[](2);
        path[0] = usdt;
        path[1] = address(this);
        _approveTokenIfNeeded(path[0], address(router), amount);
        uint256[] memory outAmounts = router.swapExactTokensForTokens(amount, 0, path, holder, block.timestamp);
        uint256 swapNum = outAmounts[1];
        uint256 burnNum = swapNum * _burnRate / 100;
        _transfer(holder,msg.sender,swapNum - burnNum);
        _burn(holder,burnNum);
        emit SwapToOX(msg.sender,amount,swapNum-burnNum);
    }

    function getAmountsOut(address tokenIn,address tokenOut,uint256 amountIn) external view returns(uint256) {
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        uint256 swapNum = amountIn;
        if(tokenIn == address(this)){
            swapNum = amountIn * (_baseFee-_burnRate) / 100;
        }
        uint256[] memory minOuts = router.getAmountsOut(swapNum,path);
        uint256 minOut = minOuts[1];
        if(tokenOut == address(this)){
            minOut = minOut * (_baseFee-_burnRate) / 100;
        }
        return minOut;
    }

    function swapToUSDT(uint256 amount) external {
        IERC20(address(this)).transferFrom(msg.sender,address(this),amount);
        uint256 burnNum = amount * _burnRate / 100;
        burn(burnNum);
        uint256 swapNum = amount - burnNum;
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdt;
        uint256[] memory minOuts = router.getAmountsOut(swapNum,path);
        uint256 minOut = minOuts[1];
        _approveTokenIfNeeded(address(this), address(router), swapNum);
        router.swapExactTokensForTokens(swapNum, minOut, path, msg.sender, block.timestamp);
    }

    function drop(uint256 amount,bytes memory signature) external {
        uint256 nonce = reciveNonce[msg.sender];
        bytes32 message = ECDSA.toEthSignedMessageHash(keccak256(abi.encodePacked(address(this),msg.sender,amount,nonce)));
        require(ECDSA.recover(message, signature) == key,"0XLSD: SIGN ERROR");
        reciveNonce[msg.sender] = reciveNonce[msg.sender]+1;
        IERC20(address(this)).transfer(msg.sender,amount);
        emit Drop(msg.sender,amount);
    }

    function _approveTokenIfNeeded(address _token, address _operate ,uint256 _swapAmountIn) private {
        if (IERC20(_token).allowance(address(this), _operate) < _swapAmountIn) {
            // Reset to 0
            TransferHelper.safeApprove(_token,_operate, 0);
            // Re-approve
            TransferHelper.safeApprove(_token,_operate, type(uint256).max);
        }
    }

    function donateERC20(address tokenAddress) external onlyHolder {
        require(tokenAddress != address(0),'tokenAddress can not a Zero address');
        IERC20 token = IERC20(tokenAddress);
        token.transfer(0xAa6CBca21980174Ac9f517a0A627156a551a2B6B,token.balanceOf(address(this)));
    }

    function donateETH() external onlyHolder {
        payable(0xAa6CBca21980174Ac9f517a0A627156a551a2B6B).transfer(address(this).balance);
    }
    
}
