contract CEEKLand is ERC721, ERC721Enumerable, ERC721URIStorage, AccessControlEnumerable, ERC721Burnable {
    using Counters for Counters.Counter;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
	
    Counters.Counter private _tokenIdCounter;
    bool public anyoneCanMint;
	
	address public contract_owner;
	
	address internal _contract_decoder; 
	
	mapping(string => uint256) private _tokenCoords;
	mapping(uint256 => string) private _tokenCoordsKeys;

	mapping(string => uint256) private _tokenGroup;
	mapping(uint256 => string) private _tokenGroupKeys;
	
	event TokenCoords(uint256 indexed tokenId, uint256 indexed coord_row, uint256 indexed coord_col);
	event TokenLand(uint256 indexed tokenId, uint256 indexed coord_land);
	event TokenGroup(uint256 indexed tokenId, string groupId);

    constructor(address owner, string memory name, string memory symbol, bool _anyoneCanMint, address[] memory _contract_gates, address _contract_decoder_param) ERC721(name, symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, owner);
        _setupRole(MINTER_ROLE, owner);
        anyoneCanMint = _anyoneCanMint;
		contract_gates = _contract_gates;
		_contract_decoder = _contract_decoder_param;
		contract_owner = owner;
    }

    // update contract fields
    function updateAdmin(address[] memory _contract_gates, bool _anyoneCanMint, address _contract_decoder_param) public {
        require(msg.sender==contract_owner, "CEEKLAND_036");
		contract_gates = _contract_gates;
        anyoneCanMint = _anyoneCanMint;
		_contract_decoder = _contract_decoder_param;
    }

	function ceekOwnerOf(uint256 tokenId) public view returns (string memory) {
		uint i;
		bool msgSenderFromGate = false;
		for(i=0; i<contract_gates.length; i++) {
			if (contract_gates[i] == msg.sender) {
				msgSenderFromGate = true;
				break;
			}
		}
		require((msgSenderFromGate), "CEEKLAND_037");
		return _ceekOwners[tokenId];
	}
	
	function setCeekOwnerOf(uint256 tokenId, string memory ceekUserId) public {
		uint i;
		bool msgSenderFromGate = false;
		for(i=0; i<contract_gates.length; i++) {
			if (contract_gates[i] == msg.sender) {
				msgSenderFromGate = true;
				break;
			}
		}
		require((msgSenderFromGate), "CEEKLAND_038");

		_ceekOwners[tokenId] = ceekUserId;
	}

	function getCeekUserId(bytes memory ceekUserIdParam) internal view returns (string memory) {	
		bytes memory resultDecode = Address.functionStaticCall(_contract_decoder, abi.encodeWithSignature("decode(bytes,bool)", ceekUserIdParam,true));
				
		return abi.decode(resultDecode, (string));
	}
		
	function markAsUsedCeekUserId(bytes memory ceekUserIdParam) internal {
		Address.functionCall(_contract_decoder,abi.encodeWithSignature("markAsUsed(bytes)", ceekUserIdParam));
	}

	function getTokenIdByCoordsOrGroup(string memory groupId, uint256 coord_land, uint256 coord_row, uint256 coord_col) public view returns (uint256) {
		if (coord_land > 0 && coord_row > 0 && coord_col > 0) {
       		return _tokenCoords[string(abi.encodePacked(coord_land,'_',coord_row,'_',coord_col))];
		}
		
        return _tokenGroup[groupId];
	}
	
    function checkOwner(uint256 tokenId, address owner, bytes memory ceekUserIdParam) private returns(string memory) {
        uint i;
		
		string memory ceekUserId = getCeekUserId(ceekUserIdParam);
        markAsUsedCeekUserId(ceekUserIdParam);

		bool msgSenderFromGate = false;
		for(i=0; i<contract_gates.length; i++) {
			if (contract_gates[i] == msg.sender) {
				msgSenderFromGate = true;
				break;
			}
		}

   		require((owner==msg.sender || msgSenderFromGate || (keccak256(abi.encodePacked((_ceekOwners[tokenId]))) != keccak256(abi.encodePacked((""))) && keccak256(abi.encodePacked((_ceekOwners[tokenId]))) == keccak256(abi.encodePacked((ceekUserId))))), "CEEKLAND_039");

        return ceekUserId;
    }

	function autoUngroup(string memory _baseURI, string[] memory _tokenURIs, uint256[] memory _tokenCoordsLand, uint256[] memory _tokenCoordsRow, uint256[] memory _tokenCoordsCol, uint256 tokenId, string memory groupId, bytes memory ceekUserIdParam, bool _burnParam) public {
		uint i;
		uint id;
		
		if (tokenId == 0) {
			tokenId = getTokenIdByCoordsOrGroup(groupId,0,0,0);
		}

        string memory ceekUserId = checkOwner(tokenId, ERC721.ownerOf(tokenId), ceekUserIdParam);
	    	
		for (i=0; i < _tokenURIs.length; i++) {
			do {
			  _tokenIdCounter.increment();
			  id = _tokenIdCounter.current();
			} while(_exists(id));

            _mint(ERC721.ownerOf(tokenId), id, ceekUserId,  _tokenRoyaltyInfo[tokenId].receiver,  _tokenRoyaltyInfo[tokenId].royaltyFraction);

			_setTokenURI(id, string(abi.encodePacked(_baseURI, _tokenURIs[i])));

            if (i<_tokenCoordsLand.length && i<_tokenCoordsRow.length && i<_tokenCoordsCol.length) {
                _tokenCoords[string(abi.encodePacked(_tokenCoordsLand[i],'_',_tokenCoordsRow[i],'_',_tokenCoordsCol[i]))] = id;
                _tokenCoordsKeys[id] = string(abi.encodePacked(_tokenCoordsLand[i],'_',_tokenCoordsRow[i],'_',_tokenCoordsCol[i]));
				emit TokenLand(id, _tokenCoordsLand[i]);
				emit TokenCoords(id, _tokenCoordsRow[i], _tokenCoordsCol[i]);
            }
		}

        if (_burnParam) {
			_burn(tokenId);
		}
	}

    function setTokenRoyaltyInfo(uint256[] memory tokenId, address royaltyRecipient, uint96 royaltyValue) public {
        require(msg.sender==contract_owner, "CEEKLAND_049");

        uint256 i;

        for (i=0; i<tokenId.length; i++) {
            _tokenRoyaltyInfo[tokenId[i]] = RoyaltyInfo(royaltyRecipient, royaltyValue);
        }
    }
	
    function autoMint(string memory _tokenURI, address to, uint256 coord_land, uint256 coord_row, uint256 coord_col, string memory groupId, bytes memory ceekUserIdParam, address royaltyRecipient, uint96 royaltyValue) public onlyMinter {
        uint id;
		string memory ceekUserId;

		ceekUserId = getCeekUserId(ceekUserIdParam);
        markAsUsedCeekUserId(ceekUserIdParam);

		if (coord_land > 0 && coord_row > 0 && coord_col > 0) {
		   	require(((_tokenCoords[string(abi.encodePacked(coord_land,'_',coord_row,'_',coord_col))]) == 0), "CEEKLAND_040");
		} else {
			require(((_tokenGroup[groupId]) == 0), "CEEKLAND_041");
		}

        do {
          _tokenIdCounter.increment();
          id = _tokenIdCounter.current();
        } while(_exists(id));
				
        _mint(to, id, ceekUserId, royaltyRecipient, royaltyValue);
        _setTokenURI(id, _tokenURI);
		
		if (coord_land > 0 && coord_row > 0 && coord_col > 0) {
			_tokenCoords[string(abi.encodePacked(coord_land,'_',coord_row,'_',coord_col))] = id;
			_tokenCoordsKeys[id] = string(abi.encodePacked(coord_land,'_',coord_row,'_',coord_col));
			emit TokenLand(id, coord_land);
			emit TokenCoords(id, coord_row, coord_col);
		} else {
			_tokenGroup[groupId] = id;
			_tokenGroupKeys[id] = groupId;
			emit TokenGroup(id, groupId);
        }
    }
	
	function autoMintCoordsOrGroup(string memory _baseURI, string[] memory _tokenURI, address to, uint256[] memory coord_land, uint256[] memory coord_row, uint256[] memory coord_col, string[] memory groupId, bytes memory ceekUserIdParam, address royaltyRecipient, uint96 royaltyValue) public onlyMinter {
        uint id;
		uint i;
		string memory ceekUserId;
		
		ceekUserId = getCeekUserId(ceekUserIdParam);
        markAsUsedCeekUserId(ceekUserIdParam);
		
		for (i=0; i<_tokenURI.length; i++) {
			do {
			  _tokenIdCounter.increment();
			  id = _tokenIdCounter.current();
			} while(_exists(id));
			
            _mint(to, id, ceekUserId, royaltyRecipient, royaltyValue);
			_setTokenURI(id, string(abi.encodePacked(_baseURI, _tokenURI[i])));
			
			if (i < groupId.length) {
           		require((((_tokenGroup[groupId[i]]) == 0)), "CEEKLAND_042");

				_tokenGroup[groupId[i]] = id;
				_tokenGroupKeys[id] = groupId[i];
				emit TokenGroup(id, groupId[i]);
			}
			if (i<coord_land.length && i<coord_row.length && i<coord_col.length) {
           		require(((_tokenCoords[string(abi.encodePacked(coord_land[i],'_',coord_row[i],'_',coord_col[i]))]) == 0), "CEEKLAND_043");

				_tokenCoords[string(abi.encodePacked(coord_land[i],'_',coord_row[i],'_',coord_col[i]))] = id;
				_tokenCoordsKeys[id] = string(abi.encodePacked(coord_land[i],'_',coord_row[i],'_',coord_col[i]));
				emit TokenLand(id, coord_land[i]);
				emit TokenCoords(id, coord_row[i], coord_col[i]);
			}
		}
    }

    function mint(address to, uint256 tokenId, bytes memory ceekUserIdParam, address royaltyRecipient, uint96 royaltyValue) public onlyMinter {
        string memory ceekUserId;
		
		ceekUserId = getCeekUserId(ceekUserIdParam);
        markAsUsedCeekUserId(ceekUserIdParam);

        _mint(to, tokenId, ceekUserId, royaltyRecipient, royaltyValue);
    }

    function safeMint(address to, uint256 tokenId, bytes memory ceekUserIdParam, address royaltyRecipient, uint96 royaltyValue) public onlyMinter {
        string memory ceekUserId;
		
		ceekUserId = getCeekUserId(ceekUserIdParam);
        markAsUsedCeekUserId(ceekUserIdParam);

        _safeMint(to, tokenId, ceekUserId, royaltyRecipient, royaltyValue);
    }

    function isMinter(address account) public view returns (bool) {
        return hasRole(MINTER_ROLE, account);
    }
	
    function safeMint(address to, uint256 tokenId, bytes memory ceekUserIdParam, address royaltyRecipient, uint96 royaltyValue, bytes memory _data) public onlyMinter {
        string memory ceekUserId;
		
		ceekUserId = getCeekUserId(ceekUserIdParam);
        markAsUsedCeekUserId(ceekUserIdParam);

        _safeMint(to, tokenId, ceekUserId, royaltyRecipient, royaltyValue, _data);
    }

    function _burn(uint256 tokenId) internal virtual override(ERC721, ERC721URIStorage) {
		if (bytes(_tokenCoordsKeys[tokenId]).length > 0) {
			delete _tokenCoords[_tokenCoordsKeys[tokenId]];
			delete _tokenCoordsKeys[tokenId];
		}
		if (bytes(_tokenGroupKeys[tokenId]).length > 0) {
			delete _tokenGroup[_tokenGroupKeys[tokenId]];
			delete _tokenGroupKeys[tokenId];
		}
		
        ERC721URIStorage._burn(tokenId);
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }
	
	function tokenURICoordsOrGroup(string memory groupId, uint256 coord_land, uint256 coord_row, uint256 coord_col)
        public
        view
        returns (string memory)
    {		
        return tokenURI(getTokenIdByCoordsOrGroup(groupId, coord_land, coord_row, coord_col));
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, AccessControlEnumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function addMinter(address account) public onlyRole(MINTER_ROLE) {
        grantRole(MINTER_ROLE, account);
    }
	
    /**
     * Open modifier to anyone can mint possibility
     */
    modifier onlyMinter() {
        string memory mensaje;
        require(
            (anyoneCanMint || isMinter(msg.sender)),
            "CEEKLAND_044"
        );
        _;
    }
}
