pragma solidity ^0.8.13;





contract DragoTracker is ERC20, Ownable {
    using SafeMath for uint256;
    using SafeMathUint for uint256;
    using SafeMathInt for int256;

    mapping(address => int) private dividendCorrection;
    mapping(address => uint) private withdrawnDividends;

    uint private magnifiedDividendPerShare;
    uint constant private magnitude = 2**128;

    uint public totalDividends;

    constructor() ERC20("DragoTracker", "DT") {}

    function distributeDividends(uint _amount) external onlyOwner {
        require(totalSupply() > 0);

        totalDividends.add(_amount);

        magnifiedDividendPerShare = magnifiedDividendPerShare.add(
            (_amount).mul(magnitude) / totalSupply()
        );
    }

    function getTotalDividens() external view returns(uint) {
        return totalDividends;
    }

    function withdrawDividend(address _account, uint _amount) external onlyOwner {
        withdrawnDividends[_account] = withdrawnDividends[_account].add(_amount);
    }

    function dividendOf(address _owner) external view returns(uint256) {
        return accumulativeDividendOf(_owner).sub(withdrawnDividends[_owner]);
    }

    function withdrawnDividendOf(address _owner) external view returns(uint256) {
        return withdrawnDividends[_owner];
    }

    function accumulativeDividendOf(address _owner) public view returns(uint256) {
        return magnifiedDividendPerShare.mul(balanceOf(_owner)).toInt256Safe()
            .add(dividendCorrection[_owner]).toUint256Safe() / magnitude;
    }

    function transferFrom(address _from, address _to, uint _value) public override onlyOwner returns (bool) {
        _transfer(_from, _to, _value);

        int256 _magCorrection = magnifiedDividendPerShare.mul(_value).toInt256Safe();
        dividendCorrection[_from] = dividendCorrection[_from].add(_magCorrection);
        dividendCorrection[_to] = dividendCorrection[_to].sub(_magCorrection);

        return true;
    }

    function burn(address _account, uint _amount) external onlyOwner {
        _burn(_account, _amount);

        dividendCorrection[_account] = dividendCorrection[_account]
            .add((magnifiedDividendPerShare.mul(_amount)).toInt256Safe());            
    }

    function mint(address _account, uint _amount) external onlyOwner {
        _mint(_account, _amount);

        dividendCorrection[_account] = dividendCorrection[_account]
            .sub((magnifiedDividendPerShare.mul(_amount)).toInt256Safe());
    }

}

