contract BabyDragoToken is IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    DragoTracker private tracker;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    
    uint private _totalSupply;

    string private _name;
    string private _symbol;

    mapping(address => bool) private isExcludedFromFee;
    mapping(address => bool) private isExcludedFromDividends;

    uint public marketingFee = 700;
    uint public dividendFee = 700;
    uint public liquidityFee = 100;

    uint private constant maxTokens = 1000000 * (10**18);
    uint private constant minTokensToSwap = 1000 * (10 ** 18);

    address public constant BUSD = 0x55d398326f99059fF775485246999027B3197955;

    address public constant marketingAddress = 0xba7F5b8a979BBaAeE1aA6DfC48Ef8E55C4B8e432;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    bool public swapEnabled = true;
    bool private inSwap;

    constructor() {
        _name = "BabyDrago Token";
         _symbol = "DRAGO";

        tracker = new DragoTracker();

        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());

        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[owner()] = true;

        isExcludedFromDividends[uniswapV2Pair] = true;
        isExcludedFromDividends[address(this)] = true;
        isExcludedFromDividends[owner()] = true;

        _mint(owner(), maxTokens);
    }

    receive() external payable {}

    function name() public view virtual returns (string memory) {
        return _name;
    }

    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 amount) public virtual override returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, allowance(owner, spender) + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        address owner = _msgSender();
        uint256 currentAllowance = allowance(owner, spender);
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function tokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        uint256 fromBalance = _balances[from];
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _balances[from] = fromBalance - amount;
        }
        _balances[to] += amount;

        emit Transfer(from, to, amount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "ERC20: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _transfer(address _from, address _to, uint _value) internal {
        if (
            balanceOf(address(this)) > minTokensToSwap &&
            _from != uniswapV2Pair &&
            swapEnabled &&
            !inSwap
        ) {
            // SWAP
            inSwap = true;

            uint _tokensToLiquidity = minTokensToSwap.mul(liquidityFee.div(2)).div(totalComission());
            uint _amountToSwap = minTokensToSwap.sub(_tokensToLiquidity);

            swapTokensForEth(_amountToSwap);

            uint _comissionWithoutTokens = totalComission().sub(liquidityFee.div(2));
            uint _ethAmountForLiquidity = address(this).balance.mul(liquidityFee.div(2)).div(_comissionWithoutTokens);
            uint _ethAmountForMarketing = address(this).balance.mul(marketingFee).div(_comissionWithoutTokens);

            addLiquidity(_tokensToLiquidity, _ethAmountForLiquidity);
            payable(marketingAddress).transfer(_ethAmountForMarketing);

            // Swap for BUSD
            uint _initialBalance = IERC20(BUSD).balanceOf(address(this));
            swapEthForUsd(address(this).balance);
            tracker.distributeDividends(IERC20(BUSD).balanceOf(address(this)).sub(_initialBalance));

            inSwap = false;
        }

        uint _comission = _value.mul(totalComission()).div(10000);

        if (isExcludedFromFee[_from] || isExcludedFromFee[_to]) {
            _comission = 0;
        }

        uint _transferedValue = _value.sub(_comission);

        tokenTransfer(_from, _to, _transferedValue);
        if (_comission > 0) {
            tokenTransfer(_from, address(this), _comission);
        }

        trackDividends(_from, _to, _transferedValue);
    }

    function swapTokensForEth(uint _amount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), _amount);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            _amount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function swapEthForUsd(uint _amount) private {
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = BUSD;

        uniswapV2Router.swapExactETHForTokens{value: _amount}(
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint _tokenAmount, uint _ethAmount) private {
        _approve(address(this), address(uniswapV2Router), _tokenAmount);

        uniswapV2Router.addLiquidityETH{value: _ethAmount}(
            address(this),
            _tokenAmount,
            0,
            0,
            owner(),
            block.timestamp
        );
    }

    function totalComission() public view returns(uint) {
        return marketingFee + dividendFee + liquidityFee;
    }

    function totalDividends() external view returns(uint) {
        return tracker.getTotalDividens();
    }

    function withdrawDividends() external {
        uint _dividends = tracker.dividendOf(msg.sender);
        bool _success = IERC20(BUSD).transfer(msg.sender, _dividends);

        if (_success) {
            tracker.withdrawDividend(msg.sender, _dividends);
        }
    }

    function dividendOf(address _owner) external view returns(uint) {
        return tracker.dividendOf(_owner);
    }

    function trackDividends(address _from, address _to, uint _amount) private {
        if (isExcludedFromDividends[_from] && isExcludedFromDividends[_to]) {
            return;
        }

        if (isExcludedFromDividends[_from]) {
            tracker.mint(_to, _amount);

            return;
        }

        if (isExcludedFromDividends[_to]) {
            tracker.burn(_from, _amount);

            return;
        }

        tracker.transferFrom(_from, _to, _amount);
    }

    function toggleSwap() external onlyOwner {
        swapEnabled = !swapEnabled;
    }

    function changeFee(uint _marketing, uint _dividends, uint _liquidity) external onlyOwner {
        require (_marketing + _dividends + _liquidity == 1500, "Total comission must be 15%");

        marketingFee = _marketing;
        dividendFee = _dividends;
        liquidityFee = _liquidity;
    }

    function excludeFromFee(address _account) external onlyOwner {
        isExcludedFromFee[_account] = true;
    }

    function includeInFee(address _account) external onlyOwner {
        require(_account != address(this), "Do not include contract address!");

        isExcludedFromFee[_account] = false;
    }

    function excludeFromDividends(address _account) external onlyOwner {
        isExcludedFromDividends[_account] = true;
    }

    function includeInDividends(address _account) external onlyOwner {
        isExcludedFromDividends[_account] = false;
    }

}
