    "/contracts/NFTHeroesMarket.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

abstract contract ContractNFT {
  function baseHeroesAttribute(uint256 tokenID)
    external
    view
    virtual
    returns (uint256);
}

contract NFTHeroesMarketplaceV1 is Ownable, ReentrancyGuard, Pausable {
  using SafeERC20 for IERC20;
  using Counters for Counters.Counter;

  ContractNFT public SMCNFT;
  Counters.Counter private _numCounter;

  struct SalesObject {
    uint256 id;
    uint256 tokenId;
    uint256 price;
    uint8 status;
    address seller;
    address buyer;
    IERC721 nft;
  }

  SalesObject[] public salesObjects;

  // List address is seller
  mapping(address => bool) public sellers;

  // List NFT support for sale
  mapping(address => bool) public supportNfts;

  // List NFT Attribute Tag Sp
  mapping(uint256 => bool) public supportTags;

  // Min Price by Gen
  uint256 public genCommonMinPrice;
  uint256 public genRareMinPrice;
  uint256 public genEpicMinPrice;
  uint256 public genLegendMinPrice;

  // Start accept user sale nft
  bool public isStartUserSales;

  // Check calc tips
  bool public isCalcTipsFee = false;

  uint256 public tipsFeeRate; // ~4.68 = 468
  uint256 public baseRate = 10000;
  address tipsFeeWallet;

  event EventSales(
    uint256 indexed id,
    uint256 tokenId,
    address buyer,
    uint256 finalPrice,
    uint256 tipsFee,
    address currencyAddress
  );

  event EventNewSales(
    uint256 indexed id,
    uint256 tokenId,
    address seller,
    address nft,
    uint256 price,
    address currencyAddress
  );

  event EventCancelSales(uint256 indexed id, uint256 tokenId);

  //
  mapping(uint256 => address) public saleOnCurrency;
  mapping(address => bool) public supportCurrency;

  event EventSupportCurrency(address currency, bool support);

  constructor() {}

  /**
   * check address
   */
  modifier validAddress(address addr) {
    require(addr != address(0x0));
    _;
  }

  modifier checkindex(uint256 index) {
    require(index <= salesObjects.length, "overflow");
    _;
  }

  // Check NFT is not selling out
  modifier mustNotSellingOut(uint256 index) {
    require(index <= salesObjects.length, "overflow");
    SalesObject storage obj = salesObjects[index];
    require(obj.buyer == address(0x0) && obj.status == 0, "sry, selling out");
    _;
  }

  // Check seles is owner or governance
  modifier onlySalesOwner(uint256 index) {
    SalesObject storage obj = salesObjects[index];
    require(obj.seller == msg.sender, "author seller");
    _;
  }

  // Check NFT attribute is accepted
  modifier checkNFTAttributeAccepted(uint256 _tokenID, uint256 _balanceSales) {
    (uint256 gen, uint256 tag) = getHeroesAttribute(_tokenID);

    require(supportTags[tag] == true, "This tag not supported!!");

    if (gen == 0) {
      require(_balanceSales >= genCommonMinPrice, "Min price CM error!");
    }
    if (gen == 1) {
      require(_balanceSales >= genRareMinPrice, "Min price Rare error!");
    }
    if (gen == 2) {
      require(_balanceSales >= genEpicMinPrice, "Min price Epic error!");
    }
    if (gen == 3) {
      require(_balanceSales >= genLegendMinPrice, "Min price Legend error!");
    }
    _;
  }

  // Set
  function setSmartContractNFT(address _newSMC) external onlyOwner {
    SMCNFT = ContractNFT(_newSMC);
  }

  function getHeroesAttribute(uint256 _tokenID)
    public
    view
    returns (uint256 heroesGen, uint256 heroesTag)
  {
    uint256 nftAttribute = SMCNFT.baseHeroesAttribute(_tokenID);
    string memory stringAttribute = uint2str(nftAttribute);

    uint256 heroesItem1 = stringToUint(substring(stringAttribute, 7, 9)) - 10;
    uint256 heroesItem2 = stringToUint(substring(stringAttribute, 9, 11)) - 10;
    uint256 heroesItem3 = stringToUint(substring(stringAttribute, 11, 13)) - 10;

    uint256[3] memory arrItem = [heroesItem1, heroesItem2, heroesItem3];

    uint256 gen;

    for (uint8 i = 0; i < arrItem.length; i++) {
      if (arrItem[i] > 8) {
        gen += 1;
      }
    }

    uint256 tag = stringToUint(substring(stringAttribute, 19, 21)) - 10;

    return (gen, tag);
  }

  function pause() public onlyOwner {
    _pause();
  }

  function unpause() public onlyOwner {
    _unpause();
  }

  // Governance widthdraw token
  function seize(IERC20 asset) external onlyOwner returns (uint256 balance) {
    balance = asset.balanceOf(address(this));
    asset.safeTransfer(msg.sender, balance);
  }

  // Set min price sales
  function setMinPrice(
    uint256 _minCM,
    uint256 _minRare,
    uint256 _minEpic,
    uint256 _minLegend
  ) external onlyOwner {
    genCommonMinPrice = _minCM;
    genRareMinPrice = _minRare;
    genEpicMinPrice = _minEpic;
    genLegendMinPrice = _minLegend;
  }

  // Set _isCalcTipsFee
  function setCalcTipsFee(bool _newCalcTipsFee) external onlyOwner {
    isCalcTipsFee = _newCalcTipsFee;
  }

  // Set array nft support
  function addSupportNft(address nft) external onlyOwner validAddress(nft) {
    supportNfts[nft] = true;
  }

  // Remove array nft support
  function removeSupportNft(address nft) external onlyOwner validAddress(nft) {
    supportNfts[nft] = false;
  }

  // Set array tag support
  function addSupportTag(uint256 tagNumber) external onlyOwner {
    supportTags[tagNumber] = true;
  }

  // Remove array tag support
  function removeSupportTag(uint256 tagNumber) external onlyOwner {
    supportTags[tagNumber] = false;
  }

  function addSeller(address seller) external onlyOwner validAddress(seller) {
    sellers[seller] = true;
  }

  function removeSeller(address seller)
    external
    onlyOwner
    validAddress(seller)
  {
    sellers[seller] = false;
  }

  function addSupportCurrency(address erc20)
    external
    validAddress(erc20)
    onlyOwner
  {
    require(supportCurrency[erc20] == false, "the currency have support");
    supportCurrency[erc20] = true;
    emit EventSupportCurrency(erc20, true);
  }

  function removeSupportCurrency(address erc20)
    external
    validAddress(erc20)
    onlyOwner
  {
    require(supportCurrency[erc20], "the currency can not remove");
    supportCurrency[erc20] = false;
    emit EventSupportCurrency(erc20, false);
  }

  // Set open for user sales
  function setIsStartUserSales(bool _isStartUserSales) external onlyOwner {
    isStartUserSales = _isStartUserSales;
  }

  function setTipsFee(
    address _wallet,
    uint256 _baseRate,
    uint256 _tipRate
  ) external onlyOwner validAddress(_wallet) {
    tipsFeeWallet = _wallet;
    baseRate = _baseRate;
    tipsFeeRate = _tipRate;
  }

  function getSales(uint256 index)
    external
    view
    checkindex(index)
    returns (SalesObject memory)
  {
    return salesObjects[index];
  }

  function cancelSales(uint256 index)
    external
    checkindex(index)
    onlySalesOwner(index)
    mustNotSellingOut(index)
    nonReentrant
  {
    require(isStartUserSales || sellers[msg.sender] == true, "cannot sales");
    SalesObject storage obj = salesObjects[index];

    obj.status = 2;
    obj.nft.safeTransferFrom(address(this), obj.seller, obj.tokenId);

    emit EventCancelSales(index, obj.tokenId);
  }

  function startSales(
    uint256 tokenId,
    uint256 price,
    address nft,
    address currency
  )
    external
    nonReentrant
    validAddress(nft)
    checkNFTAttributeAccepted(tokenId, price)
    whenNotPaused
  {
    // Check price
    require(price != 0, "invalid price");

    // Check User can seles, or sender is seller or nft is support for sale
    require(isStartUserSales || sellers[msg.sender] == true, "Cannot sales");

    require(supportNfts[nft] == true, "This NFT not support");

    // Check currency is accept to sell with
    require(supportCurrency[currency] == true, "Not support currency");

    // send nft to contract
    IERC721(nft).safeTransferFrom(msg.sender, address(this), tokenId);

    SalesObject memory obj;

    obj.id = _numCounter.current();
    obj.tokenId = tokenId;
    obj.seller = msg.sender;
    obj.nft = IERC721(nft);
    obj.buyer = address(0);
    obj.price = price;
    obj.status = 0;

    saleOnCurrency[obj.id] = currency;

    salesObjects.push(obj);

    _numCounter.increment();

    emit EventNewSales(obj.id, tokenId, msg.sender, nft, price, currency);
  }

  function buy(uint256 index)
    public
    nonReentrant
    mustNotSellingOut(index)
    whenNotPaused
  {
    SalesObject storage obj = salesObjects[index];
    require(isStartUserSales || sellers[msg.sender] == true, "cannot sales");
    address currencyAddr = saleOnCurrency[obj.id];

    uint256 price = obj.price;
    uint256 tipsFee = 0;
    uint256 purchase = 0;

    if (isCalcTipsFee) {
      tipsFee = (price * tipsFeeRate) / baseRate;
      purchase = price - tipsFee;

      require(tipsFeeWallet != address(0), "Please set tipsFeeWallet!");

      IERC20(currencyAddr).safeTransferFrom(msg.sender, tipsFeeWallet, tipsFee);
    } else {
      purchase = price;
    }

    IERC20(currencyAddr).safeTransferFrom(msg.sender, obj.seller, purchase);

    obj.nft.safeTransferFrom(address(this), msg.sender, obj.tokenId);

    obj.buyer = msg.sender;

    obj.status = 1; // This NFT Sellout

    // fire event
    emit EventSales(
      index,
      obj.tokenId,
      msg.sender,
      price,
      tipsFee,
      currencyAddr
    );
  }

  function uint2str(uint256 _i)
    internal
    pure
    returns (string memory _uintAsString)
  {
    if (_i == 0) {
      return "0";
    }
    uint256 j = _i;
    uint256 len;
    while (j != 0) {
      len++;
      j /= 10;
    }
    bytes memory bstr = new bytes(len);
    uint256 k = len;
    while (_i != 0) {
      k = k - 1;
      uint8 temp = (48 + uint8(_i - (_i / 10) * 10));
      bytes1 b1 = bytes1(temp);
      bstr[k] = b1;
      _i /= 10;
    }
    return string(bstr);
  }

  function substring(
    string memory str,
    uint256 startIndex,
    uint256 endIndex
  ) public pure returns (string memory) {
    bytes memory strBytes = bytes(str);
    bytes memory result = new bytes(endIndex - startIndex);
    for (uint256 i = startIndex; i < endIndex; i++) {
      result[i - startIndex] = strBytes[i];
    }
    return string(result);
  }

  function stringToUint(string memory s) public pure returns (uint256 result) {
    bytes memory b = bytes(s);
    uint256 i;
    result = 0;
    for (i = 0; i < b.length; i++) {
      uint256 c = uint256(uint8(b[i]));
      if (c >= 48 && c <= 57) {
        result = result * 10 + (c - 48);
      }
    }
  }

  // Required function to allow receiving ERC-721 - When safeTransferFrom called auto implement this func if (to) is contract address
  function onERC721Received(
    address, /*operator*/
    address, /*from*/
    uint256, /*id*/
    bytes calldata /*data*/
  ) external pure returns (bytes4) {
    return bytes4(keccak256("onERC721Received(address,address,uint256,bytes)"));
  }
}
