contract Stakev3 is ReentrancyGuard{
    
    uint256 public rewardsBalance = 10000000* (10**18);
    IERC20 public ksnd;
    uint256 rewardsLeft;
    constructor(address KSNDAddr){
        ksnd = IERC20(KSNDAddr);
    }
    

    event Staked(uint256 _stakeId, address indexed _staker, uint256 _amount, uint256 _stakeTimestamp, uint256 _unstakeTimeStamp);
    event Unstaked(uint256 _stakeId, address indexed _staker, uint256 _amount, uint256 _stakeTimestamp, uint256 _unstakeTimeStamp, uint _claimTimeStamp);
    event rewardClaimed(uint256 _stakeId, address _staker, uint256 _rewardAmount, uint256 _claimTimestamp, uint256 rewardsLeft);

    struct StakeEntry{
        uint256 stakeId;
        address staker;
        uint256 amount;
        uint256 stakeDuration;
        uint256 stakeTs;
        uint256 unstakeTs;
        uint256 lastClaimTimestamp; 
    }

    mapping (uint256 => StakeEntry) stakeData;
    uint256 totalStakedAmount;
    mapping(uint256=>uint256) rewardsLocked;
    uint256 rewards;

    modifier updateRewards(uint _stakeId)  {

        StakeEntry storage stakeEntry = stakeData[_stakeId];
        require(stakeEntry.staker == msg.sender, "Invaild Wallet Address, You can't claim rewards for other's wallet aadress!");
        uint256 claimTimeStamp = block.timestamp - stakeEntry.lastClaimTimestamp ;
        stakeEntry.lastClaimTimestamp = block.timestamp;
        
        if(stakeEntry.stakeDuration==6){
            rewards = (claimTimeStamp/2628000)*(stakeEntry.amount*4/100);
        }
        else if(stakeEntry.stakeDuration==12){

            rewards = (claimTimeStamp/2628000)*(stakeEntry.amount*45/1000);
        }
        else if(stakeEntry.stakeDuration==18){
            rewards = (claimTimeStamp/2628000)*(stakeEntry.amount*5/100);
        }
        _;
    }


    function getUnstakeTs(uint256 _stakeId) external view returns(uint256){
        return stakeData[_stakeId].unstakeTs;
    }

    function getStakingAmount(uint256 _stakeId) external view returns(uint256){
        return stakeData[_stakeId].amount;
    }

    function getStakedRewards(uint256 _stakeId)external view returns (uint256){
        return rewardsLocked[_stakeId];
    }
    
    function stake(uint256 _stakeId,uint256 _amount, uint256 _stakeForMonths) external nonReentrant{
        
        require(_stakeId > 0, "Order Required for stake Transaction!");
        require(_amount>=10*(10**18),"minimum 10 KSN required for staking");
        require(ksnd.balanceOf(msg.sender)>=_amount,"not enough balance to stake");
        require(stakeData[_stakeId].amount == 0, "Tokens have already been locked up for the given order id!");
        require(ksnd.allowance(msg.sender, address(this)) >= _amount, "The contract does not have enough of an allowance to escrow!");
        uint256 _unstakeTs = uint256(block.timestamp) + 157700000000;

        if(_stakeForMonths==6){
            _unstakeTs = uint256(block.timestamp+ 15770000);
            rewardsLocked[_stakeId] = (_amount*4/100)*6;
            require(rewardsLocked[_stakeId]<=rewardsBalance, "not enough reward balance for staking given amount for given time");
            rewardsBalance -= rewardsLocked[_stakeId];
        }

        else if(_stakeForMonths==12){
            _unstakeTs = uint256(block.timestamp) + 31560000;
            rewardsLocked[_stakeId] = (_amount*45/1000)*12;
            require(rewardsLocked[_stakeId]<=rewardsBalance, "not enough reward balance for staking given amount for given time");
            rewardsBalance -= rewardsLocked[_stakeId];
        }

        else if(_stakeForMonths==18){
            _unstakeTs = uint256(block.timestamp) + 47340000;
            rewardsLocked[_stakeId] = (_amount*5/100)*18;
            require(rewardsLocked[_stakeId]<=rewardsBalance, "not enough reward balance for staking given amount for given time");
            rewardsBalance -= rewardsLocked[_stakeId];
        }

        stakeData[_stakeId] = StakeEntry({
            stakeId : _stakeId,
            staker : msg.sender,
            amount : _amount,          
            stakeDuration :_stakeForMonths,
            stakeTs : block.timestamp,
            unstakeTs : _unstakeTs,
            lastClaimTimestamp: block.timestamp
        });

        bool transferSuccess = ksnd.transferFrom(msg.sender, address(this), _amount);
        require(transferSuccess, "Failed to stake tokens in the contract");

        emit Staked(_stakeId, msg.sender, _amount, block.timestamp, _unstakeTs );

    }

    function unstake(uint256 _stakeId,uint256 _amount) external nonReentrant{
        require(_stakeId > 0, "Order Required for unstake Transaction!");
        StakeEntry storage stakeEntry = stakeData[_stakeId];
        require(stakeEntry.staker == msg.sender, "Invaild Wallet Address, You can't withdraw another wallet balance!");
        require(stakeEntry.amount == _amount, "wrong input token amount");
        require(block.timestamp >= stakeEntry.unstakeTs, "Tokens are still locked up");
        stakeEntry.amount = 0;
        bool transferSuccess = ksnd.transfer(msg.sender, _amount);

        require(transferSuccess, "Failed to send tokens to the beneficiary");
        emit Unstaked(_stakeId, msg.sender, _amount, stakeEntry.stakeTs, stakeEntry.unstakeTs, block.timestamp);
    }



    function claimRewards(uint256 _stakeId) external nonReentrant updateRewards(_stakeId){
        rewardsLeft = rewardsLocked[_stakeId];
        require(rewardsLeft>=rewards, "you have consumed all of your rewards");
        bool claimSuccess = ksnd.transfer(msg.sender,rewards);
        require(claimSuccess, "Failed to send rewards to the staker");

        rewardsLocked[_stakeId] -= rewards;
        emit rewardClaimed(_stakeId, msg.sender, rewards, block.timestamp, rewardsLeft );


        
    }
}
