pragma solidity >=0.5.0;

// Allows anyone to claim a token if they exist in a merkle root.
interface IMBSale {
    // Returns the address of the token distributed by this contract.
    function nftToken() external view returns (address);
    // Returns true if the index has been marked claimed.
    function isClaimed(uint256 index, uint256 saleIndex) external view returns (bool);
    // Claim the given amount of the token to the given address. Reverts if the inputs are invalid.
    function buy(uint256 index, address account, bytes32[] calldata merkleProof, uint256 saleIndex) external;

    // This event is triggered whenever a call to #claim succeeds.
    event Claimed(uint256 index, address account);

    // This event is triggered whenever a call to #claim succeeds.
    event MBPurchased(address account, uint256 price, uint256 saleNumber);

    // This event is triggered whenever a call to #update the merkle root succeeds.
    event MerkleRootUpdated(bytes32 newMerkleRoot);
}"
