pragma solidity ^0.8.17;

import "solmate/auth/Owned.sol";
import {IERC20} from "./interfaces/IERC20.sol";
import {IUniswapV2Router} from "./Uniswap/IUniswapV2Router.sol";

IERC20 constant USDT = IERC20(0x55d398326f99059fF775485246999027B3197955);
IERC20 constant SU = IERC20(0x7080084C2dD1E3Fdec4892A7f53d3c27e82574fF);
IUniswapV2Router constant router = IUniswapV2Router(0x10ED43C718714eb63d5aA57B78B54704E256024E);

address constant P_85 = 0xfDBD5c29d263437d5dafD45F0094194bdebB7259;
address constant P_5 = 0x2b1ED13d92b46d01086B34A6317A4D291d9B3210;
address constant P_1 = 0xeC2Aba2BEB2914617c67d6F069634E826E9468bC;
address constant P_4 = 0xD4fCD0f601D73C0987e226C917c4Db5E8C46F3Ad;

contract Staking is Owned {
    event Deposit(address indexed sender, uint256 amount);

    constructor() Owned(msg.sender) {
        USDT.approve(address(router), type(uint256).max);
    }

    function deposit(uint256 amount, uint256 minOut) external {
        USDT.transferFrom(msg.sender, address(this), amount);
        uint256 t85 = amount * 85 / 100;
        uint256 t5 = amount * 5 / 100;
        uint256 t4 = amount * 4 / 100;
        uint256 t1 = amount / 100;
        uint256 tburn = amount - t85 - t5 - t4 - t1;
        USDT.transfer(P_85, t85);
        USDT.transfer(P_5, t5);
        USDT.transfer(P_4, t4);
        USDT.transfer(P_1, t1);

        address[] memory path = new address[](2);
        path[0] = address(USDT);
        path[1] = address(SU);
        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tburn, minOut, path, address(0xdead), block.timestamp
        );

        emit Deposit(msg.sender, amount);
    }

    function withdrawToken(IERC20 token, uint256 _amount) external onlyOwner {
        token.transfer(msg.sender, _amount);
    }
}
