      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {INoFomo} from "./interface/INoFomo.sol";

contract AirdropAdapter is Ownable {
    INoFomo public noFomoToken;

    mapping(address => bool) airdropRecord;

    bytes32 public merkleRoot;

    constructor(address _nofomo) Ownable(msg.sender) {
        noFomoToken = INoFomo(_nofomo);
    }

    function claimAirDrop(bytes32[] calldata _merkleProof) external {
        address receipt = msg.sender;
        require(
            !airdropRecord[receipt],
            "AirdropAdapter: claimAirDrop: already claimed"
        );
        bytes32 leaf = keccak256(abi.encodePacked(receipt));
        require(MerkleProof.verify(_merkleProof, merkleRoot, leaf), "AirdropAdapter: claimAirDrop: invalid proof");
        airdropRecord[receipt] = true;
        noFomoToken.airdrop(receipt);
    }

    function setMerkleRoot(bytes32 _merkleRoot) external onlyOwner {
        merkleRoot = _merkleRoot;
    }
}
"
