    "/contracts/ERC721Tradable.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "../node_modules/@openzeppelin/contracts/utils/math/SafeMath.sol";
import "../node_modules/@openzeppelin/contracts/utils/Strings.sol";
import "../node_modules/@openzeppelin/contracts/token/ERC721/presets/ERC721PresetMinterPauserAutoId.sol";
import "./ContentMixin.sol";
import "./utils/DefaultOperatorFilterer.sol";

contract OwnableDelegateProxy {}

contract ProxyRegistry {
    mapping(address => OwnableDelegateProxy) public proxies;
}

contract ERC721Tradable is
    ContextMixin,
    ERC721PresetMinterPauserAutoId,
    DefaultOperatorFilterer
{
    using SafeMath for uint256;
    address public proxyRegistryAddress;
    bool public isRevealed = false;
    string public _placeholderURI = "https://assetsmeta.virtua.com/foundersdistrict/guardian/unrevealed/guardian";

    constructor(
        string memory _name,
        string memory _symbol,
        address _proxyRegistryAddress,
        string memory _baseURI,
        address royaltiesReceiver,
        uint96 royaltiesFeeNumerator,
        address eventContract
    )
        ERC721PresetMinterPauserAutoId(
            _name,
            _symbol,
            _baseURI,
            royaltiesReceiver,
            royaltiesFeeNumerator,
            eventContract
        )
    {
        proxyRegistryAddress = _proxyRegistryAddress;
    }

    function tokenURI(
        uint256 _tokenId
    ) public view override returns (string memory) {
        if (isRevealed) {
            return
                string(
                    abi.encodePacked(getBaseURI(), Strings.toString(_tokenId))
                );
        } else {
            return _placeholderURI;
        }
    }

    function toggleIsReveal() public onlyOwner{
        isRevealed = !isRevealed;
    }

    // for mainnet deployments
    function isApprovedForAll(address owner, address operator)
       override(ERC721,IERC721)
        public
        view
        returns (bool)
    {
        ProxyRegistry proxyRegistry = ProxyRegistry(proxyRegistryAddress);
        if (address(proxyRegistry.proxies(owner)) == operator) {
            return true;
        }else if (hasRole(SECONDARY_WHITELISTED_ROLE,_msgSender())){
            return true;
        }

        return super.isApprovedForAll(owner, operator);
    }

    function _msgSender() internal view override returns (address sender) {
        return ContextMixin.msgSender();
    }


    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override(ERC721, IERC721) onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override(ERC721, IERC721) onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) public override(ERC721, IERC721) onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, data);
    }
}
"
    },
    "/contracts/ContentMixin.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

abstract contract ContextMixin {
    function msgSender()
        internal
        view
        returns (address payable sender)
    {
        if (msg.sender == address(this)) {
            bytes memory array = msg.data;
            uint256 index = msg.data.length;
            assembly {
                // Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
                sender := and(
                    mload(add(array, index)),
                    0xffffffffffffffffffffffffffffffffffffffff
                )
            }
        } else {
            sender = payable(msg.sender);
        }
        return sender;
    }
}"
    }
  },
