pragma solidity 0.8.20;




contract MerkleDistributor is IMerkleDistributor {
    using SafeERC20 for IERC20;
    address public immutable override token;
    bytes32 public immutable override merkleRoot;
    uint256 public immutable endTime;


    // This is a packed array of booleans.
    mapping(address => bool) private claimedMap;

    constructor(address token_, bytes32 merkleRoot_,uint256 endTime_) {
        token = token_;
        merkleRoot = merkleRoot_;
        endTime = endTime_;
    }

    function isClaimed(address _address) public view override returns (bool) {
        return claimedMap[_address];
    }
    
    function getCurrentTime()public view returns (uint256){
        return block.timestamp;
    }
    function isOver()public view returns (bool){
        return getCurrentTime()>endTime;
    }

    function _setClaimed(address _address) private {
        claimedMap[_address] = true;
    }

    function claim(uint256 amount, bytes32[] calldata merkleProof) public virtual override {
        require(!claimedMap[msg.sender], 'Account already claimed.');
        require(!isOver(),'Time is over');
        // Verify the merkle proof.
        bytes32 node = keccak256(
            abi.encodePacked(msg.sender, amount)
        );
        bool isValidProof = MerkleProof.verify(
            merkleProof,
            merkleRoot,
            node
        );
        require(isValidProof, 'Invalid proof.');

        // Mark it claimed and send the token.
        _setClaimed(msg.sender);
        IERC20(token).safeTransfer(msg.sender, amount);

        emit Claimed(msg.sender, amount);
    }
}
