    uint256 private _status;

    constructor() {
        _status = _NOT_ENTERED;
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and make it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        // On the first call to nonReentrant, _notEntered will be true
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");

        // Any calls to nonReentrant after this point will fail
        _status = _ENTERED;

        _;

        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = _NOT_ENTERED;
    }
}

pragma solidity ^0.8.0;

/**
 * @dev Contract module that works as staking vault for NFT
 *
 *  _              ______      
 * | |             |  _  \     
 * | |     ___  ___| | | |___  
 * | |    / _ \/ _ \ | | / _ \ 
 * | |___|  __/  __/ |/ / (_) |
 * \_____/\___|\___|___/ \___/ 
 * LEEDO Project
 */

interface ILeedoNft {
    event Transfer(address indexed _from, address indexed _to, uint256 indexed _tokenId);
    event Approval(address indexed _owner, address indexed _approved, uint256 indexed _tokenId);
    event ApprovalForAll(address indexed _owner, address indexed _operator, bool _approved);
    function balanceOf(address _owner) external view returns (uint256);
    function ownerOf(uint256 _tokenId) external view returns (address);
    function safeTransferFrom(address _from, address _to, uint256 _tokenId, bytes memory data) external payable;
    function safeTransferFrom(address _from, address _to, uint256 _tokenId) external payable;
    function transferFrom(address _from, address _to, uint256 _tokenId) external payable;
    function approve(address _approved, uint256 _tokenId) external payable;
    function setApprovalForAll(address _operator, bool _approved) external;
    function getApproved(uint256 _tokenId) external view returns (address);
    function isApprovedForAll(address _owner, address _operator) external view returns (bool);
    
    function claim() external;
    function ownerClaim(uint256 _tokenId) external;
    function tokenByIndex(uint256 index) external view returns (uint256);
    function totalSupply() external view returns (uint256);
    function getConsonantsIndex(uint256 _tokenId) external view returns (uint8[3] memory);  
    function tokenOfOwnerByIndex(address _owner, uint256 _index) external view returns (uint256);
    function getConsonants(uint256 _tokenId) external view returns (string[3] memory);
    function getGenes(uint256 _tokenId) external view returns (uint8[8] memory);    
    function tokenURI(uint256 _tokenId) external view returns (string memory);    
}

interface ILeedoERC20 {
    function mintNftVaultRewards(address _to, uint _amount) external returns (bool);
}

contract LeedoNftVault is ERC721Enumerable, Ownable, ReentrancyGuard { 
    using SafeMath for uint;
    
    bool public daoInitialized = false;
    bool public transferAllowed = false; //by default regular transfer of staked NFTs are not allowed except bridge transfer
    address private _nftAddr;
    address private _erc20Addr; 
    address private _bridgeAddr;
    address private _daoAddr;
    
    uint public expiration = block.number + 365 days;
    uint public rate = 5194874553201880; //per card per block reward rate in wei        
    mapping(address => uint) public lastBlocks; //address => lastBlock claimed
    mapping(address => uint) public totalClaims; // address => total paid    
    mapping(uint => uint) private bridgeBlocks; //brodge tokenId => lastBlock claimed

    modifier onlyDao() {
        require(_daoAddr == _msgSender(), "VAULT: caller is not the DAO address!");
        _;
    } 
    
    //nftAddr = 0xed8a775Fde8454c99Eb4e511D7D4D6b969024C5d; //mainnet
    constructor(address _nftAddress, address _erc20Address, address _bridgeAddress) 
        ERC721("LEEDO LP - Squid Game Card", "LeedoSquidGameCardLP") {
        _nftAddr = _nftAddress;
        _erc20Addr = _erc20Address;
        _bridgeAddr = _bridgeAddress;
    }
    
    function setBridgeAddr(address _bridgeAddress) public onlyOwner {
        require(!daoInitialized, 'NftVault: DAO is already initialized');
        _bridgeAddr = _bridgeAddress;
    }
    
    function setErc20Addr(address _erc20Address) public onlyOwner {
        require(!daoInitialized, 'NftVault: DAO is already initialized');
        _erc20Addr = _erc20Address;
    }
        
        
    function initializeDao(address _daoAddress) public onlyOwner {
        require(!daoInitialized, 'NftVault: DAO is already initialized');
        _daoAddr = _daoAddress;
        daoInitialized = true;
    }
    
    function setBridgeAddrByDao(address _bridgeAddress) public onlyDao() {
        _bridgeAddr = _bridgeAddress;
    }
    
    function setErc20AddrByDao(address _erc20Address) public onlyDao() {
        _erc20Addr = _erc20Address;
    }    
    
    function setAllowTransfer(bool _bool) public onlyDao() {
        transferAllowed = _bool;
    }
    
    function setDaoAddr(address _daoAddress) public onlyDao() {
        _daoAddr = _daoAddress;
    }
    
    function setRate(uint256 _rate) public onlyDao() {
        rate = _rate;
    }

    function setExpiration(uint256 _expiration) public onlyDao() {
        expiration = block.number + _expiration;
    }
    
    function calcRewards(address account) public view returns (uint) {
        require(Math.min(block.number, expiration) >= lastBlocks[account], "Invalid blocks");
        uint blockCount = Math.min(block.number, expiration) - lastBlocks[account];
        return rate.mul(balanceOf(account)).mul(blockCount) ;
    }
    
    function calcBridgeRewards(uint tokenId) public view returns (uint) {
        require(Math.min(block.number, expiration) >= bridgeBlocks[tokenId], "Invalid blocks");
        uint blockCount = Math.min(block.number, expiration) - bridgeBlocks[tokenId];
        return rate.mul(blockCount);
    }    
    
    function claimRewards() public {
        _claimRewards(_msgSender());
    } 
    
    function _claimRewards(address account) private nonReentrant {
        uint amount = calcRewards(account);
        uint blockCur = Math.min(block.number, expiration);
        lastBlocks[_msgSender()] = blockCur;
        if (amount > 0) {
            totalClaims[_msgSender()] = totalClaims[_msgSender()].add(amount);
            require(ILeedoERC20(_erc20Addr).mintNftVaultRewards(account, amount), 'NftVault: Leedo Minting failed');
        }        
    }  
    
    function _claimBridgeRewards(uint tokenId, address to) private nonReentrant {
        uint amount = calcBridgeRewards(tokenId);
        uint blockCur = Math.min(block.number, expiration);
        bridgeBlocks[tokenId] = blockCur;
        if (amount > 0) {
            totalClaims[to] = totalClaims[to].add(amount);
            require(ILeedoERC20(_erc20Addr).mintNftVaultRewards(to, amount), 'NftVault: Leedo Minting failed');
        }        
    }       
    
    function stake(uint256[] calldata tokenIds) external returns (bool) {
        claimRewards();
        ILeedoNft nft = ILeedoNft(_nftAddr);

        for (uint256 i; i < tokenIds.length; i++) {
            uint tokenId = tokenIds[i];
            require(!_exists(tokenId), 'NftVault: The tokenId already exists');
            require(nft.ownerOf(tokenId) == _msgSender(), 'NftVault: Owner is differenent' );
            ILeedoNft(_nftAddr).safeTransferFrom(
                _msgSender(),
                address(this),
                tokenId,
                ""
            );
            require(safeMint(_msgSender(), tokenId), 'NftValut: Staking failed');
        }
        return true;
    }
    
    function stakeByCount(uint256 _count) external returns (uint256[] memory){
        claimRewards();
        ILeedoNft nft = ILeedoNft(_nftAddr);
        
        require(_count <= nft.balanceOf(_msgSender()), 'NftVault: Count is more than the owner has');
        uint256[] memory tokenIds = new uint256[] (_count);

        for (uint256 i; i < _count; i++) {
            tokenIds[i] = nft.tokenOfOwnerByIndex(_msgSender(), i);
        }
        
        for (uint256 i; i < tokenIds.length; i++) {
            ILeedoNft(_nftAddr).safeTransferFrom(
                _msgSender(),
                address(this),
                tokenIds[i],
                ""
            );
            require(safeMint(_msgSender(), tokenIds[i]), 'NftVault: Staking failed');
        }
        return tokenIds;
    }       
    
    function safeMint(address _to, uint _tokenId) private nonReentrant returns (bool) {
        _safeMint(_to, _tokenId);
        return true;
    }   
    
    function withdraw(uint256[] calldata _tokenIds) external {
        claimRewards();

        for (uint256 i; i < _tokenIds.length; i++) {
            uint tokenId = _tokenIds[i];
            require(ownerOf(tokenId) == _msgSender(), 'NftVault: LP owner is different');            
            _burn(tokenId);
            ILeedoNft(_nftAddr).safeTransferFrom(
                address(this),
                _msgSender(),
                tokenId,
                ""
            );            
        }
    }

    function tokensOf(address _account) public view returns (uint[] memory) {
        uint[] memory tokenIds = new uint256[] (balanceOf(_account));
        for (uint i; i<balanceOf(_account); i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_account, i);
        }
        return tokenIds;
    }
    
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId);    
        if (from != address(0) && to != address(0)) {
            if (from != _bridgeAddr && to != _bridgeAddr) {
                require(transferAllowed, 'NftVault: Regular transfer is not allowed');
                _claimRewards(from);
                _claimRewards(to);
                
            } else if (to == _bridgeAddr) {
                _claimRewards(from);
                bridgeBlocks[tokenId] = Math.min(block.number, expiration);
                
            } else if (from == _bridgeAddr) {
                _claimRewards(to);
                _claimBridgeRewards(tokenId, to);
            }
        }
    }
    
    function daoAddr() external view returns (address) {
        return _daoAddr;
    }
    
    function nftAddr() external view returns (address) {
        return _nftAddr;
    }
    
    function erc20Addr() external view returns (address) {
        return _erc20Addr;
    }
    
    function bridgeAddr() external view returns (address) {
        return _bridgeAddr;
    }    

    function getConsonantsIndex(uint256 _tokenId) public view returns (uint8[3] memory) {
        return ILeedoNft(_nftAddr).getConsonantsIndex(_tokenId);
    }  

    function getConsonants(uint256 _tokenId) public view returns (string[3] memory) {
        return ILeedoNft(_nftAddr).getConsonants(_tokenId);
    }

    function getGenes(uint256 _tokenId) public view returns (uint8[8] memory) {
        return ILeedoNft(_nftAddr).getGenes(_tokenId);
    }
    
    function tokenURI(uint256 _tokenId) override public view returns (string memory) {
        
        string[9] memory parts;
        string[27] memory attrParts;
        string memory ojingeo;
        string memory sameConsonants;
        uint8[8] memory geneArray = getGenes(_tokenId);
        string[3] memory consArray = getConsonants(_tokenId);
        uint8[3] memory consIndex = getConsonantsIndex(_tokenId);        
        if (consIndex[0] == 7 && consIndex[1] == 14 && consIndex[2] == 4) {
            ojingeo = 'Y';
        } else {
            ojingeo = 'N';
        }
        if (consIndex[0] == consIndex[1] && consIndex[0] == consIndex[2]) {
            sameConsonants = 'Y';
        } else {
            sameConsonants = 'N';
        }

        parts[0] = '<svg xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMinYMin meet" viewBox="0 0 350 220">';
        parts[1] = '<style>.base {font-family: Verdana; fill: white;}</style>';
        parts[2] = '<rect width="100%" height="100%" fill="#458B74" />';
        parts[3] = '<text x="50%" y="100" dominant-baseline="middle" text-anchor="middle" class="base" style="font-size:700%; letter-spacing: -0.2em;">';
        parts[4] = string(abi.encodePacked(consArray[0], ' ', consArray[1], ' ', consArray[2]));
        parts[5] = '</text><text x="50%" y="180" dominant-baseline="middle" text-anchor="middle" class="base" style="font-size:150%;">&#937; ';
        parts[6] = string(abi.encodePacked(toString(geneArray[0]), toString(geneArray[1]), toString(geneArray[2]), toString(geneArray[3]), ' '));
        parts[7] = string(abi.encodePacked(toString(geneArray[4]), toString(geneArray[5]), toString(geneArray[6]), toString(geneArray[7]) ));
        parts[8] ='</text></svg>';
        
        string memory output = string(abi.encodePacked(parts[0], parts[1], parts[2], parts[3], parts[4], parts[5], parts[6], parts[7], parts[8]));

        attrParts[0] = '[{"trait_type": "Left Consonant", "value": "';
        attrParts[1] = consArray[0];
        attrParts[2] = '"}, {"trait_type": "Center Consonant", "value": "';
        attrParts[3] = consArray[1];
        attrParts[4] = '"}, {"trait_type": "Right Consonant", "value": "';        
        attrParts[5] = consArray[2];
        attrParts[6] = '"}, {"trait_type": "Gene0", "value": "';
        attrParts[7] = toString(geneArray[0]);
        attrParts[8] = '"}, {"trait_type": "Gene1", "value": "';
        attrParts[9] = toString(geneArray[1]);
        attrParts[10] = '"}, {"trait_type": "Gene2", "value": "';        
        attrParts[11] = toString(geneArray[2]);
        attrParts[12] = '"}, {"trait_type": "Gene3", "value": "';        
        attrParts[13] = toString(geneArray[3]);
        attrParts[14] = '"}, {"trait_type": "Gene4", "value": "';        
        attrParts[15] = toString(geneArray[4]);
        attrParts[16] = '"}, {"trait_type": "Gene5", "value": "';        
        attrParts[17] = toString(geneArray[5]);
        attrParts[18] = '"}, {"trait_type": "Gene6", "value": "';        
        attrParts[19] = toString(geneArray[6]);
        attrParts[20] = '"}, {"trait_type": "Gene7", "value": "';        
        attrParts[21] = toString(geneArray[7]);
        attrParts[22] = '"}, {"trait_type": "Ojingeo", "value": "';        
        attrParts[23] = ojingeo;
        attrParts[24] = '"}, {"trait_type": "Same Consonants", "value": "';        
        attrParts[25] = sameConsonants;
        attrParts[26] = '"}]';
        
        string memory attrs = string(abi.encodePacked(attrParts[0], attrParts[1], attrParts[2], attrParts[3], attrParts[4], attrParts[5], attrParts[6], attrParts[7]));
        attrs = string(abi.encodePacked(attrs, attrParts[8], attrParts[9], attrParts[10], attrParts[11], attrParts[12], attrParts[13], attrParts[14]));        
        attrs = string(abi.encodePacked(attrs, attrParts[15], attrParts[16], attrParts[17], attrParts[18], attrParts[19], attrParts[20]));        
        attrs = string(abi.encodePacked(attrs, attrParts[21], attrParts[22], attrParts[23], attrParts[24], attrParts[25], attrParts[26]));        

        string memory json = Base64.encode(bytes(string(abi.encodePacked('{"name": "LEEDO LP - Squid Game Card NFT #', toString(_tokenId), '", "attributes": ', attrs ,', "description": "The squid game cards are invitation to enter the adventurous and mysterious metaverse games. Genes characteristics and other functionality are intentionally omitted for unlimited imagination and community-driven game development. Start your journey now!", "image": "data:image/svg+xml;base64,', Base64.encode(bytes(output)), '"}'))));
        output = string(abi.encodePacked('data:application/json;base64,', json));
        return output;
    }    
       
    //function tokenURI(uint256 _tokenId) public override view returns (string memory) {
    //    return ILeedoNft(_nftAddr).tokenURI(_tokenId);
    //}    

    function onERC721Received(
        address, 
        address, 
        uint256, 
        bytes calldata
        ) pure external returns(bytes4) {
        return bytes4(keccak256("onERC721Received(address,address,uint256,bytes)"));
    }
    
    function toString(uint256 value) internal pure returns (string memory) {
    // Inspired by OraclizeAPI's implementation - MIT license
    // https://github.com/oraclize/ethereum-api/blob/b42146b063c7d6ee1358846c198246239e9360e8/oraclizeAPI_0.4.25.sol

        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }    
    
}
/// [MIT License]
/// @title Base64
/// @notice Provides a function for encoding some bytes in base64
/// @author Brecht Devos <brecht@loopring.org>
library Base64 {
    bytes internal constant TABLE = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";

    /// @notice Encodes some bytes to the base64 representation
    function encode(bytes memory data) internal pure returns (string memory) {
        uint256 len = data.length;
        if (len == 0) return "";

        // multiply by 4/3 rounded up
        uint256 encodedLen = 4 * ((len + 2) / 3);

        // Add some extra buffer at the end
        bytes memory result = new bytes(encodedLen + 32);

        bytes memory table = TABLE;

        assembly {
            let tablePtr := add(table, 1)
            let resultPtr := add(result, 32)

            for {
                let i := 0
            } lt(i, len) {

            } {
                i := add(i, 3)
                let input := and(mload(add(data, i)), 0xffffff)

                let out := mload(add(tablePtr, and(shr(18, input), 0x3F)))
                out := shl(8, out)
                out := add(out, and(mload(add(tablePtr, and(shr(12, input), 0x3F))), 0xFF))
                out := shl(8, out)
                out := add(out, and(mload(add(tablePtr, and(shr(6, input), 0x3F))), 0xFF))
                out := shl(8, out)
                out := add(out, and(mload(add(tablePtr, and(input, 0x3F))), 0xFF))
                out := shl(224, out)

                mstore(resultPtr, out)

                resultPtr := add(resultPtr, 4)
            }

            switch mod(len, 3)
            case 1 {
                mstore(sub(resultPtr, 2), shl(240, 0x3d3d))
            }
            case 2 {
                mstore(sub(resultPtr, 1), shl(248, 0x3d))
            }

            mstore(result, encodedLen)
        }

        return string(result);
    }
}
