    Safe public safe;

    function requireSafeCall() private view {
        require(msg.sender == address(safe), "GS031");
    }

    modifier authorized() {
        // Modifiers are copied around during compilation. This is a function call as it minimized the bytecode size
        requireSafeCall();
        _;
