    "contracts/token-launcher/TokenLauncherERC1155.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";

import { TokenFiERC1155 } from "./templates/TokenFiERC1155.sol";
import { ITokenLauncherERC1155 } from "./interfaces/ITokenLauncherERC1155.sol";
import { ITokenLauncherPaymentModule } from "./interfaces/ITokenLauncherPaymentModule.sol";
import { ITokenLauncherPricingModule } from "./interfaces/ITokenLauncherPricingModule.sol";
import { ITokenLauncherStore } from "./interfaces/ITokenLauncherStore.sol";
import { ITokenLauncherCommon } from "./interfaces/ITokenLauncherCommon.sol";

contract TokenLauncherERC1155 is ITokenLauncherERC1155, AccessControl {
    address public override tokenLauncherStore;
    event TokenLauncherStoreUpdated(address indexed oldStore, address indexed newStore);

    constructor(address _tokenLauncherStore) {
        tokenLauncherStore = _tokenLauncherStore;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function createErc1155(CreateErc1155Input memory input) external payable override {
        // First let's calculate the price
        ITokenLauncherPricingModule.AddOns memory addOns = ITokenLauncherPricingModule.AddOns({
            erc20TransferFee: false,
            erc20BurnFee: false,
            erc20Reflection: false,
            erc20Buyback: false
        });
        ITokenLauncherPricingModule.PriceResult memory price = ITokenLauncherPricingModule(ITokenLauncherStore(tokenLauncherStore).pricingModule()).getPrice(
            msg.sender,
            addOns,
            ITokenLauncherCommon.TokenType.ERC1155
        );
        // Now let's process the payment
        ITokenLauncherPaymentModule.ProcessPaymentInput memory paymentInput = ITokenLauncherPaymentModule.ProcessPaymentInput({
            user: msg.sender,
            referrer: input.referrer,
            usdPrice: price.usdPrice,
            paymentMethod: ITokenLauncherCommon.PaymentMethod(uint256(input.paymentMethod))
        });
        ITokenLauncherPaymentModule.ProcessPaymentOutput memory paymentOutput = ITokenLauncherPaymentModule(
            ITokenLauncherStore(tokenLauncherStore).paymentModule()
        ).processPayment{ value: msg.value }(paymentInput);
        input.tokenStore = tokenLauncherStore;

        // Create the new ERC1155 Token
        TokenFiERC1155 token = new TokenFiERC1155(input);

        // Log new token into store
        ITokenLauncherStore.CreateTokenInput memory storeInput = ITokenLauncherStore.CreateTokenInput({
            tokenAddress: address(token),
            owner: input.owner,
            referrer: input.referrer,
            usdTotalAmount: price.usdPrice,
            treasuryUsdShare: paymentOutput.feeCollected,
            referrerUsdShare: paymentOutput.referrerSharePaid,
            usdBurned: paymentOutput.usdBurned,
            flokiBurned: paymentOutput.flokiBurned,
            tokenType: ITokenLauncherCommon.TokenType.ERC1155
        });
        ITokenLauncherStore(tokenLauncherStore).addToken(storeInput);
    }

    function updateTokenLauncherStore(address newStore) external onlyRole(DEFAULT_ADMIN_ROLE) {
        address oldStore = tokenLauncherStore;
        tokenLauncherStore = newStore;
        emit TokenLauncherStoreUpdated(oldStore, newStore);
    }
}
"
    },
