    "contracts/token-launcher/interfaces/ITokenLauncherERC1155.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import { ITokenLauncherCommon } from "./ITokenLauncherCommon.sol";

interface ITokenLauncherERC1155 is ITokenLauncherCommon {
    struct CreateErc1155TokenInput {
        uint256 tokenId;
        uint256 maxSupply;
        uint256 publicMintPrice;
        uint8 decimals;
        string uri;
    }

    struct CreateErc1155Input {
        string name;
        string symbol;
        string collectionLogo;
        string tokenURI;
        bool isPublicMintEnabled;
        bool isAdminMintEnabled;
        address treasury;
        address owner;
        address referrer;
        address tokenStore;
        PaymentMethod paymentMethod;
        CreateErc1155TokenInput[] initialTokens;
    }

    function tokenLauncherStore() external returns (address);

    function createErc1155(CreateErc1155Input memory input) external payable;
}
"
    },
    "contracts/token-launcher/interfaces/ITokenLauncherPaymentModule.sol": {
      "content": "// SPDX-License-Identifier: MIT
