    "contracts/token-launcher/templates/TokenFiERC1155.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { ERC1155 } from "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import { ERC1155Burnable } from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import { ERC1155Pausable } from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Pausable.sol";
import { ERC1155Supply } from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import { ERC1155URIStorage } from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155URIStorage.sol";

import { ITokenLauncherERC1155 } from "../interfaces/ITokenLauncherERC1155.sol";

contract TokenFiERC1155 is ERC1155, ERC1155URIStorage, ERC1155Pausable, ERC1155Burnable, ERC1155Supply, AccessControl {
    address public treasury;

    mapping(address => uint256) public nonces;
    bool public isPublicMintEnabled;
    bool public isAdminMintEnabled;
    string public name;
    string public symbol;
    string public collectionLogo;

    // tokenID -> maxSupply
    mapping(uint256 => uint256) public maxSupply;
    // tokenID -> decimals
    mapping(uint256 => uint256) public decimals;
    // tokenID -> publicMintPrice
    mapping(uint256 => uint256) public publicMintPrice;

    uint256[] public existingTokenIds;

    event CollectionLogoUpdated(string indexed oldCollectionLogo, string indexed newCollectionLogo);
    event PublicMintPriceUpdated(uint256 indexed tokenId, uint256 newPrice, uint256 oldPrice);
    event MintPaymentProccessed(address indexed sender, uint256 amount);

    constructor(ITokenLauncherERC1155.CreateErc1155Input memory _input) ERC1155(_input.tokenURI) {
        require(_input.treasury != address(0), "TokenFiErc1155: Treasury cannot be empty");
        name = _input.name;
        symbol = _input.symbol;
        collectionLogo = _input.collectionLogo;
        isPublicMintEnabled = _input.isPublicMintEnabled;
        isAdminMintEnabled = _input.isAdminMintEnabled;
        treasury = _input.treasury;

        if (_input.owner != address(0)) {
            _grantRole(DEFAULT_ADMIN_ROLE, _input.owner);
        }

        _grantRole(DEFAULT_ADMIN_ROLE, _input.tokenStore);

        for (uint256 i = 0; i < _input.initialTokens.length; i++) {
            require(_input.initialTokens[i].maxSupply > 0, "TokenFiERC1155: maxSupply must be greater than 0");
            require(maxSupply[_input.initialTokens[i].tokenId] == 0, "TokenFiERC1155: token already exists");

            maxSupply[_input.initialTokens[i].tokenId] = _input.initialTokens[i].maxSupply;
            decimals[_input.initialTokens[i].tokenId] = _input.initialTokens[i].decimals;
            publicMintPrice[_input.initialTokens[i].tokenId] = _input.initialTokens[i].publicMintPrice;
            _setURI(_input.initialTokens[i].tokenId, _input.initialTokens[i].uri);
            existingTokenIds.push(_input.initialTokens[i].tokenId);
        }
    }

    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    function adminMint(
        address _to,
        uint256 _tokenId,
        uint256 _amount
    ) external whenNotPaused tokenExists(_tokenId) supplyAvailable(_tokenId, _amount) onlyRole(DEFAULT_ADMIN_ROLE) {
        require(isAdminMintEnabled, "TokenFiERC1155: admin mint disabled");
        _mint(_to, _tokenId, _amount, "");
    }

    function createToken(ITokenLauncherERC1155.CreateErc1155TokenInput memory input) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(!exists(input.tokenId), "TokenFiERC1155: token already exists");
        require(input.maxSupply > 0, "TokenFiERC1155: maxSupply must be greater than 0");
        require(maxSupply[input.tokenId] == 0, "TokenFiERC1155: token already exists");

        maxSupply[input.tokenId] = input.maxSupply;
        decimals[input.tokenId] = input.decimals;
        publicMintPrice[input.tokenId] = input.publicMintPrice;
        _setURI(input.tokenId, input.uri);
        existingTokenIds.push(input.tokenId);
    }

    function setTokenUri(uint256 _tokenId, string memory _uri) external tokenExists(_tokenId) onlyRole(DEFAULT_ADMIN_ROLE) {
        _setURI(_tokenId, _uri);
    }

    function setTreasury(address _treasury) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_treasury != address(0), "TokenFiErc1155: Treasury cannot be address(0)");
        treasury = _treasury;
    }

    function setPublicMintPrice(uint256 _tokenId, uint256 _newPublicMintPrice) external tokenExists(_tokenId) onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 oldPrice = publicMintPrice[_tokenId];
        publicMintPrice[_tokenId] = _newPublicMintPrice;
        emit PublicMintPriceUpdated(_tokenId, _newPublicMintPrice, oldPrice);
    }

    function mint(address _to, uint256 _tokenId, uint256 _amount) external payable whenNotPaused tokenExists(_tokenId) supplyAvailable(_tokenId, _amount) {
        require(isPublicMintEnabled, "TokenFiERC1155: public mint disabled");
        uint256 totalMintPrice = publicMintPrice[_tokenId] * _amount;

        require(msg.value >= totalMintPrice, "TokenFiERC1155: Insufficient payment amount");

        (bool success, ) = payable(treasury).call{ value: totalMintPrice }("");
        require(success, "Failed to send funds to treasury");

        // refund any extra ETH sent
        uint256 toBeRefunded = msg.value - totalMintPrice;
        if (toBeRefunded > 0) {
            (success, ) = payable(msg.sender).call{ value: toBeRefunded }("");
            require(success, "Failed to refund leftover native tokens");
        }

        emit MintPaymentProccessed(msg.sender, totalMintPrice);

        _mint(_to, _tokenId, _amount, "");
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC1155, AccessControl) returns (bool) {
        return ERC1155.supportsInterface(interfaceId) || AccessControl.supportsInterface(interfaceId);
    }

    function updateCollectionLogo(string memory _collectionLogo) external onlyRole(DEFAULT_ADMIN_ROLE) {
        string memory oldCollectionLogo = collectionLogo;
        collectionLogo = _collectionLogo;
        emit CollectionLogoUpdated(oldCollectionLogo, _collectionLogo);
    }

    function uri(uint256 tokenId) public view override(ERC1155, ERC1155URIStorage) returns (string memory) {
        return ERC1155URIStorage.uri(tokenId);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155, ERC1155Pausable, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function _recoverSigner(bytes32 ethSignedMessageHash, string memory ownerSignature) private pure returns (address) {
        bytes32 r;
        bytes32 s;
        uint8 v;
        (r, s, v) = _splitSignature(ownerSignature);
        return ecrecover(ethSignedMessageHash, v, r, s);
    }

    function _splitSignature(string memory signature) private pure returns (bytes32 r, bytes32 s, uint8 v) {
        require(bytes(signature).length == 65, "TokenFiERC1155: invalid signature length");
        assembly {
            r := mload(add(signature, 32))
            s := mload(add(signature, 64))
            v := byte(0, mload(add(signature, 96)))
        }
    }

    function getExistingTokenIds() external view returns (uint256[] memory) {
        return existingTokenIds;
    }

    modifier supplyAvailable(uint256 _tokenId, uint256 _amount) {
        require(totalSupply(_tokenId) + _amount <= maxSupply[_tokenId], "TokenFiERC1155: maxSupply reached");

        _;
    }

    modifier tokenExists(uint256 _tokenId) {
        require(maxSupply[_tokenId] > 0, "TokenFiERC1155: token doesn't exist");

        _;
    }
}
"
    }
  }
}}
