    "contracts/storage/SapienzStorageV1.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

contract SapienzStorageV1 {
    bytes32 public merkleRoot;

    // contracts allowed to claim sapienz mint spots
    mapping(address => bool) public allowedContracts;

    // contracts that do not require approvals
    mapping(address => bool) public controlledContracts;

    // mapping token address => token ID => claimant => claimed balance
    mapping(address => mapping(uint256 => mapping(address => uint256)))
        public claimedBalances;
}
"
    },
    "contracts/storage/SapienzStorageV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/utils/structs/BitMapsUpgradeable.sol";
import "@erc6551/reference/src/interfaces/IERC6551Registry.sol";

contract SapienzStorageV2 {
    string BASE_URI;

    IERC6551Registry erc6551Registry;
    address erc6551AccountImplementation;

    bool public claimEnabled;
    bool public mintEnabled;

    mapping(address => BitMapsUpgradeable.BitMap) _erc721Minted;
}
"
