// File contracts/Aliens88CERC1155.sol



pragma solidity ^0.8.13;
contract Aliens88CERC1155 is Ownable, ERC1155 {
    string public name;
    string public symbol;

    mapping (address => bool) _minters;
    mapping (uint256 => string) uris;
    

    modifier onlyMinter() {
        require(_minters[msg.sender], "Sender is not authorized Minter.");
        _;
    }

    constructor (
        string memory name_,
        string memory symbol_
    ) ERC1155('') {
        name = name_;
        symbol = symbol_;
    }

    function mint(address to, uint256 tokenId_, uint256 amount) onlyMinter public {
        _mint(to, tokenId_, amount, '');
    }

    function mintBatch(address to, uint256[] memory tokenIds_, uint256[] memory amounts) onlyMinter public {
        _mintBatch(to, tokenIds_, amounts, '');
    }

    function setURI(uint256 tokenId_, string memory uri_) public onlyOwner {
        uris[tokenId_] = uri_; 
    }

    function setMinter(address contract_) public onlyOwner {
        _minters[contract_] = true;
    }

    function releaseMinter(address contract_) public onlyOwner {
        _minters[contract_] = false;
    }

    function uri(uint256 tokenId_) public view override (ERC1155) returns (string memory) {
        return uris[tokenId_];
    }

}


// File contracts/Aliens88CMinter.sol



pragma solidity ^0.8.13;
contract Aliens88CMinter is Ownable {
    // claimId => snapshotOrBurn(1||2) => id
    mapping(uint256 => mapping(uint256 => mapping(uint256 => bool))) usedToClaim;

    error SnapshotIdUsed();
    error BurnIdUsed();
    error InvalidBurnId();
    error InvalidSnapshotProof();
    error NotBurnOwner();
    error BurnIsNotBurnProof();
    error RequestingTooMany();
    error ClaimClosed();

    address public ALIEN_88C_CONTRACT;
    address public CONTRACT_8LIENS;
    address public GNOSIS_SAFE = 0xe7826EBb0d2035134e8Aed6ddb310fF6Fa3745DC;
    address _signer;

    uint256 public constant MAX_SUPPLY = 888;
    uint256 public TOTAL_SUPPLY = 0;

    bool public IS_LIVE = false;

    struct Claim {
        uint256 snapshotId;
        uint256 burnId;
        bytes proofOfSnapshot;
        bytes proofToSaveBurnId;
    }

    function setSigner(address signer) external onlyOwner {
        _signer = signer;
    }

    function setAliensContract(address _aliens) external onlyOwner {
        CONTRACT_8LIENS = _aliens;
    }

    function set88CContract(address _88c) external onlyOwner {
        ALIEN_88C_CONTRACT = _88c;
    }

    function setGnosisSafe(address _safe) external onlyOwner {
        GNOSIS_SAFE = _safe;
    }

    function setContractLive(bool _state) external onlyOwner {
        IS_LIVE = _state;
    }

    function claimRemaining(uint256 tokenId) external onlyOwner {
        TOTAL_SUPPLY = MAX_SUPPLY;
        Aliens88CERC1155(ALIEN_88C_CONTRACT).mint(
            GNOSIS_SAFE,
            tokenId,
            MAX_SUPPLY - TOTAL_SUPPLY
        );
    }

    function claim88C(uint256 claimId, Claim[] calldata claims) external {
        if (!IS_LIVE) { revert ClaimClosed(); }

        uint256 length = claims.length;
        if (TOTAL_SUPPLY + length > MAX_SUPPLY) { revert RequestingTooMany(); }

        for (uint256 i; i < length; i++) {
            // this will revert if any claim is not good
            _claim(claimId, claims[i]);
        }

        TOTAL_SUPPLY += length;

        Aliens88CERC1155(ALIEN_88C_CONTRACT).mint(
            msg.sender,
            claimId,
            length
        );

    }

    function _claim(uint256 claimId, Claim calldata claim) internal {
        if (usedToClaim[claimId][1][claim.snapshotId]) {
            revert SnapshotIdUsed();
        }

        if (usedToClaim[claimId][2][claim.burnId]) {
            revert BurnIdUsed();
        }

        // make sure the user doesn't try to burn the id they use for claim
        if (claim.snapshotId == claim.burnId) {
            revert InvalidBurnId();
        }

        // make sure we have a signature that proved msg.sender was holding claim.snapshotId at time of snaphsot
        // 1 in the signature means "token to hold to be able to claim claimId"
        bytes32 message = keccak256(
            abi.encode(msg.sender, claim.snapshotId, uint256(1), claimId)
        );
        if (
            ECDSA.recover(
                ECDSA.toEthSignedMessageHash(message),
                claim.proofOfSnapshot
            ) != _signer
        ) {
            revert InvalidSnapshotProof();
        }

        // make sure user is the owner of claim.burnId
        if (msg.sender != ERC721(CONTRACT_8LIENS).ownerOf(claim.burnId)) {
            revert NotBurnOwner();
        }

        // if there is any signatures passed for the second ID this means the user is trying to claim without burning
        if (claim.proofToSaveBurnId.length != 0) {
            // 2 in the signature means "burn proof for this claimId"
            bytes32 burnMessage = keccak256(
                abi.encode(claim.burnId, uint256(2), claimId)
            );
            if (
                ECDSA.recover(
                    ECDSA.toEthSignedMessageHash(burnMessage),
                    claim.proofToSaveBurnId
                ) != _signer
            ) {
                revert BurnIsNotBurnProof();
            }
        } else {
            ERC721Burnable(CONTRACT_8LIENS).burn(claim.burnId);
        }
        usedToClaim[claimId][1][claim.snapshotId] = true;
        usedToClaim[claimId][2][claim.burnId] = true;
    }
}
