pragma solidity ^0.8.0;





contract Quarcy is ERC20, Ownable {
    using SafeMath for uint256;

    mapping(address => uint256) private _lastTransactionTimestamp;
    mapping(address => uint256) private _nonces;

    uint256 public buyFee = 3; 
    uint256 public sellFee = 3;
    uint256 public earlySellFee = 33;

    uint256 public constant MAX_FEE = 50;  // Safety constant to prevent setting too high of a fee

    constructor() ERC20("Quarcy", "QRCY") Ownable(msg.sender) {
        _mint(msg.sender, 626070150000 * 10**18);
    }

    function setFees(uint256 _buyFee, uint256 _sellFee, uint256 _earlySellFee) external onlyOwner {
        require(_buyFee <= MAX_FEE && _sellFee <= MAX_FEE && _earlySellFee <= MAX_FEE, "Fees too high");
        buyFee = _buyFee;
        sellFee = _sellFee;
        earlySellFee = _earlySellFee;
    }

    function handleCooldown(address sender) internal {
        require(_lastTransactionTimestamp[sender].add(33 minutes) <= block.timestamp, "Cooldown not over");
        
        uint256 currentNonce = _nonces[sender];
        uint256 computedCooldown = (currentNonce % 10).add(1).mul(3 minutes);  // Some pseudo randomness based on nonce
            
        require(_lastTransactionTimestamp[sender].add(computedCooldown) <= block.timestamp, "Cooldown not over");

        _lastTransactionTimestamp[sender] = block.timestamp;
        _nonces[sender] = _nonces[sender].add(1);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        handleCooldown(msg.sender);
        return super.transfer(recipient, applyFees(msg.sender, recipient, amount));
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        handleCooldown(sender);
        return super.transferFrom(sender, recipient, applyFees(sender, recipient, amount));
    }

    function applyFees(address sender, address recipient, uint256 amount) internal returns (uint256) {
        if (recipient == address(this) || recipient == owner()) {
            return amount;
        } else {
            uint256 feeAmount;
            if (_lastTransactionTimestamp[sender].add(33 minutes) > block.timestamp) {
                feeAmount = amount.mul(earlySellFee).div(100);
            } else if (sender == address(this) || sender == owner()) {
                feeAmount = 0;  // Exempt from fee
            } else {
                feeAmount = recipient == owner() ? amount.mul(sellFee).div(100) : amount.mul(buyFee).div(100);
            }

            uint256 transferAmount = amount.sub(feeAmount);
            require(amount == transferAmount + feeAmount, "Fee error!");

            super._transfer(sender, address(this), feeAmount);
            return transferAmount;
        }
    }

    function retrieveTokens(address to, uint256 amount) external onlyOwner {
        _transfer(address(this), to, amount);
    }
}
