// File: ArtFreethWhitelist.sol


pragma solidity ^0.8.2;




contract ArtFreethWhitelist is Pausable, Ownable {
    using EnumerableSet for EnumerableSet.AddressSet;
    mapping(address => bool) public _isAllowedAddress;
    uint public _maxWhitelistedUsers;
    bool public _IsEarlyAccessEnabled;
    EnumerableSet.AddressSet whitelistedUsers;

    constructor(uint maxWhitelistedUsers) Ownable(msg.sender) {
        _isAllowedAddress[msg.sender] = true;
        _maxWhitelistedUsers = maxWhitelistedUsers;
        _IsEarlyAccessEnabled = true;
        whitelistedUsers.add(msg.sender);
    }

    function transferOwnership(
        address newOwner
    ) public override whenNotPaused onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        _isAllowedAddress[owner()] = false;
        _isAllowedAddress[newOwner] = true;
        whitelistedUsers.add(newOwner);
        whitelistedUsers.remove(owner());
        _transferOwnership(newOwner);
    }

    function addToWhitelist(
        address[] memory users
    ) public whenNotPaused onlyOwner {
        uint usersLength = users.length;

        require(usersLength > 0, "Whitelist: Empty user address");
        maxUserLimitReached(usersLength);

        for (uint i; i < usersLength; i++) {
            isValidUserAddress(users[i]);
            checkAddressNotAllowed(users[i]);

            _isAllowedAddress[users[i]] = true;
            whitelistedUsers.add(users[i]);
        }
    }

    function joinWhitelist() public {
        normalUsersCanJoin();
        maxUserLimitReached(1);
        isValidUserAddress(msg.sender);
        checkAddressNotAllowed(msg.sender);

        _isAllowedAddress[msg.sender] = true;
        whitelistedUsers.add(msg.sender);
    }

    function updateMaxWhitelistedUsers(
        uint updatedLimit
    ) public onlyOwner whenNotPaused {
        require(
            updatedLimit >= whitelistedUsers.length(),
            "Whitelist: Invalid limit"
        );

        _maxWhitelistedUsers = updatedLimit;
    }

    function updateEarlyAccessStatus(
        bool status
    ) public onlyOwner whenNotPaused {
        _IsEarlyAccessEnabled = status;
    }

    function removeFromWhitelist(address user) public whenNotPaused onlyOwner {
        isValidUserAddress(user);
        checkAddressIsAllowed(user);

        _isAllowedAddress[user] = false;
        whitelistedUsers.remove(user);
    }

    function maxUserLimitReached(uint usersToJoin) public view {
        require(
            whitelistedUsers.length() + usersToJoin <= _maxWhitelistedUsers,
            "Whitelist: Max users limit exceeds"
        );
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function isValidUserAddress(address user) private pure {
        require(user != address(0), "Whitelist: Got null address");
    }

    function checkAddressNotAllowed(address user) public view {
        require(
            !_isAllowedAddress[user],
            "Whitelist: Address already in whitelist"
        );
    }

    function isAddressWhitelisted(address user) public view returns (bool) {
        return _isAllowedAddress[user];
    }

    function getWhitelistedUsers() public view returns (address[] memory) {
        return whitelistedUsers.values();
    }

    function getWhitelistedUsersLength() public view returns (uint) {
        return whitelistedUsers.length();
    }

    function checkAddressIsAllowed(address user) public view {
        require(
            _isAllowedAddress[user],
            "Whitelist: Address is not in whitelist"
        );
    }

    function normalUsersCanJoin() public view {
        require(_IsEarlyAccessEnabled, "Whitelist: Normal users can't join");
    }
}
