    "contracts/access/DefaultAccessControl.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/AccessControl.sol";

abstract contract DefaultAccessControl is AccessControl {
	event ManagerAuthorized(address manager, address admin);
	event ManagerRevoked(address manager, address admin);

	bytes32 private _managerRole;

	// solhint-disable-next-line func-name-mixedcase

	address internal _adminAddress;
	address internal _managerAddress;

	constructor(
			address adminAddress,
			address managerAddress
	) {
		_setupRole(DEFAULT_ADMIN_ROLE, adminAddress);
		_managerRole = keccak256("FACTORY_MANAGER_ROLE");
		_grantRole(_managerRole, managerAddress);
		_adminAddress = adminAddress;
		_managerAddress = managerAddress;
	}

	function transferDefaultAdmin(
		address _defaultAdmin
	) external returns (bool) {
		_onlyAdmin();
		require(msg.sender != _defaultAdmin, "The new admin can not be the same");
		_grantRole(DEFAULT_ADMIN_ROLE, _defaultAdmin);
		renounceRole(DEFAULT_ADMIN_ROLE, _msgSender());
		_adminAddress = _defaultAdmin;
		return true;
	}

	modifier disabled() {
		require(false, "This function has been disabled");
		_;
	}

	function grantRole(
		bytes32 role,
		address account
	) public virtual override disabled {
		super.grantRole(role, account);
	}

	function revokeRole(
		bytes32 role,
		address account
	) public virtual override disabled {
		super.revokeRole(role, account);
	}

	function _onlyAdmin() internal view {
		require(
			hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
			"DefaultAccessControl: Not admin"
		);
	}

	function authorizeManager(address manager) external returns (bool) {
		_onlyAdmin();
		_revokeRole(_managerRole, _managerAddress);
		_grantRole(_managerRole, manager);
		_managerAddress = manager;
		emit ManagerRevoked(manager, msg.sender);
		emit ManagerAuthorized(manager, msg.sender);
		return true;
	}

	function revokeManager() external returns (bool) {
		_onlyAdmin();
		_revokeRole(_managerRole, _managerAddress);
		emit ManagerRevoked(_managerAddress, msg.sender);
		_managerAddress = address(0);
		return true;
	}

	function _onlyManager() internal view {
		require(
			hasRole(_managerRole, _msgSender()) ||
			hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
			"DefaultAccessControl: must have manager or default admin role"
		);
	}

	function getAdminAddress() external view returns (address) {
		return _adminAddress;
	}

	function getManagerAddress() external view returns (address) {
		return _managerAddress;
	}
}
"
    },
