    "contracts/splitter/factory/PaymentSplitterFactory.sol": {
      "content": "pragma solidity ^0.8.17;
// SPDX-License-Identifier: MIT

import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts/proxy/beacon/IBeacon.sol";
import "../../access/DefaultAccessControlUpgradeable.sol";
import "../PaymentSplitterLogic.sol";

contract PaymentSplitterFactory is DefaultAccessControlUpgradeable {
    struct PaymentSplitterCreateCommand {
        address customerAddress;
        bytes data;
    }

    event PaymentSplitterCreated(
        address paymentSplitterAddress,
        address vcAddress,
        uint8 vcFees,
        address customerAddress,
        uint8 customerShares
    );

    event ProxyCreated(address proxy);
    event LogicAddressUpdated(address newLogicAddress);
    //PSF-03
    event VCFeesSet(uint8 vcFees);
    event VCAddressSet(address vcAddress);
    event VCStakingContractAddressSet(address stakingAddress);

    address private _beaconAddress;
    address private _logicAddress;
    address private _vcAddress;
    address private _vcStakingContractAddress;
    uint8 private _vcFees;
    uint8 private _customerShares;

    mapping(address => address) public customerRegistry;

	/// @custom:oz-upgrades-unsafe-allow constructor
	constructor() {
		_disableInitializers();
	}

    /**
        * @notice Initialize the factory with a beacon and logic contract address
        * @param _beacon Address of the beacon
        * @param _beacon Address of the _logic
        */
    function initialize(
        address _beacon,
        address _logic,
        address _adminAddress,
        address _managerAddress,
        address vcAddress,
        uint8 vcFees
    ) external initializer {
        //CON-05
        require(
            _beacon != address(0),
            "PaymentSplitterFactory: _beacon is the zero address"
        );
        require(
            _logic != address(0),
            "PaymentSplitterFactory: _logic is the zero address"
        );
        require(
            _adminAddress != address(0),
            "PaymentSplitterFactory: _adminAddress is the zero address"
        );
        require(
            _managerAddress != address(0),
            "PaymentSplitterFactory: _managerAddress is the zero address"
        );
        require(
            vcAddress != address(0),
            "PaymentSplitterFactory: vcAddress is the zero address"
        );
        require(vcFees < 100, "PaymentSplitterFactory: vcFees must be < 100");

        defaultAccessControl_init(_adminAddress, _managerAddress);
        _beaconAddress = _beacon;
        _logicAddress = _logic;
        _vcAddress = vcAddress;
        _vcFees = vcFees;
        _customerShares = 100 - _vcFees;
    }

    function beaconAddress() external view returns (address) {
        return _beaconAddress;
    }

    function logicAddress() external view returns (address) {
        return _logicAddress;
    }

    function stakingAddress() external view returns (address) {
        return _vcStakingContractAddress;
    }

    function vcAddress() external view returns (address) {
        return _vcAddress;
    }

    function vcFees() external view returns (uint8) {
        return _vcFees;
    }

    function customerShares() external view returns (uint8) {
        return _customerShares;
    }

    function setVCFees(uint8 fees) external {
        _onlyManager();
        require(fees < 100, "Fees must be < 100");
        _vcFees = fees;
        _customerShares = 100 - fees;
        emit VCFeesSet(_vcFees);
    }

    function setVCAddress(address vcAddress_) external {
        _onlyAdmin();
        require(
            vcAddress_ != address(0),
            "PaymentSplitterFactory: vcAddress_ is the zero address"
        );
        _vcAddress = vcAddress_;
        emit VCAddressSet(_vcAddress);
    }

    function setVCStakingContractAddress(
        address vcStakingContractAddress
    ) external {
        _onlyAdmin();
        require(
            vcStakingContractAddress != address(0),
            "PaymentSplitterFactory: vcStakingContractAddress is the zero address"
        );
        _vcStakingContractAddress = vcStakingContractAddress;
        emit VCStakingContractAddressSet(_vcStakingContractAddress);
    }

    function updateLogicAddress(address logicAddress_) external {
        _onlyAdmin();
        require(
            logicAddress_ != address(0),
            "PaymentSplitterFactory: logicAddress_ is the zero address"
        );
        _logicAddress = logicAddress_;
        emit LogicAddressUpdated(_logicAddress);
    }

    function createPaymentSplitter(
        PaymentSplitterCreateCommand calldata _command
    ) external returns (address) {
        require(
            msg.sender == _vcStakingContractAddress || msg.sender == _managerAddress,
            "PaymentSplitterFactory: Only VC Staking or manager can create a splitter"
        );
        BeaconProxy splitterProxy = _deployBeaconProxy(_command.data);

        PaymentSplitterLogic paymentSplitter = PaymentSplitterLogic(
            payable(address(splitterProxy))
        );
        paymentSplitter.initialize(
            _vcAddress,
            _vcFees,
            _command.customerAddress,
            _customerShares,
            address(this)
        );

        //Emit event
        emit PaymentSplitterCreated(
            address(splitterProxy),
            _vcAddress,
            _vcFees,
            _command.customerAddress,
            _customerShares
        );

        customerRegistry[_command.customerAddress] = address(splitterProxy);

        return address(splitterProxy);
    }

    function _deployBeaconProxy(
        bytes memory _data
    ) private returns (BeaconProxy proxy) {
        proxy = new BeaconProxy(_beaconAddress, _data);
        emit ProxyCreated(address(proxy));
        return proxy;
    }
}"
