    "contracts/splitter/PaymentSplitterLogic.sol": {
      "content": "// SPDX-License-Identifier: MIT
// OpenZeppelin Contracts (last updated v4.8.0) (finance/PaymentSplitter.sol)

pragma solidity ^0.8.17;

import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./factory/PaymentSplitterFactory.sol";


/**
 * @title PaymentSplitter
 * @dev This contract allows to split Ether payments among a group of accounts. The sender does not need to be aware
 * that the Ether will be split in this way, since it is handled transparently by the contract.
 *
 * The split can be in equal parts or in any other arbitrary proportion. The way this is specified is by assigning each
 * account to a number of shares. Of all the Ether that this contract receives, each account will then be able to claim
 * an amount proportional to the percentage of total shares they were assigned.
 *
 * `PaymentSplitter` follows a _pull payment_ and _push_payment_ model. This means that payments are not automatically
 * forwarded to the accounts but kept in this contract in case of a internal transaction because receive function
 * in that case is not called, in this case transfer is triggered as a separate step by calling the {release} function.
 * In case of an external transaction the transer is automatically trigger in the receive function.
 *
 * NOTE: This contract assumes that ERC20 tokens will behave similarly to native tokens (Ether). Rebasing tokens, and
 * tokens that apply fees during transfers, are likely to not be supported as expected. If in doubt, we encourage you
 * to run tests before sending real value to this contract.
 */
contract PaymentSplitterLogic is
	Initializable,
	ContextUpgradeable
{
	event PayeeAdded(address account, uint8 shares);
	event PayeeRemoved(address account);
	event PaymentReleased(address to, uint256 amount);
	event PaymentReceived(address from, uint256 amount);

	uint8 private _totalShares;
	uint256 private _totalReleased;

	mapping(address => uint8) private _shares;
	mapping(address => uint256) private _released;
	address private _vcAddress;
	address private _customerAddress;
	address private _factoryAddress;

	/// @custom:oz-upgrades-unsafe-allow constructor
	constructor() {
		_disableInitializers();
	}

	function initialize(
		address vcAddress_,
		uint8 vcShares,
		address customerAddress_,
		uint8 customerShares,
		address factoryAddress
	) external initializer {
		//CON-05
		require(
			vcAddress_ != address(0),
			"PaymentSplitter: vcAddress_ is the zero address"
		);
		require(
			customerAddress_ != address(0),
			"PaymentSplitter: customerAddress_ is the zero address"
		);
		require(
			factoryAddress != address(0),
			"PaymentSplitter: factoryAddress is the zero address"
		);

		_factoryAddress = factoryAddress;

		__PaymentSplitter_init(
			vcAddress_,
			vcShares,
			customerAddress_,
			customerShares
		);
	}

	/**
		* @dev Creates an instance of `PaymentSplitter` where each account in `payees` is assigned the number of shares at
		* the matching position in the `shares` array.
		*
		* All addresses in `payees` must be non-zero. Both arrays must have the same non-zero length, and there must be no
		* duplicates in `payees`.
		*/
	function __PaymentSplitter_init(
		address vcAddress_,
		uint8 vcShares,
		address customerAddress_,
		uint8 customerShares
	) internal onlyInitializing {
		__PaymentSplitter_init_unchained(
			vcAddress_,
			vcShares,
			customerAddress_,
			customerShares
		);
	}

	function __PaymentSplitter_init_unchained(
		address vcAddress_,
		uint8 vcShares,
		address customerAddress_,
		uint8 customerShares
	) internal onlyInitializing {
		_vcAddress = vcAddress_;
		_customerAddress = customerAddress_;
		_addPayee(vcAddress_, vcShares);
		_addPayee(_customerAddress, customerShares);
	}

	/**
		* @dev The Ether received will be logged with {PaymentReceived} events. Note that these events are not fully
		* reliable: it's possible for a contract to receive Ether without triggering this function. This only affects the
		* reliability of the events, and not the actual splitting of Ether.
		*
		* To learn more about this see the Solidity documentation for
		* https://solidity.readthedocs.io/en/latest/contracts.html#fallback-function[fallback
		* functions].
		*/
	receive() external payable virtual {
		emit PaymentReceived(_msgSender(), msg.value);
		release();
	}

	/**
		* @dev Getter for the total shares held by payees.
		*/
	function totalShares() external view returns (uint8) {
		return _totalShares;
	}

	/**
		* @dev Getter for the total amount of Ether already released.
		*/
	function totalReleased() public view returns (uint256) {
		return _totalReleased;
	}

	/**
		* @dev Getter for the amount of shares held by an account.
		*/
	function shares(address account) external view returns (uint8) {
		return _shares[account];
	}

	/**
		* @dev Getter for the amount of Ether already released to a payee.
		*/
	function released(address account) public view returns (uint256) {
		return _released[account];
	}

	/**
		* @dev Getter for the VC address.
		*/
	function vcAddress() external view returns (address) {
		return _vcAddress;
	}

	/**
		* @dev Getter for the customer address.
		*/
	function customerAddress() external view returns (address) {
		return _customerAddress;
	}

	/**
		* @dev Getter for the amount of payee's releasable Ether.
		*/
	function releasable(address account) public view returns (uint256) {
		return _pendingPayment(account);
	}

	/**
		* @dev Triggers a transfer to `msg.sender` of the amount of Ether they are owed, according to their percentage
		* of the total shares and their previous withdrawals.
		*/
	function release() public virtual {
		require(
			_shares[_customerAddress] > 0,
			"PaymentSplitter: customer account has no shares"
		);

		uint256 customerPayment = releasable(_customerAddress);
		uint256 vcPayment = releasable(_vcAddress);

		_totalReleased += customerPayment + vcPayment;
		unchecked {
			_released[_customerAddress] += customerPayment;
			_released[_vcAddress] += vcPayment;
		}
		if (customerPayment != 0) {
			AddressUpgradeable.sendValue(
				payable(_customerAddress),
				customerPayment
			);
			emit PaymentReleased(payable(_customerAddress), customerPayment);
		}

		//known strict equal
		if (vcPayment == 0) {
			return;
		}
		AddressUpgradeable.sendValue(payable(_vcAddress), vcPayment);
		emit PaymentReleased(payable(_vcAddress), vcPayment);
	}

	/**
		* @dev internal logic for computing the pending payment of an `account` given the token historical balances and
		* already released amounts.
		*/
	function _pendingPayment(
		address account
	) private view returns (uint256) {
		return (address(this).balance * _shares[account]) / _totalShares;
	}

	/**
		* @dev Add a new payee to the contract.
		* @param account The address of the payee to add.
		* @param shares_ The number of shares owned by the payee.
		*/
	function _addPayee(address account, uint8 shares_) private {
		require(
			account != address(0),
			"PaymentSplitter: account is the zero address"
		);

		require(
			_shares[account] == 0,
			"PaymentSplitter: account already has shares"
		);

		_shares[account] = shares_;
		_totalShares = _totalShares + shares_;

		emit PayeeAdded(account, shares_);
	}

	/**
		* @dev Remove an existing payee from the contract.
		* @param account The address of the payee to remove.
		*/
	function _removePayee(address account) private {
		require(
			account != address(0),
			"PaymentSplitter: account is the zero address"
		);
		require(_shares[account] > 0, "PaymentSplitter: account has no shares");

		_totalShares = _totalShares - _shares[account];
		_shares[account] = 0;

		emit PayeeRemoved(account);
	}

	/**
		* @dev Update the VC address.
		* @param _newVCAddress The new address of VC.
		*/
	function updateVCAddress(address _newVCAddress) external {
		_onlyAdmin();

		require(
			_newVCAddress != address(0),
			"PaymentSplitter: _newVCAddress is the zero address"
		);

		require(
			_shares[_newVCAddress] == 0,
			"PaymentSplitter: account already has shares"
		);
		release();
		uint8 _payeeShares = _shares[_vcAddress];
		_removePayee(_vcAddress);
		_addPayee(_newVCAddress, _payeeShares);

		_released[_newVCAddress] = _released[_vcAddress];
		_released[_vcAddress] = 0;

		_vcAddress = _newVCAddress;
	}

	/**
		* @dev Set the fees for VC and customer. Must be called by admin. Must be less than 100.
		* @param fees The fees for VC.
		*/
	function setVCFees(uint8 fees) external {
		_onlyManagerOrAdmin();
		require(fees < 100, "Fees must be less than 100");
		release();
		_shares[_vcAddress] = fees;
		_shares[_customerAddress] = 100 - fees;
		_totalShares = 100;
	}

	function _onlyManagerOrAdmin() internal view {
		PaymentSplitterFactory paymentSplitterFactory = PaymentSplitterFactory(
            _factoryAddress
        );

		require(
			 _msgSender() == paymentSplitterFactory.getManagerAddress() ||
			 _msgSender() == paymentSplitterFactory.getAdminAddress(),
			"DefaultAccessControl: must have manager or default admin role"
		);
	}

	function _onlyAdmin() internal view {
		PaymentSplitterFactory paymentSplitterFactory = PaymentSplitterFactory(
            _factoryAddress
        );

		require(
			_msgSender() == paymentSplitterFactory.getAdminAddress(),
			"DefaultAccessControl: Not admin"
		);
	}
}
"
