    "contracts/staking/VCStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interfaces/IDepositContract.sol";
import "../splitter/factory/PaymentSplitterFactory.sol";
import "../access/DefaultAccessControl.sol";

contract VCStaking is ReentrancyGuard, Pausable, DefaultAccessControl {
	/**
		* @dev Eth2 Deposit Contract address.
		*/
	IDepositContract public depositContract;

	/**
		* @dev VC Splitter Factory Contract address.
		*/
	PaymentSplitterFactory public splitterFactoryContract;

	/**
		* @dev Event for when the minimal amount of nodes per transaction is set.
		*/
	event NodesMinAmountSet(uint256 amount);

	/**
		* @dev Event for when the maximal amount of nodes per transaction is set.
		*/
	event NodesMaxAmountSet(uint256 amount);


	/**
		* @dev Minimal and maximum amount of nodes per transaction.
		*/
	uint256 private _nodesMinAmount;
	uint256 private _nodesMaxAmount;

	uint256 public constant PUBKEY_LENGTH = 48;
	uint256 public constant CREDENTIALS_LENGTH = 32;
	uint256 public constant SIGNATURE_LENGTH = 96;

	event DepositEvent(address from, uint256 nodesAmount, bool isNewCustomer);
	event ExitRequestEvent(address caller, bytes pubkey);

	/**
		* @dev Collateral size of one node.
		*/
	uint256 public constant COLLATERAL = 32 ether;

	/**
		* @dev Setting Eth2 Smart Contract address during construction.
		*/
	constructor (
		address adminAddress_,
		address managerAddress_,
		address depositContract_,
		address splitterFactoryContract_
	) DefaultAccessControl(adminAddress_, managerAddress_) {
		//CON-05
		require(
			adminAddress_ != address(0),
			"VCStaking: _beaadminAddress_con is the zero address"
		);
		require(
			managerAddress_ != address(0),
			"VCStaking: managerAddress_ is the zero address"
		);
		require(
			depositContract_ != address(0),
			"VCStaking: depositContract_ is the zero address"
		);
		require(
			splitterFactoryContract_ != address(0),
			"VCStaking: splitterFactoryContract_ is the zero address"
		);
		depositContract = IDepositContract(depositContract_);
		splitterFactoryContract = PaymentSplitterFactory(
			splitterFactoryContract_
		);
		_nodesMinAmount = 1;
		_nodesMaxAmount = 100;
	}

	/**
		* @dev This contract will not accept direct ETH transactions.
		*/
	receive() external payable {
			revert("VCStaking: do not send ETH directly here");
	}

	/**
		* @dev Function that allows to deposit up to 100 nodes at once.
		*
		* - pubkeys                - Array of BLS12-381 public keys.
		* - withdrawal_credentials - Array of commitments to a public keys for withdrawals.
		* - signatures             - Array of BLS12-381 signatures.
		* - deposit_data_roots     - Array of the SHA-256 hashes of the SSZ-encoded DepositData objects.
		*/
	function deposit(
		bytes[] calldata pubkeys_,
		bytes[] calldata withdrawalCredentials_,
		bytes[] calldata signatures_,
		bytes32[] calldata depositDataRoots_
	) external payable whenNotPaused {
		uint256 nodesAmount = pubkeys_.length;

		require(
			nodesAmount >= _nodesMinAmount && nodesAmount <= _nodesMaxAmount,
			"VCStaking: number of nodes must be within the node limits in contract"
		);
		require(
			msg.value == COLLATERAL * nodesAmount,
			"VCStaking: the amount of ETH does not match the amount of nodes"
		);

		require(
			withdrawalCredentials_.length == nodesAmount &&
				signatures_.length == nodesAmount &&
				depositDataRoots_.length == nodesAmount,
			"VCStaking: amount of parameters do no match"
		);

		for (uint256 i = 0; i < nodesAmount; ++i) {
			require(
				pubkeys_[i].length == PUBKEY_LENGTH,
				"VCStaking: wrong pubkey"
			);
			require(
				withdrawalCredentials_[i].length == CREDENTIALS_LENGTH,
				"VCStaking: wrong withdrawal credentials"
			);
			require(
				signatures_[i].length == SIGNATURE_LENGTH,
				"VCStaking: wrong signatures"
			);

			IDepositContract(address(depositContract)).deposit{
					value: COLLATERAL
			}(
				pubkeys_[i],
				withdrawalCredentials_[i],
				signatures_[i],
				depositDataRoots_[i]
			);
		}

		address splitterAddress = splitterFactoryContract.customerRegistry(
            msg.sender
        );

        if (splitterAddress == address(0)) {
            PaymentSplitterFactory.PaymentSplitterCreateCommand
                memory command = PaymentSplitterFactory
                    .PaymentSplitterCreateCommand({
                        customerAddress: msg.sender,
                        data: ""
                    });

            splitterFactoryContract.createPaymentSplitter(command);
            emit DepositEvent(msg.sender, nodesAmount, true);
        } else {
            emit DepositEvent(msg.sender, nodesAmount, false);
        }

	}

	/// @notice Request exit for one or many validators
	/// @param pubkeys_ list of validator pubkeys to request exit for
	function requestExit(bytes[] calldata pubkeys_) external {
		for (uint256 i = 0; i < pubkeys_.length; ) {
			emit ExitRequestEvent(msg.sender, pubkeys_[i]);
			unchecked {
					++i;
			}
		}
	}

	/**
		* @dev Triggers stopped state.
		*
		* Requirements:
		*
		* - The contract must not be paused.
		*/
	function pause() external {
		_onlyAdmin();
		_pause();
	}

	/**
		* @dev Returns to normal state.
		*
		* Requirements:
		*
		* - The contract must be paused.
		*/
	function unpause() external {
		_onlyAdmin();
		_unpause();
	}

	/**
		* @dev Sets the minimal amount of nodes per transaction.
		*
		* Requirements:
		* - only admin
		*/
	function setNodesMinAmount(uint256 nodesMinAmount_) external {
		_onlyAdmin();
		//STA-04
		require(
			nodesMinAmount_ <= _nodesMaxAmount,
			"VCStaking: nodesMinAmount_ should be smaller than _nodesMaxAmount"
		);
		_nodesMinAmount = nodesMinAmount_;
		emit NodesMinAmountSet(nodesMinAmount_);
	}

	/**
		* @dev Sets the maximal amount of nodes per transaction.
		*
		* Requirements:
		* - only admin
		*/
	function setNodesMaxAmount(uint256 nodesMaxAmount_) external {
		_onlyAdmin();
		//STA-04
		require(
			nodesMaxAmount_ >= _nodesMinAmount,
			"VCStaking: nodesMaxAmount_ should be bigger than _nodesMinAmount"
		);
		_nodesMaxAmount = nodesMaxAmount_;
		emit NodesMaxAmountSet(nodesMaxAmount_);
	}

	/**
		* @dev Returns the minimal amount of nodes per transaction.
		*/
	function nodesMinAmount() external view returns (uint256) {
		return _nodesMinAmount;
	}

	/**
		* @dev Returns the maximal amount of nodes per transaction.
		*/
	function nodesMaxAmount() external view returns (uint256) {
		return _nodesMaxAmount;
	}

}
"
    }
